 background-image:linear-gradient(rgba(0,0,0,0),rgba(0,0,0,0.15))!important;
 background-image:-moz-linear-gradient(rgba(0,0,0,0),rgba(0,0,0,0.15))!important;
 background-image:-webkit-linear-gradient(rgba(0,0,0,0),rgba(0,0,0,0.15))!important;}

.wiki_object .btn.view-more:hover{background-color:#eaebeb!important;}

.wiki_object .btn#wikiEditCancel,body.wiki_object.wiki-volume .btn-add-cover{
