 background-image:-moz-linear-gradient(rgba(0,0,0,0),rgba(0,0,0,0.15))!important;
 background-image:-webkit-linear-gradient(rgba(0,0,0,0),rgba(0,0,0,0.15))!important;}

.wiki_object .btn#wikiEditCancel:hover,body.wiki_object.wiki-volume .btn-add-cover:hover{background-color:#eaebeb!important;}

.wiki_object #wiki-relatedList .btn.view-more{color:#515458!important;}

.btn[disabled="disabled"]{
