 background-color:#f7f7f8!important;
 border:1px solid #a8abad!important;
 background-image:linear-gradient(rgba(0,0,0,0),rgba(0,0,0,0.15))!important;
 background-image:-moz-linear-gradient(rgba(0,0,0,0),rgba(0,0,0,0.15))!important;
 background-image:-webkit-linear-gradient(rgba(0,0,0,0),rgba(0,0,0,0.15))!important;}

#gallery-edit .btn.btn-single-image[href*="set-default-image"]:hover,#gallery-edit .btn.btn-single-image[href*="set-background-image"]:hover{background-color:#eaebeb!important;}

#gallery-edit-form > .btn{margin-bottom:4px!important;}

.wiki_object .table-moderation tbody tr td.min a,.wiki_object .table-moderation tbody tr td.content-object a{color:#0098FF!important;}

.wiki_object .span8 .related-volumes h4.header-borders a.btn{
