.wiki_object .span8 .related-volumes h4.header-borders a.btn:hover{background-color:#eaebeb!important;}

.wiki_object .wiki-details table tr td select{max-width:64%!important;}

.wiki_object .wiki-details table tr td select#issue_cover_date_quarter{max-width:30%!important;}

.datepicker.dropdown-menu{padding:4px!important;}

.issue-grid li:hover a.issue-date{color:#0098FF!important;}

.wiki_object .gallery-box-pod a.btn{
 color:#515458!important;
 background-color:#f7f7f8!important;
 border:1px solid #a8abad!important;
 background-image:linear-gradient(rgba(0,0,0,0),rgba(0,0,0,0.15))!important;
 background-image:-moz-linear-gradient(rgba(0,0,0,0),rgba(0,0,0,0.15))!important;
 background-image:-webkit-linear-gradient(rgba(0,0,0,0),rgba(0,0,0,0.15))!important;}

.wiki_object .gallery-box-pod a.btn:hover{background-color:#eaebeb!important;}

.wiki_object .js-table-pagintor-table li span a.icon-minus-sign,.wiki_object #wiki-relatedList .js-table-pagintor-table li span a.icon-minus-sign{color:#df2020!important;}

.wiki_object .js-table-pagintor-table li span a.icon-plus-sign,.wiki_object #wiki-relatedList .js-table-pagintor-table li span a.icon-plus-sign{color:#0098FF!important;}

.editorial li a span.further-detail{
