#moderation .table-moderation tr td a{color:#0098FF!important;}

#moderation .span3 .well.filter .often-used li a{color:#0098FF!important;}

#moderation .span3 .well.filter .log li a{color:#df2020!important;}

#moderation .span3 .well.filter .often-used li a[href*="state=approved"],#moderation .user-history ul li.approved span{color:#0098FF!important;}

