 border-radius: 0 0 5px 0!important;
 -moz-border-radius:0 0 5px 0!important;
 -webkit-border-bottom-right-radius:5px!important;}

#forums .forum-aside.board-list ul.often-used li a[href*="ignored"]{color:#df2020!important;}

#default-body .span8 .table tbody tr th a{color:#0098FF!important;}

.span4 .aside-pod li span.filter-checkbox{color:#0098FF!important;}

.imgboxart.issue-cover a,.imgboxart.issue-cover a.hide{display:none!important;}

.wiki-editing aside.secondary-content.span5:hover .imgboxart.issue-cover a{display:inline-block!important;}
      
.wiki-editing aside.secondary-content.span5:hover .imgboxart.issue-cover a.hide{display:none!important;}

/* Forum */

.neutral .message-user{background:#d9d9d9!important;}

.evil .message-user:hover{color:#8f3d3d!important;}

.good .message-user:hover{color:#3d668f!important;}

.neutral .message-user:hover{color:#333!important;}

.message.evil .avatar-user a.avatar{border-color:#efbbbb!important;}

.message.good .avatar-user a.avatar{border-color:#b3d2ee!important;}

.message.neutral .avatar-user a.avatar{border-color:#c2c2c2!important;}

.message.evil .message-wrap{background-color:#f0dbdb!important;}

.message.good .message-wrap{background-color:#dbe6f0!important;}

.message.neutral .message-wrap{background-color:#d9d9d9!important;}

.message.evil .message-inner{border-color:#efbbbb!important;}

.message.good .message-inner{border-color:#b3d2ee!important;}

.message.neutral .message-inner{border-color:#c2c2c2!important;}

.message.evil .message-inner:before{border-right-color:#efbbbb!important;}

.message.good .message-inner:before{border-right-color:#b3d2ee!important;}

.message.neutral .message-inner:before{border-right-color:#c2c2c2!important;}

.message .avatar-user a.avatar.notes{
 background-color:#308ce8!important;
 border:solid 5px transparent!important;
 background-image:linear-gradient(-45deg, #1259a1 25%,transparent 25%,transparent 50%,#1259a1 50%,#1259a1 75%,transparent 75%,transparent)!important;
 background-image:-moz-linear-gradient(-45deg, #1259a1 25%, transparent 25%, transparent 50%, #1259a1 50%, #1259a1 75%, transparent 75%, transparent!important);
 background-image:-webkit-linear-gradient(-45deg, #1259a1 25%, transparent 25%, transparent 50%, #1259a1 50%, #1259a1 75%, transparent 75%, transparent)!important;
 background-image:-webkit-gradient(linear, 0 100%, 100% 0, color-stop(0.25, #1259a1), color-stop(0.25, transparent), color-stop(0.5, transparent), color-stop(0.5, #1259a1), color-stop(0.75, #1259a1), color-stop(0.75, transparent), to(transparent))!important;}

.message .avatar-user a.avatar.high-karma {
 background-color:#e18456!important;
 border:solid 5px transparent!important;
 background-image:linear-gradient(-45deg, #b2501f 25%,transparent 25%,transparent 50%,#b2501f 50%,#b2501f 75%,transparent 75%,transparent)!important;
 background-image:-o-linear-gradient(-45deg, #b2501f 25%, transparent 25%, transparent 50%, #b2501f 50%, #b2501f 75%, transparent 75%, transparent)!important;
 background-image:-moz-linear-gradient(-45deg, #b2501f 25%, transparent 25%, transparent 50%, #b2501f 50%, #b2501f 75%, transparent 75%, transparent)!important;
 background-image:-webkit-linear-gradient(-45deg, #b2501f 25%, transparent 25%, transparent 50%, #b2501f 50%, #b2501f 75%, transparent 75%, transparent)!important;
 background-image:-webkit-gradient(linear, 0 100%, 100% 0, color-stop(0.25, #b2501f), color-stop(0.25, transparent), color-stop(0.5, transparent), color-stop(0.5, #b2501f), color-stop(0.75, #b2501f), color-stop(0.75, transparent), to(transparent))!important;}
        
.message .avatar-user a.avatar.banned, .message .avatar-user a.avatar.karma-blocked {
 background-color:#df2020!important;
 border:solid 5px transparent!important;
 background-image:linear-gradient(-45deg, #861313 25%,transparent 25%,transparent 50%,#861313 50%,#861313 75%,transparent 75%,transparent)!important;
 background-image:-o-linear-gradient(-45deg, #861313 25%, transparent 25%, transparent 50%, #861313 50%, #861313 75%, transparent 75%, transparent)!important;
 background-image:-moz-linear-gradient(-45deg, #861313 25%, transparent 25%, transparent 50%, #861313 50%, #861313 75%, transparent 75%, transparent)!important;
 background-image:-webkit-linear-gradient(-45deg, #861313 25%, transparent 25%, transparent 50%, #861313 50%, #861313 75%, transparent 75%, transparent)!important;
 background-image:-webkit-gradient(linear, 0 100%, 100% 0, color-stop(0.25, #861313), color-stop(0.25, transparent), color-stop(0.5, transparent), color-stop(0.5, #861313), color-stop(0.75, #861313), color-stop(0.75, transparent), to(transparent))!important;}

#forums ul.breadcrumbs li a{color:#0098FF!important;}

#forums .board-list a{color:#0098FF!important;}

#forums .top-users li.on a, #forums aside .forum-aside li.on a{color:#2c2e30!important;}

