css:
@-moz-document domain("tieba.baidu.com") {
* {
    font-family: 'Microsoft YaHei' !important;
}


body {
    background: #FCFCFC !important;
}

.u_mobile {
	display: none !important;
}

#tbhp_opition_div {
	position: fixed !important;
}

#auto_sign_progess_div {
	//position: fixed !important;
}

.ui_bubble_wrap_fixed {
	margin-top: 20px;
}

.u_username .u_ddl_con {
	width: 125px;
    text-align: center;
}

#bdshare {
	display: none !important;
}

.hgame_mod_theme1 {
	display: none !important;
}
}

@-moz-document url-prefix("http://tieba.baidu.com/f?kw="), url-prefix("http://tieba.baidu.com/f?ie=utf-8&kw="), url-prefix("http://tieba.baidu.com/f?fr="), url-prefix("http://tieba.baidu.com/f/good?kw="), url-prefix("http://tieba.baidu.com/f/vote?kw="), url-prefix("http://tieba.baidu.com/p/"), url-prefix("http://tieba.baidu.com/f?ct="), url-prefix("http://tieba.baidu.com/f?kz="), url-prefix("http://tieba.baidu.com/i/"), url-prefix("http://tieba.baidu.com/shipin/"), url-prefix("http://tieba.baidu.com/photo?kw="), url-prefix("http://tieba.baidu.com/bakan/view?kw="), url-prefix("http://tieba.baidu.com/daquan?kw="), url-prefix("http://tieba.baidu.com/daquan/sub?kw=") {
a {
    color: #014A8F !important;
    text-decoration: none !important;
}
a:hover {
    color: #3986A6 !important;
}
a:visited {
    color: #4B0F68 !important;
}


.wrap2 {
    background-position: top center !important;
    background-color: #F3EFE3 !important;
    background-image: url("http://static.tieba.baidu.com/tb/cms/frs/bg/bg_203_wrap2.jpg?v=1") !important;
    background-repeat: no-repeat !important;
}
.wrap_bright,
.container_bright {
    background: none !important;
}


#wd1 {
    border-color: #E5E5E5 !important;
}

#search_submit {
    background: #FCFCFC !important;
}


#com_userbar {
    position: fixed !important;
    top: 0px !important;
    right: 5px !important;
    border: 2px rgb(200,200,200) solid;
    border-top: 0 !important;
    border-radius: 0px 0px 6px 6px;
    background: #FCFCFC !important;
}
#com_userbar:hover {
    box-shadow: lightgray 0px 0px 2px 2px !important;
}
#com_userbar a {
    color: #304560 !important;
}
#com_userbar a:hover {
    color: #3986A6 !important;
}
#com_userbar .u_ddl_con {
    box-shadow: 0px 0px 5px 0px #000 !important;
}


#frs_nav,
#frs_nav_wrap,
#frs_nav_wrap ul,
#frs_nav_wrap ul li,
#frs_nav_wrap ul li p {
    background: #FCFCFC !important;
}
#frs_nav_wrap {
    border-top: 1px solid #D3DAED;
    border-bottom: 1px solid #D3DAED;
}
.nav_left li a {
    font-weight: bold !important;
    color: #161820 !important;
    background: none !important;
}
.nav_left .focus {
    background: none !important;
}
.nav_center li a,
.nav_right li a {
    padding: 0px 0px 0px 8px !important;
    color: #555555 !important;
    background-image: none !important;
}


.pager > span,
.pager > a {
     padding-left: 8px !important;
     padding-right: 8px !important;
     color: #2C72C7 !important;
}

.l_pager > span,
.l_pager > a {
     padding-left: 8px !important;
     padding-right: 8px !important;
     color: #323232 !important;
}


#title1,
.tb-editor-wrapper {
    border-color: #E5E5E5 !important;
}

.subbtn_bg,
.subbtn_bg_hover,
.subbtn_bg_active {
    color: #161820 !important;
    background: #FFFFFF !important;
}


#footer {
    display: none !important;
}
}

@-moz-document url-prefix("http://tieba.baidu.com/f?kw="), url-prefix("http://tieba.baidu.com/f?ie=utf-8&kw="), url-prefix("http://tieba.baidu.com/f?fr="), url-prefix("http://tieba.baidu.com/f/good?kw="), url-prefix("http://tieba.baidu.com/f/vote?kw=") {
#frs_good_nav {
    padding-top: 5px !important;
}
#frs_good_nav span a {
    color: #405065 !important;
}
#frs_good_nav span a:hover {
    font-weight: bold !important;
}
}

