css:
@-moz-document domain('synfig.org') {
body {font: inherit !important;}

#page {border-width: 0px !important; width: auto !important;}

/*--- header ---*/
#page #header {background-size: contain !important; height: auto !important;}
#page #header #headerQuote {
 position: static !important;
 float: right !important;
 font-family: inherit !important;
 width: 50% !important;
 height: auto !important;
 margin: auto 1em !important;
 }
#page #header #headerNav {
 position: static !important;
 top: inherit !important; left: inherit !important;
 margin: auto 20px !important;
 }
#page #header #logo {
 position: absolute !important;
 margin: 0 !important;
 }

}
