css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://ssl.reddit.com),
               url-prefix(http://www.reddit.com),
               url-prefix(https://ssl.reddit.com),
               url-prefix(https://www.reddit.com)
{


/* body */

body {
    width: 99% !important;
    left: 1% !important;
    right: 0% !important;
    background-attachment: fixed !important;
    background-color: #fff !important;
    font-family: Lucida Grande !important;
    font-size: 7pt !important;
    margin-left: 0% !important;
}

/*
====================
Header + navigation
====================
*/


/* turn the nav bar into a drop-down menu */

#header {
    background: transparent!important;
    border-bottom: none !important;
}

    #sr-header-area {
            position: fixed !important;
            right: 1% !important;
            margin-right: 10px !important;
            width: 100px !important;
            top: -1000% !important;
            font-weight: normal !important;
            font-family: limey !important;
            text-align: left !important;
            background: #888 !important;
            height: 200px !important;
            line-height: 12px !important;
            padding-top: 0px !important;
            border: 2px solid !important;
            color: transparent !important;
            z-index: 1001 !important;
            border-radius: 3px !important;
            padding-left: 3px !important;
            padding-right: 3px !important;
            padding-top: 1px !important;
            box-shadow: 0px 3px 2px rgba(0,0,0,0.2) !important;
            overflow: scroll !important;
        }
        
        .separator {
            display: none !important;
        }
        
        #sr-header-area .flat-list li {
            display:block !important;
            overflow: scroll !important;
            padding-left: 2px !important;
        }
       
        #sr-header-area:active, #sr-header-area:hover {
            top: 14px !important;
        }
        
        #sr-header-area:hover #sr-more-link:before {
            background: #888 !important;
            border-radius: 3px !important;
        }
        
        
        #sr-header-area .sr-list a[class="current"]{
            color: #f9f9f9 !important;
            font-weight: normal !important;
            text-shadow: none !important;
        }
        
        .dropdown.srdrop .selected {
            display: none !important;
        }

        #sr-header-area a {
            color: #ccc !important;
            font-size: 9px !important;
            text-decoration: none !important;
            transition: 0.5s color ease;
            top: -2px !important;
        }

            #sr-header-area a:hover {
                color: #FFF !important;
                transition: 0.5s color ease;
            }

        .sr-bar .separator {
            color: #909090 !important
        }
        
        #sr-more-link {
            background: #888!important;
            color: #FFF !important;
            height: 15px !important;
            line-height: 12px !important;
            font-weight: normal !important;
            text-align: center !important;
            position: fixed !important;
            right: -1000px !important;;
            padding-top: 1px !important;
        }

        #sr-more-link:before {
            content: url('http://i46.tinypic.com/mry1wz.jpg') !important;
            text-align: center !important;
            background: transparent !important;
            height: 16px !important;
            padding-top: 2px !important;
            width: 14px !important;
            position: fixed !important;
            top: 1px !important;
            right: 1% !important;
            margin-right: 10px;
            z-index: 5;
        }
        
        #sr-more-link:hover:before, #sr-header-area:hover #sr-more-link:before{
            content: url('http://i.imgur.com/8GQaSW1.png') !important;
            transition: 0.5s color ease;
        }
        
        #sr-more-link:hover:before, #sr-header-area:hover::after {
            background: #888 !important;
            border-radius: 3px !important;
            transition: 0.5s color ease;
         }
         
         #sr-more-link:active {
            top: 14px !important;
            margin-right: 4px !important;
            width: 40px !important;
            border-radius: 3px !important;
            text-align: left !important;
         }
         
/* user menu; links */

#sr-header-area .selected a {
    color: #fff !important;
    font-weight: normal !important;
}
  

    #header-img {
        display: none !important

    }
        .tabmenu {
            vertical-align: 0px !important;
            width: 100% !important;
            text-align: center !important;
            padding-top: 0px !important;
            margin-top: 38px !important;
            position: absolute !important;
            font-family: Helvetica Neue !important;
        }

            .tabmenu li a {
                background: transparent !important;
                color: #909090 !important;
                font-size: 10px !important;
                font-weight: 300 !important;
                padding: 0px 6px !important;
                transition: 0.5s color ease;
                border: 0px none !important;
                margin-left: -1px !important;
            }

                .tabmenu li a:hover {
                    color: #3f3f3f !important;
                    transition: 0.5s color ease;
                }

                .tabmenu li.selected a {
                    color: #3f3f3f !important;
                    background: transparent !important;
                    border: 0px none !important;
                }
                
        ul.tabmenu.formtab {
            margin-top: -5px !important;
            width: 100% !important;
            left: 0% !important;
            margin-left: 0 !important;
            padding-left: 0 !important
        }
    
    #header-bottom-left {
        border: none !important;
        background: none !important;
        box-shadow: none !important;
        height: 40px !important;
        padding-top: 5px !important;
    }
    
    #mail {
        margin-left: 3px !important;
        margin-right: 6px !important;
    }
    
    .user,.pref-lang  {
        margin-left: 0px !important;
        margin-right: 3px !important;
    }

    .logout {
        margin-left: 3px !important;
        margin-right: 0px !important;
    }
    
    .menuarea a {
        margin-left: 3px !important;
        margin-right: 3px !important;
    }
    
    #header-bottom-right {
        margin-top: 10px !important;
        background: transparent !important;
        box-shadow: none !important;
        font-size: 10px !important;
        font-weight: 300 !important;
        padding: 3px !important;
        width: 80% !important;
        right:10% !important;
        left:10% !important;
        text-align: center !important;
        position: relative !important;
        font-family: Helvetica Neue !important;
    }
    
        #header-bottom-right a {
            color: #909090 !important;
            transition: 0.5s color ease;
        }
        
            #header-bottom-right a:hover {
                color: #3f3f3f !important;
                text-decoration: none !important;
                transition: 0.5s color ease;
            }
            
        .user .userkarma {
            border: 0 !important;
            color: #646464;
            font-weight: 300 !important;
        }
        
        .pref-lang {
            font-weight: 300 !important;
        }

/* page header */

.pagename {
    left: 0px !important;
    width: 100% !important;
    position: absolute;
    text-align: center !important;
    text-decoration: none !important;
    bottom: 0px !important;
    font: normal 24pt Helvetica Lt STD Light !important;
    top: 4px !important;
}

.pagename a {
    color: #444 !important;
    text-decoration: none !important;
}

.pagename a:hover {
    color: #222 !important;
    transition: 0.5s color ease;
}

/* turn the sidebar into a drop-down menu */

.side {
    background: #FFF !important;
    box-shadow: rgba(0,0,0,0.25) 0 0 10px !important;
    border: 2px solid #888 !important;
    position: fixed !important;
    padding: 16px !important;
    float: right !important;
    width: 180px !important;
    right: 1% !important;
    top: -100% !important;
    z-index: 1000 !important;

}

.side::before {
    content: url('http://i46.tinypic.com/maaqzd.jpg');
    text-align: center !important;
    background: transparent!important;
    height: 14px !important;
    max-width: 12px !important;
    min-width: 12px !important;
    position: fixed !important;
    top: 1px !important;
    right: 0% !important;
    padding-top: 2px !important;
    padding-left: 0px !important;
    padding-right: 0px !important;
    margin-right: 35px !important;
    z-index: 5;
    transition: 0.5s color ease;
}

.side:hover::before {
    content: url('http://i48.tinypic.com/2ijhgmr.jpg') !important;
    background: #888 !important;
    border-radius: 3px !important;
    transition: 0.5s color ease;
}

.side:hover {
    position: fixed !important;
    top: 15px !important;
}

.sidebox .subtitle {
    background: transparent !important;
}

.sidebox .spacer {
    position: relative;
    margin-top: 10px;
    padding: 5px 0px 0px 44px;
    min-height: 41px;
    background: transparent !important;
}

.titlebox {
    font-size: larger;
    text-align: center !important;
}

.sidebox a {
    color: #1a1a1a !important;
    font-weight: normal !important;
    text-decoration: none !important;
}

.md {
    max-width: 100% !important;
    font-size: 8pt !important;
    font-family: Helvetica !important;
    color: #646464 !important;
}

.panestack-title {
    width: 100% !important;
    text-align: center !important;
}

.comment {
    width: 99% !important;
}

.commentarea {
    width: 100% !important;
}

.titlebox h1 {
    color: black !important;
    font-family: Helvetica !important;
    font-size: 8pt !important;
    text-align: center !important;
    width: 100% !important;
    position: absolute;
    top: 5px;
    left: 11px;
}

.titlebox h1 a {
    display: none !important;
    color: black !important;
    font-family: Helvetica !important;
    font-size: 8pt !important;
    text-align: center !important;
    width: 100% !important;
    position: absolute;
    top: -3px;
    left: -5px !important;
}

.morelink {
    text-align: center;
    font-size: 11px !important;
    font-weight: normal !important;
    line-height: 29px;
    letter-spacing: 0px !important;
}

.linkinfo {
    padding: 5px;
    border: 1px solid rgb(95, 153, 207);
    background-color: #f5f5f5 !important;
    color: #3f3f3f;
    font-size: 8pt !important;
    border-radius: 3px 3px 3px 3px;
    text-align: center !important;
}

    .linkinfo .date {
        font-family: limey !important;
    }
    
    .linkinfo .score .word {
        font-family: Helvetica !important;
        font-size: 11pt !important;
        font-weight: bold;
        color: #1c1c1c !important;
    }

    .linkinfo .score .number {
        font-size: 11pt !important;
        font-weight: bold;
        font-family: Helvetica !important;
        color: #1c1c1c !important;
    }

    .linkinfo .upvotes, .linkinfo .downvotes {
        font-family: limey !important;
        font-size: 8pt !important;
        color: #646464 !important;
    }


    .linkinfo .shortlink input {
        border: 1px solid gray;
        padding: 3px 3px;
        width: 140px !important;
    }



/* 
================
Content
================
*/

/* links */

.link {
    color: #777 !important;
    border-top: 1px solid #c3c3c3 !important;
    min-height: 60px;
    padding: 12px 0 !important;
    position: relative !important;
    background: transparent !important;
    margin-bottom: -1px !important;
    width: 80% !important;
    right: 10% !important;
    left: 10% !important;
    border-bottom: none !important;
}

.link.promotedlink, .organic-listing .link, .organic-listing .link.promotedlink,.organic-listing .promoted,.organic-listing {
    padding: 12px 0px !important;
    margin-bottom: -10px !important;
    border-left: none !important;
    border-right: none !important;
}

.link.last-clicked {
    color: #3f3f3f !important;
    border-left: none !important;
    border-right: none !important;
}

.link .title {
    font-weight: bold !important;
    margin-left: 0px !important;
}

    .lk .thumbnail {
        margin-right: auto!important;
        width: auto !important;
    }

        .link .thumbnail img {
            border: none!important;
            box-shadow: rgba(0,0,0,0.2) 0 0 8px !important;
            width: auto !important;
            margin-right: 3px !important;
        }

    .thing p.title {
        padding-bottom: 0px !important;
    }
     
    .thing .title.loggedin, .thing.p.title  a {
            color: #677996 !important;
            line-height: 14px !important;
            text-indent: 155px;
            text-shadow: none !important;
            transition: 0.5s ease all;
            font-size: 8pt !important;
    }
       
        .thing .title.loggedin:hover, .link .title:hover {
            color: #444 !important;
            transition: 0.5s color ease;
        }

        .thing .title.loggedin:visited {
            box-shadow: none !important;
            color: #677996 !important;
        }
        
        .domain, .domain a {
            font-family: Lucida Grande !important;
            font-weight: bold !important;
            font-size: 7pt !important;
            color: #bf7c83 !important;
        }
    
    .tagline {
        margin-left: 10px !important;
        color: #888!important;
        font-size: 7pt !important;
        font-family: Lucida Grande !important;
        margin-top: 7px !important;
    }
    
    .link .flat-list {
        margin-top: 4px !important;
        margin-left: 2px !important;
    }
    
    .entry {
        border: none !important;
        margin-left: 5px !important;
    }
    
        .entry .buttons li a {
            color: #999 !important;
            font-size: 7pt !important;
            font-weight: normal !important;
            padding: 0 8px 3px !important;
            transition: 0.5s ease all;
        }
        
            .entry .buttons li a:hover {
                color: #1c1c1c !important;
                text-decoration: none !important;
                transition: 0.5s color ease;
            }
            
.expando-button {
    display: none !important
}

.organic-listing .nextprev, .organic-listing .help {
    display: none !important;
}

.title {
    color: #677996 !important;
    background: none !important;
    text-align: left;
    margin-left: 2px !important;
}

.title.click {
    color: #1c1c1c !important;
    background: none !important;
}

.comment .author {
    font-weight: normal;
    color: #666 !important;
}

a.author {
    margin-right: 0.5em;
    font-weight: normal;
    color: #7b80a2 !important;
    transition: 0.5s color ease;
}

a.author:hover, a .author:hover {
    color: #444 !important;
    text-decoration: none !important;
    transition: 0.5s color ease;
}

.tagline .submitter {
    color: #999;
}


/* 
===============
Other pages
===============
*/


/* submitting a link */

.roundfield {
    background: #fbfbfb!important;
    -webkit-border-radius: 10px;
    border-radius: 10px !important;
    margin: 0 auto !important;
    border: 1px solid #909090 !important;
}

.roundfield textarea {
    width:492px!important;
    -webkit-border-radius: 10px;
    border-radius: 10px;
    color: #3f3f3f !important;
    background: #fff !important;
    border-color: #c3c3c3 !important;
}

    .roundfield .title {
        color: #3f3f3f !important;
    }

.roundfield .content {
        background: #fff !important;
}

.textarea {
    color: #3f3f3f !important;
    background: #fff !important;
    border: none !important;
}

.usertext-edit textarea {
    width: 100% !important;
}

#link-desc.infobar {
    background-color: transparent !important;
    color: #646464 !important;
    border-color: #909090 !important;
    -webkit-border-radius: 0px;
    border-radius: 10px;
    border:none !important;
}

#newlink.submit.content {
    width:58em !important;
    margin-right:auto !Important;
    margin-left:auto !important;
    box-shadow:none !important;
    -webkit-box-shadow:none !important;

}

#newlink.submit.content .spacer {
    box-shadow:none !important;
    -webkit-box-shadow:none !important;
    text-align: center !important;

}

.formtabs-content {
    border-top:none !important;
    margin:0 auto !important;
}

.tabmenu.formtab {
    margin-left:6em !important;
}

.infobar {
    background: #f5f5f5 !important;
    border: #333 1px solid !important;
    -webkit-border-radius:10px;
    border-radius:10px;
}

#url {
    color: #3f3f3f !important;
    background: #fff !important;
    -webkit-border-radius:5px;
    border-radius:5px;
    border-color: #c3c3c3 !important;
}

#sr-autocomplete {
    color: #3f3f3f !important;
    background: #fff !important;
    -webkit-border-radius:5px;
    border-radius: 5px;
    border-color: #c3c3c3 !important;
}

#newlink.submit.content .btn {
    margin-left:0 !important;
    margin-top:1em !important;
}

#text-desc.infobar {
    background: #f5f5f5 !important;
    color: #3f3f3f !important;
}

.nextprev {
    text-align: center !important;
}

/* reddits */

.sr-interest-bar {
    position: relative;
    background: none !important;
    overflow: hidden;
    border: none !important;
    margin-bottom: 5px;
    margin-left: 0px !important;
    padding-left:0px !important;
    background: transparent !important;
    top: 10px !important;
    width: 98% !important;
}

    .sr-interest-bar .bubble p {
        display: none !important;
    }
    
    .sr-interest-bar .query-box {
        position: relative;
        border: 1px solid #c3c3c3 !important;
        height: auto !important;
    }
    
    .sr-interest-bar .query {
        font-size: 10px !important;
        margin: 0px;
        padding: 0px;
        outline: none !important;
        background: transparent !important;
        height: auto !important;
    }
    
    .sr-interest-bar ul.results {
        font-size: 11px !important;
    }
    
.searchpane {
    display: none !important;
    background: none !important;
    position: relative !important;
    border: none !important;
    width: 45% !important;
}

    .raisedbox h4 {
        font-weight: normal !important;
        text-align: left !important;
        border: 1px solid #909090 !important;
    }

.infobar {
    display: none !important;
}

.menuarea {
    border-bottom: 0px none !important;
    text-align: center !important;
    padding: 0px 0px 0px 0px !important;
    width: 100% !important;
    color: #646464 !important;
}

.dropdown.lightdrop .selected {
    text-decoration: none !important;
    color: #1a1a1a !important;
}

.selected, .choice.primary {
    font-weight: normal !important;
}

.link .usertext .md {
    border: none !important;
    background: transparent !important;
}

.sitetable {
    width: 100% !important;
    margin-right: -50px !important;
    padding-right: 0px !important;
}


/* 
===========
random 
===========
*/

h1 {
    font-size: 18px;
    font-weight: normal;
    margin: 10px 0px;
    text-align: center !important;
}

#search input,
.goldvertisement,
#ad-frame,
.infobar.welcome,
.titlebox .md,
.sidecontentbox .content,
a.helplink,
.titlebox .age,
.titlebox .bottom,
.titlebox form.flairtoggle,
.titlebox .tagline,
.users-online,
.sidecontentbox .title h1 {
    display: none !important;
}

.organic-listing {
    border: 0px none !important;
}
       
}
