css:
@-moz-document url-prefix("http://theoldreader.com") {
.navbar.navbar-fixed-top{
	position: absolute;
	top: -50px;
-webkit-transition: all 300ms cubic-bezier(0.420, 0.000, 0.580, 1.000); 
   -moz-transition: all 300ms cubic-bezier(0.420, 0.000, 0.580, 1.000); 
    -ms-transition: all 300ms cubic-bezier(0.420, 0.000, 0.580, 1.000); 
     -o-transition: all 300ms cubic-bezier(0.420, 0.000, 0.580, 1.000); 
        transition: all 300ms cubic-bezier(0.420, 0.000, 0.580, 1.000); /* ease-in-out */

-webkit-transition-timing-function: cubic-bezier(0.420, 0.000, 0.580, 1.000); 
   -moz-transition-timing-function: cubic-bezier(0.420, 0.000, 0.580, 1.000); 
    -ms-transition-timing-function: cubic-bezier(0.420, 0.000, 0.580, 1.000); 
     -o-transition-timing-function: cubic-bezier(0.420, 0.000, 0.580, 1.000); 
        transition-timing-function: cubic-bezier(0.420, 0.000, 0.580, 1.000); /* ease-in-out */
}
.navbar.navbar-fixed-top:hover{
	top:0;
-webkit-transition: all 300ms cubic-bezier(0.420, 0.000, 0.580, 1.000); 
   -moz-transition: all 300ms cubic-bezier(0.420, 0.000, 0.580, 1.000); 
    -ms-transition: all 300ms cubic-bezier(0.420, 0.000, 0.580, 1.000); 
     -o-transition: all 300ms cubic-bezier(0.420, 0.000, 0.580, 1.000); 
        transition: all 300ms cubic-bezier(0.420, 0.000, 0.580, 1.000); /* ease-in-out */

-webkit-transition-timing-function: cubic-bezier(0.420, 0.000, 0.580, 1.000); 
   -moz-transition-timing-function: cubic-bezier(0.420, 0.000, 0.580, 1.000); 
    -ms-transition-timing-function: cubic-bezier(0.420, 0.000, 0.580, 1.000); 
     -o-transition-timing-function: cubic-bezier(0.420, 0.000, 0.580, 1.000); 
        transition-timing-function: cubic-bezier(0.420, 0.000, 0.580, 1.000); /* ease-in-out */
}
.reader .container-fluid{
	top:20px;
}

.body-fixed-top>.static{
	border-bottom: 1px solid #ccc;
	float: left;
	width: 100%;
}
.body-fixed-top > .static .page-header{
	float:left;
	border:0 none;
    margin:0;
    padding:0;
}

.body-fixed-top > .static .floating{
	float:right;
    margin:0;
    padding:0;
}
.body-fixed-top > .static .floating .pull-left{
	margin-right:5px
}

.reader .body-fixed-top .slide{
	top:33px !important
}

.clr{display:none;
    position:absolute;
    top:25px;
    z-index:9999;
    background:#fff;
    border:1px solid #ccc;
    padding:5px;
    border-radius:4px;
    box-shadow:2px 2px 10px -2px #666;  
  
-webkit-transition: all 300ms cubic-bezier(0.250, 0.250, 0.750, 0.750); 
   -moz-transition: all 300ms cubic-bezier(0.250, 0.250, 0.750, 0.750); 
    -ms-transition: all 300ms cubic-bezier(0.250, 0.250, 0.750, 0.750); 
     -o-transition: all 300ms cubic-bezier(0.250, 0.250, 0.750, 0.750); 
        transition: all 300ms cubic-bezier(0.250, 0.250, 0.750, 0.750); /* linear */

-webkit-transition-timing-function: cubic-bezier(0.250, 0.250, 0.750, 0.750); 
   -moz-transition-timing-function: cubic-bezier(0.250, 0.250, 0.750, 0.750); 
    -ms-transition-timing-function: cubic-bezier(0.250, 0.250, 0.750, 0.750); 
     -o-transition-timing-function: cubic-bezier(0.250, 0.250, 0.750, 0.750); 
        transition-timing-function: cubic-bezier(0.250, 0.250, 0.750, 0.750); /* linear */
}
.reader .body-fixed-top .page-header:hover .clr{
	display:block;
    -webkit-transition: all 300ms cubic-bezier(0.250, 0.250, 0.750, 0.750); 
   -moz-transition: all 300ms cubic-bezier(0.250, 0.250, 0.750, 0.750); 
    -ms-transition: all 300ms cubic-bezier(0.250, 0.250, 0.750, 0.750); 
     -o-transition: all 300ms cubic-bezier(0.250, 0.250, 0.750, 0.750); 
        transition: all 300ms cubic-bezier(0.250, 0.250, 0.750, 0.750); /* linear */

-webkit-transition-timing-function: cubic-bezier(0.250, 0.250, 0.750, 0.750); 
   -moz-transition-timing-function: cubic-bezier(0.250, 0.250, 0.750, 0.750); 
    -ms-transition-timing-function: cubic-bezier(0.250, 0.250, 0.750, 0.750); 
     -o-transition-timing-function: cubic-bezier(0.250, 0.250, 0.750, 0.750); 
        transition-timing-function: cubic-bezier(0.250, 0.250, 0.750, 0.750); /* linear */
}
}
