.tabbrowser-tab:-moz-lwtheme > .tab-stack > .tab-content[selected="true"] {
	background-image: url("data:image/png;base64,"),
					  url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAYAAAAfCAYAAADEFbwbAAAAP0lEQVQokWPhkjfmYsACWIBYGavEy2tH9bFKcLKzPMYqwcjI+B+XHTgl/g2kDppLAIOEinb8pZ5RA+rzAUsMAOzWH4mRgtJTAAAAAElFTkSuQmCC"),
