					  url("data:image/png;base64,"),
					  url("data:image/png;base64,") !important;
	background-repeat: no-repeat !important;
	background-position: 0, 30px, 100% !important;
	background-size: 30px 31px, -moz-calc(100% - 60px) 31px, 30px 31px !important}
.tabbrowser-tab:not(:last-child):not([selected="true"]):hover + .tabbrowser-tab:not([selected="true"]) > .tab-stack > .tab-content:-moz-locale-dir(ltr) {
	background-image: none !important}
.tabbrowser-tab:not(:last-child):not([selected="true"]):hover + .tabbrowser-tab[pinned][titlechanged]:not([selected="true"]) > .tab-stack > .tab-content:-moz-locale-dir(ltr) {
