	border: 0 !important;
	margin: 0 3px 0 1px !important;
	min-width: 3px !important;
	width: 3px !important}
#nav-bar toolbarseparator {
	margin: -2px 1px -2px !important}
#navigator-toolbox[tabsontop] > #nav-bar {
	padding: 1px 2px 1px 0 !important}

