	border-radius: 0 !important}

#main-window[stylish-url="about:config"] #navigator-toolbox[tabsontop] > toolbar:not(#toolbar-menubar):not(#TabsToolbar),
#main-window[stylish-url="about:sessionrestore"] #navigator-toolbox[tabsontop] > toolbar:not(#toolbar-menubar):not(#TabsToolbar),
#main-window[stylish-url="chrome://global/content/console.xul"] #navigator-toolbox[tabsontop] > toolbar:not(#toolbar-menubar):not(#TabsToolbar),
