	visibility: collapse !important}
#main-window[stylish-url="about:addons"][sizemode=normal][tabsontop]:not(:-moz-lwtheme) #navigator-toolbox::after,
#main-window[stylish-url="about:config"][sizemode=normal][tabsontop]:not(:-moz-lwtheme) #navigator-toolbox::after,
#main-window[stylish-url="about:sessionrestore"][sizemode=normal][tabsontop]:not(:-moz-lwtheme) #navigator-toolbox::after,
#main-window[stylish-url="chrome://global/content/console.xul"][sizemode=normal][tabsontop]:not(:-moz-lwtheme) #navigator-toolbox::after,
