	border-radius: 0 !important;
	height: 0 !important}
#main-window[stylish-url="about:config"][tabsintitlebar] #navigator-toolbox::after,
#main-window[stylish-url="about:sessionrestore"][tabsintitlebar] #navigator-toolbox::after,
#main-window[stylish-url="chrome://global/content/console.xul"][tabsintitlebar] #navigator-toolbox::after,
#main-window[stylish-url="chrome://browser/content/places/places.xul"][tabsintitlebar]#navigator-toolbox::after {
	height: 0 !important}

.tab-close-button {
	-moz-appearance: none !important;
	list-style-image: url("data:image/png;base64,") !important;
	-moz-image-region: rect(0 16px 16px 0) !important;
	margin-right: 1px !important}
.tab-close-button:hover,
.tab-close-button:hover[selected="true"] {
	-moz-image-region: rect(0 32px 16px 16px) !important}
.tab-close-button:hover:active,
.tab-close-button:hover:active[selected="true"] {
	-moz-image-region: rect(0 48px 16px 32px) !important}
.tab-drop-indicator {
	padding-right: 30px !important}

.tabbrowser-tab:not([selected]) .tab-icon-image {
	list-style-image: url("data:image/png;base64,") !important}
.tabs-newtab-button,
#TabsToolbar > #new-tab-button,
#TabsToolbar > toolbarpaletteitem > #new-tab-button {
	list-style-image: url("data:image/png;base64,") !important}

#main-window[sizemode=normal]:not(:-moz-lwtheme) #titlebar-content {
	margin-top: 2px !important;
	padding-bottom: 1px !important}
#main-window[sizemode=normal]:not(:-moz-lwtheme) #appmenu-button {
	margin-top: -2px !important;
	margin-bottom: 0 !important}
#main-window[sizemode=normal]:not(:-moz-lwtheme) #titlebar-buttonbox-container #titlebar-buttonbox {
	margin-top: -2px !important}

#browser-bottombox .toolbarbutton-menu-dropmarker .dropmarker-icon {
	display: none !important}

/* Customize Toolbar */
#CustomizeToolbarWindow {
	padding: 0 !important}
#CustomizeToolbarWindow > #main-box > #instructions {
	margin: 0 !important;
	padding: 9px;
	text-align: center}
#CustomizeToolbarWindow > #main-box > #palette-box {
	margin: 0 !important}
#CustomizeToolbarWindow > #main-box > box {
	padding-bottom: 2px !important;
	padding-top: 3px !important}
#CustomizeToolbarWindow > #main-box > separator {
	display: none !important;
	visibility: collapse !important}
#donebutton {
	margin-bottom: 5px !important}

/* Australis Sidebar */
#bookmarksPanel, #history-panel, #detailsDeck {
	background: #EEF3FA !important}
#placesView, #searchModifiers, #infoPane {
	background: White !important}
#sidebar-header {
	-moz-appearance: none !important;
	background: #F2F5FA !important;
	border: 0 !important;
	color: #566475 !important}
#sidebar-search-container {
	-moz-appearance: none !important;
	border-bottom: 1px solid #A9B7C9 !important;
	padding-bottom: 5px !important}
#bookmarksPanel, #history-panel, #placesList {
	-moz-appearance: none !important;
	background: #F2F6F9 !important}
#placeContent {
	background: #FAFBFD !important;
	box-shadow: none !important}
#detailsDeck {
	background: #F2F6F9 !important}
#main-window[tabsintitlebar]:not([inFullscreen]) #TabsToolbar {
	margin-left: -1px !important;
	margin-right: -1px !important}

/* Iconos nuevos */
.tabbrowser-tab[label="about:config"] .tab-icon-image,
.tabbrowser-tab[label="Untrusted Connection"] .tab-icon-image,
.tabbrowser-tab[label*="n no confiable"] .tab-icon-image,
.tab-icon-image[src*="warning-16.png"],
.bookmark-item[src*="warning-16.png"] {
	background: url("chrome://mozapps/skin/extensions/alerticon-warning.png") center no-repeat !important;
	padding-left: 16px !important}
.tabbrowser-tab[label="about:blocked"] .tab-icon-image,
.tab-icon-image[src*="Blacklist_favicon.png"],
.bookmark-item[src*="Blacklist_favicon.png"] {
	background: url("chrome://mozapps/skin/extensions/alerticon-error.png") center no-repeat !important;
	padding-left: 16px !important}
.messageImage[type="info"], .console-row[type="message"], .statusIcon[status="success"] {
	list-style-image: url("chrome://mozapps/skin/extensions/alerticon-info-negative.png") !important}
.messageImage[type="warning"], .console-row[type="warning"] {
	list-style-image: url("chrome://mozapps/skin/extensions/alerticon-warning.png") !important}
.messageImage[type="critical"], .console-row[type="error"], .console-row[type="exception"] {
	list-style-image: url("chrome://mozapps/skin/extensions/alerticon-error.png") !important}
#PersonalToolbar #bookmarks-menu-button .toolbarbutton-icon {
	height: 18px !important;
	width: 18px !important}

/* url Bar */
#urlbar, .searchbar-textbox {
	font-size: 12.5px !important}
#identity-box {
	font-size: 12px !important}
#notification-popup-box {
	background: linear-gradient(to right, hsla(0,0%,100%,0), White 97%) padding-box !important;
	padding-right: 0 !important;
	padding-left: 6px !important}
#cookieInfoBox {
	background: hsla(0,0%,100%,.8) padding-box !important}
#main-window[stylish-url^="about:"] #page-proxy-favicon,
#main-window[stylish-url^="chrome://"] #page-proxy-favicon {
	list-style-image: url("data:image/png;base64,") !important}
.verifiedDomain > #page-proxy-favicon[pageproxystate="valid"] {
	list-style-image: url("data:image/png;base64,") !important}
#plugins-notification-icon,
#alert-plugins-notification-icon {
	list-style-image: url("data:image/png;base64,") !important}
#blocked-plugins-notification-icon {
	list-style-image: url("data:image/png;base64,") !important}
#plugins-notification-icon,
#alert-plugins-notification-icon,
#blocked-plugins-notification-icon {
	-moz-image-region: rect(0, 16px, 16px, 0) !important}
#plugins-notification-icon:hover,
#alert-plugins-notification-icon:hover,
#blocked-plugins-notification-icon:hover {
	-moz-image-region: rect(0, 32px, 16px, 16px) !important}
#plugins-notification-icon:active,
#alert-plugins-notification-icon:active,
#blocked-plugins-notification-icon:active {
	-moz-image-region: rect(0, 48px, 16px, 32px) !important}

/* W8 Modifications */
#downloads-indicator-progress,
#downloads-indicator-progress > .progress-bar,
#downloads-indicator-progress > .progress-remainder,
#downloads-indicator[paused] > #downloads-indicator-anchor > #downloads-indicator-progress-area > #downloads-indicator-progress > .progress-bar {
	border-radius: 0 !important}
#main-window[tabsintitlebar]:not([inFullscreen]) #toolbar-menubar[inactive] ~ #TabsToolbar:not(:-moz-lwtheme) {
	background: linear-gradient(to top, rgba(10%,10%,10%,.25) 1px, Transparent 1px) !important}
#main-window[sizemode=normal][tabsontop] #toolbar-menubar {
	margin-top: -1px !important}
#main-window[sizemode=normal][tabsontop]:not(:-moz-lwtheme) #nav-bar,
#navigator-toolbox[tabsontop]:not([customizing]) > #nav-bar[collapsed="true"] + #customToolbars + #PersonalToolbar,
#main-window[sizemode=normal][tabsontop]:not(:-moz-lwtheme) #PersonalToolbar {
	border-color: rgba(10%,10%,10%,.25) !important}
#browser-border-start, #browser-border-end {
	display: none !important}
#main-window[sizemode=normal] #browser {
	border: 1px solid rgba(10%,10%,10%,.25) !important;
	border-top: 0 !important;
	border-bottom: 0 !important}
#main-window[sizemode=normal]:-moz-lwtheme #browser {
	border: 0 !important}
#browser-bottombox:not(:-moz-lwtheme) {
	border-color: rgba(10%,10%,10%,.25) !important}

#alltabs-button {
	list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAHCAYAAADam2dgAAAABGdBTUEAALGPC/xhBQAAAAlwSFlzAAALEgAACxIB0t1+/AAAAAd0SU1FB9wCCQ0RGgiDIycAAAAadEVYdFNvZnR3YXJlAFBhaW50Lk5FVCB2My41LjEwMPRyoQAAAEBJREFUKFNj+P//P0EMJuRlBO/iwnBFUIUXgPg6EJ+B0hdgcnBFUIUgBVdANLI4iiIQRjYBhlE4uDBWQVT8nwEAqxSelXWGALgAAAAASUVORK5CYII=") !important}
.tabbrowser-arrowscrollbox > .scrollbutton-up, 
.tabbrowser-arrowscrollbox > .scrollbutton-down {
	list-style-image: url("data:image/png;base64,") !important}

/* Addon-bar */
#addon-bar, #FindToolbar {
	-moz-appearance: none !important;
	background: 0 !important;
	border: 0 !important;
	padding-right: 3px !important}
#browser-bottombox:not(:-moz-lwtheme) {
	-moz-appearance: none !important;
	background-color: hsl(210,75%,92%) !important;
	background-image: linear-gradient(#ABBCCE 1px, hsla(0,0%,100%,.2) 1px) !important;
	background-size: 100% 2px !important;
	background-repeat: no-repeat !important;
	background-clip: padding-box !important;
	border: 1px solid rgba(10%,10%,10%,.25) !important;
	border-top: 0 !important}
#main-window[tabsintitlebar] #browser-bottombox,
#browser-bottombox:-moz-lwtheme {
	border: 0 !important}
#browser-bottombox:not(:-moz-lwtheme) {
	border: 1px solid rgba(10%,10%,10%,.25) !important;
	border-top: 0 !important}
#FindToolbar:not(:-moz-lwtheme) {
	padding: 0 !important;
	color: Black !important;
	text-shadow: 0 0 10px White,
		0 0 10px White,
		0 1px 0 hsla(0,0%,100%,.3) !important}
#FindToolbar .findbar-textbox[status="notfound"],
#FindToolbar .findbar-textbox[focused][status="notfound"] {
	background-color: #F66 !important}
#FindToolbar .findbar-find-next, #FindToolbar .findbar-find-previous, #FindToolbar .findbar-highlight {
	color: Black !important}
#FindToolbar .findbar-find-next > label, #FindToolbar .findbar-find-previous > label {
	display: none !important}
#FindToolbar .findbar-find-next > image, #FindToolbar .findbar-find-previous > image {
	margin: 0 !important}
.findbar-find-next, .findbar-find-previous, .findbar-highlight {
	list-style-image: url("data:image/png;base64,") !important}
/* find-next button */
.findbar-find-next {
	-moz-image-region: rect(0 16px 16px 0) !important}
.findbar-find-next:hover {
	-moz-image-region: rect(16px 16px 32px 0) !important}
.findbar-find-next[disabled="true"] {
	-moz-image-region: rect(32px 16px 48px 0) !important}
/* find-previous button */
.findbar-find-previous {
	-moz-image-region: rect(0 32px 16px 16px) !important}
.findbar-find-previous:hover {
	-moz-image-region: rect(16px 32px 32px 16px) !important}
.findbar-find-previous[disabled="true"] {
	-moz-image-region: rect(32px 32px 48px 16px) !important}
/* highlight button */
.findbar-highlight {
	-moz-image-region: rect(0 48px 16px 32px) !important}
.findbar-highlight:hover {
	-moz-image-region: rect(16px 48px 32px 32px) !important}
.findbar-highlight[disabled="true"] {
	-moz-image-region: rect(32px 48px 48px 32px) !important}
.findbar-highlight:active, .findbar-highlight[checked="true"] {
	-moz-image-region: rect(48px 48px 64px 32px) !important}
.findbar-highlight[checked="true"]:hover {
	-moz-image-region: rect(64px 48px 80px 32px) !important}

/* Sync preference fix */
#syncEnginesList {
	-moz-appearance: none !important;
	background: 0 !important;
	border: 0 !important;
	height: 156px !important}
#syncEnginesList richlistitem {
	background: 0 !important;
	border-color: Transparent !important;
	box-shadow: none !important}

/* Notifications */
#sync-notifications, #content {
	background-color: hsla(0,0%,100%,.8) !important;
	background-clip: padding-box !important;
	box-shadow: none !important}
notification {
	-moz-appearance: none !important;
	background-color: rgba(255,255,127,.2) !important;
	background-image: url("chrome://mozapps/skin/extensions/stripes-warning.png") !important;
	background-repeat: repeat-x !important;
	border-top: 1px solid #AABCCF !important;
	border-bottom: 1px solid #AABCCF !important;
	color: #916D15 !important;
	font-weight: Bold !important;
	text-shadow: 0 0 9px White,
				 0 0 9px White,
				 0 1px 0 hsla(0,0%,100%,.3) !important}
notification[type="critical"] {
	background-color: rgba(255,63,63,.2) !important;
	background-image: url("chrome://mozapps/skin/extensions/stripes-error.png") !important;
	color: #864441 !important}
notification[type="info"] {
	background-color: rgba(112,162,232,.2) !important;
	background-image: url("chrome://mozapps/skin/extensions/stripes-info-negative.png") !important;
	color: #3F3F3F !important}
notification > * {
	-moz-appearance: none !important;
	background-color: Transparent !important;
	background-clip: padding-box !important;
	border: 0 !important;
	box-shadow: none !important}
notification > button {
	-moz-appearance: button !important}
#content notification {
	border-top-color: Transparent !important}
#sync-notifications notification {
	padding-left: 3px !important}

/* TextBox & Search Icons */
.search-go-button, .searchbar-engine-image, .textbox-search-icon,
.ac-result-type-keyword, .autocomplete-treebody::-moz-tree-image(keyword, treecolAutoCompleteImage) {
	background-color: Transparent !important;
