css:
@-moz-document domain("emacswiki.org") {
/* zenburn colors */
/* mixins */
html > * {
  background: #3f3f3f !important;
  color: #dcdccc !important;
  border-color: #656555 !important; }

a {
  color: #f0dfaf !important; }
  a:visited {
    color: #e0cf9f !important; }

.btn {
  color: #dcdccc !important;
  background-color: #7f9f7f;
  background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #7f9f7f), color-stop(100%, #7f9f7f));
  background-image: -webkit-linear-gradient(#7f9f7f, #7f9f7f);
  background-image: linear-gradient(#7f9f7f, #7f9f7f);
  border: 1px solid #656555 !important; }
  .btn:visited {
    color: #dcdccc !important;
    background-color: #7f9f7f;
    background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #7f9f7f), color-stop(100%, #7f9f7f));
    background-image: -webkit-linear-gradient(#7f9f7f, #7f9f7f);
    background-image: linear-gradient(#7f9f7f, #7f9f7f);
    border: 1px solid #656555 !important; }
  .btn:hover {
    background-color: #8fb28f;
    background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #8fb28f), color-stop(100%, #8fb28f));
    background-image: -webkit-linear-gradient(#8fb28f, #8fb28f);
    background-image: linear-gradient(#8fb28f, #8fb28f);
    -webkit-transition: none !important;
    -moz-transition: none !important;
    transition: none !important;
    background-position: 0 !important; }

h1, h2, h3 {
  text-shadow: none !important; }

.table {
  background-color: #6f6f6f !important;
  border: 1px solid #656555 !important; }

body input, body textarea, body select, body button {
  background-color: #4f4f4f !important;
  color: #dcdccc !important;
  border: 1px solid #656555 !important; }

code, pre {
  background-color: #6f6f6f !important;
  color: #dcdccc !important;
  border: 1px solid #656555 !important; }

.navbar-inner {
  background-color: #6f6f6f;
  background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #6f6f6f), color-stop(100%, #3f3f3f));
  background-image: -webkit-linear-gradient(#6f6f6f, #3f3f3f);
  background-image: linear-gradient(#6f6f6f, #3f3f3f);
  border: 1px solid #656555 !important; }

.navbar .nav > li > a {
  text-shadow: none !important; }
.navbar .nav > .active > a {
  color: #dfaf8f !important;
  background-color: #383838 !important; }
  .navbar .nav > .active > a:hover, .navbar .nav > .active > a:focus {
    color: #dfaf8f !important;
    background-color: #383838 !important; }

.toc {
  background-color: #6f6f6f !important;
  border: 1px solid #656555 !important; }

div.two, div.one {
  color: #dcdccc !important;
  background-color: #7f9f7f !important;
  box-shadow: none !important; }

.footer_wrapper {
  background-color: #3f3f3f !important;
  background-color: #3f3f3f;
  background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #3f3f3f), color-stop(100%, #4f4f4f));
  background-image: -webkit-linear-gradient(#3f3f3f, #4f4f4f);
  background-image: linear-gradient(#3f3f3f, #4f4f4f); }

span.builtin {
  color: #93e0e3 !important; }
span.comment {
  color: #7f9f7f !important; }
span.constant {
  color: #bfebbf !important; }
span.function {
  color: #8cd0d3 !important; }
span.keyword {
  color: #f0dfaf !important;
  font-weight: bold !important; }
span.string {
  color: #cc9393 !important; }
span.type {
  color: #7cb8bb !important; }
span.warning {
  color: #e0cf9f !important;
  font-weight: bold !important; }
span.comment span, span.string span {
  color: inherit !important; }
span.comment span.important.constant, span.string span.important.constant {
  color: #dfaf8f !important; }
span.linecomment {
  color: #8fb28f !important; }
span.quote {
  color: #cc9393 !important; }

}
