css:
#top_logo_down {
margin-left: 4px;
visibility: hidden;
width: 134px;
height: 36px;
background: #4E729A url('http://savepic.org/3625099.png') -4px 0px;
margin-right: 8px;
}

#page_header .back {
position: absolute;
width: 100%;
height: 40px;
background: #4E729A url('http://savepic.org/3625099.png') 0px -43px repeat-x;
}

#page_header .left {
position: absolute;
left: 0px;
width: 134px;
height: 40px;
background: #4E729A url('http://savepic.org/3625099.png') 0px -1px;
}

#page_header .right {
position: absolute;
right: 0px;
width: 10px;
height: 40px;
background: #4E729A url('http://savepic.org/3625099.png') 100% -1px;
}

#head_play_btn {
  display: none;
}

.head_nav a.top_nav_link#head_music {
padding-right: 8px;
position: relative;
}

body {
background: #fff;
color: #000;
margin: 0px;
padding: 0px;
direction: ltr;
font-family: Comic Sans MS, arial, verdana, sans-serif, Lucida Sans;
font-size: 12px;
font-weight: normal;
}

.head_nav a.top_nav_link {
display: block;
font-family: HelveticaNeueCyr-UltraLight;
padding: 10px 15px 19px 15px;
height: 13px;
margin-top: -2px;
font-weight: bold;
font-size: 18px;
color: #E8EFF7;
text-shadow: 0px 1px 0px #416389;
}

#ts_wrap {
position: absolute;
top: 0px;
left: 144px;
height: 40px;
width: 174px;
display: none!important;
}

.button_blue button,
.button_gray button {
  border: 1px solid;
  background: none;
  font-size: 11px;
  margin: 0px;
  cursor: pointer;
  white-space: nowrap;
  outline: none;
  padding: 4px 14px;
  *padding: 5px 15px 3px;
  font-family: Comic Sans MS, arial, verdana, sans-serif, Lucida Sans;
  vertical-align: top;
  overflow: visible;
  line-height: 13px;
}

img{
  border-radius: 20px;
}

#header {
position: relative;
font-weight: bold;
font-size: 11px;
padding: 7px 10px;
line-height: 140%;
background: url('http://savepic.org/3632268.png');
color: #607387;
border: solid #D9E0E7;
border-width: 0px 1px;
}

.module_header .header_top {
background: url('http://savepic.org/3632268.png');
overflow: hidden;
padding: 4px 8px;
font-weight: bold;
outline: none;
color: #45688E;
}

#header span, #header b {
color: #5df3c7;
}

.module_header .header_top .right_link {
color: #5df3c7;
font-weight: bold;
}

#public #subscribe, #public #unsubscribe, #public #unshare {
background: #d4d848;
padding: 10px;
width: 180px;
top: 0px;
}
