css:
@-moz-document domain("rouming.cz"), domain("roumenovomaso.cz") {
a {transition-duration:0.2s}

img {max-width:100%;position:relative;z-index:5000}

img:hover {max-width:500%;transition-duration:0.5s;}

hr{opacity:0}

.roumingBody{
  background:black !important;
  background: url(http://i44.tinypic.com/9fsn50.jpg) #000 scroll left top !important; 
  color:rgb(170, 170, 170);
}

.roumingForumMessage{
  background-color:rgb(44, 44, 44) ; 
  color:rgb(170, 170, 170);
  }
  
.roumingForumTitle {
  background-color:#1D1D1D ; 
  color:white;
  box-shadow: inset 0 1px 1px rgba(255, 255, 255, 0.06),inset 0 -1px 1px rgba(0, 0, 0, 0.1);
  text-shadow: 0 1px 1px black;
    }
  
.roumingForumTitle a{
  background-color:#1D1D1D ; 
  color:white;
    }

.roumingForumTitle a:visited{ 
  color:rgb(170, 170, 170);
    }
    
.roumingSubMenu {
  background:none; 
  color:rgb(170, 170, 170);
  border-left:0px;
    }

.roumingSubMenu li a{
  background:none;  
  color:rgb(170, 170, 170); 
  border-right:0px;  
  border-top: 1px solid grey;
      }

.roumingSubMenu li a:hover{
  background:none;   
  color:white;  
  border-top:1px solid white;
      }
  
.roumingMenu {
  background: #efefef; 
  background: linear-gradient(top, rgb(0, 0, 0) 0%,rgb(60, 60, 60) 100%);  
  background: -moz-linear-gradient(top, rgb(0, 0, 0) 0%,rgb(60, 60, 60) 100%); 
  background: -webkit-linear-gradient(top, rgb(0, 0, 0) 0%,rgb(60, 60, 60) 100%); 
  color:rgb(170, 170, 170);
  border-bottom: 1px solid grey;
  text-shadow: 0 1px 0 black;
    }

.roumingMenu li a{
  background:none;  
  color:rgb(170, 170, 170); 
  border-right:0px;
  border-top:0px;
  border-left:0px;
  border-bottom: 0px solid grey;
    top:-2px;
    position:relative;
    }

.roumingMenu li a:hover{
  background:none;   
  color:white; 
  border-bottom: 1px solid white;
  padding:3px 7px;
    }
    
.roumingMenu li.active a{
  background:none;   
  color:white; 
  border-bottom: 1px solid white;
  padding:3px 7px;
      }
  
.input{ 
  color: rgb(202, 202, 202);
  background: rgb(92, 92, 92);
  border: 0;
  box-shadow: inset 0 1px 5px rgba(0, 0, 0, 0.5);
border-radius: 5px;
text-shadow: 0 1px 0 rgba(0, 0, 0, 0.5);
padding: 1px 5px;
    }
          
.roumingButton a{ 
  color:rgb(170, 170, 170);
  background: linear-gradient(top, rgb(0, 0, 0) 0%,rgb(30, 30, 30) 100%)!important;   
  background: -moz-linear-gradient(top, rgb(0, 0, 0) 0%,rgb(30, 30, 30) 100%)!important; 
  background: -webkit-linear-gradient(top, rgb(0, 0, 0) 0%,rgb(30, 30, 30) 100%)!important;
  border: 1px solid #ccc;
    }

.roumingButton a:visited{ 
  color:rgb(170, 170, 170); 
  background: linear-gradient(top, rgb(0, 0, 0) 0%,rgb(30, 30, 30) 100%)!important;   
  background: -moz-linear-gradient(top, rgb(0, 0, 0) 0%,rgb(30, 30, 30) 100%)!important; 
  background: -webkit-linear-gradient(top, rgb(0, 0, 0) 0%,rgb(30, 30, 30) 100%)!important;    
	}

.roumingButton a:hover{ 
  border: 1px solid white;
  background: linear-gradient(top, rgb(0, 0, 0) 0%,rgb(30, 30, 30) 100%)!important;   
  background: -moz-linear-gradient(top, rgb(0, 0, 0) 0%,rgb(30, 30, 30) 100%)!important; 
  background: -webkit-linear-gradient(top, rgb(0, 0, 0) 0%,rgb(30, 30, 30) 100%)!important;  color:white;
    }

.yellow{
  color:white;
	}

::selection{
  background:rgb(68, 68, 68);
  color:white;
	}

::-moz-selection{
  background:rgb(68, 68, 68);
  color:white;
	}

.roumingList a:hover{ 
  color:grey!important; 
  background:none;
    }

.roumingList a:visited{ 
  color:rgb(170, 170, 170);
    }

.roumingForumTitle span {
display: none;
}
}
