css:
@-moz-document
	url-prefix('http://www.tumblr.com/'),
	url-prefix('https://www.tumblr.com/')
{

a.new_post_label > i {
  height: 90px;
}
.new_post_label {
  padding: 79px 1px 0 0 !important;
}
.new_post_label/*_icon*/ > I {
  color: transparent !important;
  background: url('http://25.media.tumblr.com/8f9cc81e9f3c580a88b022535113406f/tumblr_mo9719V0b91rxghfko5_r1_1280.png') no-repeat !important;
  background-size: 525px 95px !important; 
  z-index: -1;
  transition: background-position .08s linear !important;
}

#new_post .new_post_label:nth-of-type(1) > I {background-position: 0px 0px !important}
#new_post .new_post_label:nth-of-type(2) > I {background-position: -75px 0px !important}
#new_post .new_post_label:nth-of-type(3) > I {background-position: -150px 0px !important}
#new_post .new_post_label:nth-of-type(4) > I {background-position: -225px 0px  !important}
#new_post .new_post_label:nth-of-type(5) > I {background-position: -300px 0px !important}
#new_post .new_post_label:nth-of-type(6) > I {background-position: -375px 0px !important}
#new_post .new_post_label:nth-of-type(7) > I {background-position: -450px 0px  !important}
#new_post .new_post_label:hover:nth-of-type(1) > I {background-position: 0px -4px !important}
#new_post .new_post_label:hover:nth-of-type(2) > I {background-position: -75px -4px !important}
#new_post .new_post_label:hover:nth-of-type(3) > I {background-position: -150px -4px !important}
#new_post .new_post_label:hover:nth-of-type(4) > I {background-position: -225px -4px  !important}
#new_post .new_post_label:hover:nth-of-type(5) > I {background-position: -300px -4px !important}
#new_post .new_post_label:hover:nth-of-type(6) > I {background-position: -375px -4px  !important}
#new_post .new_post_label:hover:nth-of-type(7) > I {background-position: -450px -4px  !important}

}
