css:
@-moz-document url-prefix("http://blockspot.eu") {
body {
 	background: white; 
  animation: fadeInTop 0.5s;
             -moz-animation: fadeInTop 0.5s;
             -webkit-animation: fadeInTop 0.5s;
             -o-animation: fadeInTop 0.5s;
}
section {
 	background: #f5f5f5;
  border: none;
  -moz-box-shadow:    0px 0px 0px 0px #fff;
  -webkit-box-shadow: 0px 0px 0px 0px #fff;
  box-shadow:         0px 0px 0px 0px #fff;
  margin-top: 10px;
              -webkit-border-radius: 0px;
            -moz-border-radius: 0px;
            border-radius: 0px;
    animation: none;
             -moz-animation: none;
             -webkit-animation: none;
             -o-animation: none;
}
 nav ul li, aside .widget {
  background: #f5f5f5;
}
nav ul .currenttab, nav ul li:hover{
background:#EBE9E9;
}
header {
 background:#2d2d2d ;
  margin: 0px;
  height: 30px;
  overflow: hidden;
  position: fixed;
top: 0px;
  left: 0px;
  text-align: center;
  color: #bbb;
  font-weight: bold;
}
section header {
 background: none; 
  position: relative;
  color: black;
  font-weight: normal;
}
header img {
	height: 90%;
}
header a {
 	display: none; 
}
header span:before {
  content: "BLOCKSPOT "; 
  font-weight: bold;
  color: #bbb;
  position: fixed;
top: 5px;
  left: 10px;
    }
.navbuttonlinksrechts, .hscrollerbase, #impressum_daten
{
 background: #f5f5f5; 
}
nav {
 float: none; 
  width: 100%;
  margin-top: 50px;
  color: white;
  padding-left: 26px;
  min-height: 0px;
}
nav ul {
 display: inline; 
  padding-right: 31px;
}
 nav ul li {
  display: inline;
  border: 0px;
  padding: 10px;
  -moz-box-shadow:    0px 0px 0px 0px #fff;
  -webkit-box-shadow: 0px 0px 0px 0px #fff;
  box-shadow:         0px 0px 0px 0px #fff;
}
 aside .widget form input, #search {
  background: #ccc;
  width: 168px;
}

.hscrollerbar, ul.tabs {
  background: #ccc!important;
  }
}
