css:
@-moz-document url-prefix("http://www.youtube.com/results") {
#search-results li { float: left; width: 250px; height: 195px; }
.yt-lockup-content { float: left; }
.video-thumb, .yt-lockup-thumbnail img { width:276px !IMPORTANT; }
.yt-lockup-meta-info li:first-child { display: list-item; }
.yt-lockup-meta-info li,
.yt-lockup.clearfix.yt-uix-tile.result-item-padding.yt-lockup-movie.yt-lockup-tile.vve-check.context-data-item,
.yt-lockup-badges { display: none; }
.yt-lockup-title { height: 26px; overflow: hidden; }
.yt-uix-pager.search-pager { float: left; width: 100%; }
#watch-channel-brand-div,.watch-pyv-vid,#instream_google_companion_ad_div,.promoted-videos,#watch-longform-ad { display: none; }
/* Fixed playlists and navigation */
.yt-lockup-meta-info li { height: auto !IMPORTANT; }
.yt-lockup-meta.yt-lockup-playlist-items { display: none; }
@media only screen and (min-width: 1150px) { div.yt-card.clearfix { width: 1150px; } }
@media only screen and (min-width: 870px) { div.yt-card.clearfix { width: 870px; } }
@media only screen and (max-width: 870px) { div.yt-card.clearfix { width: 592px; } }
.yt-uix-pager.search-pager { margin-top: 20px; }
}
