css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.flickr.com/") {

/* Replace the black bar page header with a light-grey strip */
.new-header {
    background: transparent !important;
} 
#global-nav {
    background: transparent !important;
    background-color: #EEE !important;
	margin-left:125px !important;
	width: 90% !important;
	height: 40px !important;
}

/* Set text of top-menu items to black */
#global-nav span {
	color: #000 !important;
}

/* Make link text show in blue */
a:link {
    color: blue !important;
}

/* Set size of group image*/
.pc_img {
    width: 65% !important;
}

/* Lighter group info panel with dark text*/
.group-info {
    background-color: #EEE !important;
}
p {
    color: black !important;
}
span {
    color: #555 !important;
}

/* Hide the 'explore' panel */
DIV#sihp-explore.sihp-sidebar-module, .explore-title {
    visibility:hidden !important;
    }

/* Hide the flickr blog panel */
DIV#sihp-flickrBlog {
    visibility:hidden !important;
}

/* Hide the 'People You May Know' panel */
DIV#sihp-friendfinder, .peopleYouMayKnow {
    visibility:hidden !important;
}

/* Remove the fade from contact's photos */
.fade-big {
    visibility:hidden !important;
}

/* Set background & size for contact thumbnails */
.sessionPhotos {
    background: transparent !important;
    width: 450px !important;
}

/* Reduce size of contact's photos */
.imgWrapper {
    background: transparent !important;
    background-color: #EEE !important;
    width: 450px !important;
    margin-bottom: 80px !important;
}
.card {
    background: transparent !important;
    margin-bottom: 20px !important;
}
img {
height: 100% !important;
width: 100% !important;
margin: 0px !important;
}

/* The above changes mess up the size of buddy-icons; so fix this */
.comment-buddy-icon {
    height: 36px !important;
    width: auto !important;
}

/* Reduce the width of the recent comments area for a cleaner look */
.micro-card {
    background: transparent !important;
    width: 700px !important;
    background-color: #EEE !important;
}   

/* Fix the size and position of the comments thumbnail image */
.micro-image {
    position: absolute !important;
    margin-left: 600px !important;
    height: 100px !important;
    width: 100px !important;
}
    
/* Hide the comment-area background image */
.background-image {
    visibility:hidden !important;
}

/* Make the image nav-button background transparent */
.opacityBackground {
    background: transparent !important;
}

/* Dark text in the comments area */
#comments small.comment-meta a, .comment-author, .action-date {
    color: #555 !important;
}

.comment-controls, .comment-reply {
    display: inline !important;
}

.content-section-wrapper {
    width: 600px !important;
    }

.action-author {
    color: black !important;
    font-size: 15px !important;
}

.card-comment .comments-inner p, .usernameLink {
    color: black !important;
}

/* Increase the height and reduce the width of the text insert box for comments */
textarea#message {
    height: 60px !important;
}
}
