/** Coffeescript.org Title Fix v1.2_2013-06-14, Fx-Opera-Safari-Chrome
 * http://userstyles.org/styles/89070
 * Author: spmbt, http://spmbt.habr.ru/
 */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("coffeescript.org"),regexp("http://cidocs.ru/coffeescript/.*"){
    #flybar{overflow: hidden; width: 1px; min-width: 0!important;
        left:2px!important; margin-left: -13px!important; padding-left: 64px!important; opacity: 0.5;
        font-family:Arial,"Helvetica Neue","Lucida Grande","Lucida Sans Unicode",Helvetica,sans-serif!important}
    #flybar:hover{overflow: inherit; width: auto; min-width: 590px!important;
         margin-right: 155px!important; padding-left: 252px!important; opacity:1}
    #fadeout{height: 30px!important;
        background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(255,255,255,0.8)), color-stop(100%,rgba(255,255,255,0)))!important;
        background: -webkit-linear-gradient(top, rgba(255,255,255,0.8) 0%,rgba(255,255,255,0) 100%)!important;
        background: -o-linear-gradient(top, rgba(255,255,255,0.8) 0%,rgba(255,255,255,0) 100%)!important;
        background: -moz-linear-gradient(top, rgba(255,255,255,0.8) 0%, rgba(255,255,255,0) 100%)!important;
        background: linear-gradient(to bottom, rgba(255,255,255,0.8) 0%,rgba(255,255,255,0) 100%)!important;
    }
    .container >ul +h2 +p +p +h2{margin-bottom: 0!important; padding: 2px 8px; border: 1px solid #ccc; background-color:#eee/*; cursor: pointer*/}
    .container >ul +h2 +p +p +h2 ~p, .container >ul +h2 +p +p +h2 ~ul{display:none2; width: 100%!important; margin: 0 0 0 -36px;  padding: 6px  0 6px 36px!important}
    .container >ul +h2 +p +p +h2:hover{background-color:#ddd}
    .container >ul +h2 +p +p +h2/*:hover*/ ~p, .container >ul +h2 +p +p +h2/*:hover*/ ~ul,
    .container/*:hover*/ >ul +h2 +p +p +h2 ~p, .container/*:hover*/ >ul +h2 +p +p +h2 ~ul{display:block; background-color:#eaeaea}
}
