css:
@-moz-document url-prefix("https://twitter.com"), url-prefix("http://twitter.com") {
@media all and (max-width: 640px) {

  .dashboard, .site-footer, .stream-end-inner { display: none !important; }
  #timeline, .profile-card.profile-header { float: left; }
  #page-container { width !important: auto !important; }
  
  body { background-image: none !important; }
  body, label, input, textarea, select, button { font-family: "Lucida Grande", Arial, sans-serif !important; font-size: 12px !important; line-height: 15px !important; }
  
  .ithumb-container { position: relative !important; margin: 5px 0 !important; height: auto !important; width: auto !important; }
  .ithumb-ul { height: auto !important; }
  .ithumb-img { -webkit-transform: none !important; }
  .stream-item.hovered-stream-item:not(.open) .ithumb-a, .stream-item:hover:not(.open) .ithumb-a { margin-left: 0 !important; }
  .stream-item { border-left: 0 !important; border-right: 0 !important; }
  
  .bird-topbar-etched { background-image: none !important; }
  .content-main { float: left !important; width: 521px !important; } /* was 510 */
  .profile-card.profile-header, .profile-header-inner-overlay { width: 519px !important; }
  .wrapper, .wrapper-narrow, .wrapper-permalink { width: auto !important; padding: 40px 0px 0px !important; }
  .with-icn { font-size: 10px !important; }
  .header-inner { display: none; }
  
  .stream-item-header .avatar { width: 32px !important; height: 32px !important; top: 7px !important; left: 7px !important; margin-left: -40px !important; }
  .stream-item-header .username { font-size: 10px !important; }
  .stream-item .content { margin-left: 35px !important; }
  .time .js-relative-timestamp { font-size: 10px !important; margin-right: -4px; }
  
  .permalink-tweet .permalink-header .avatar { left: 12px !important; top: 12px !important; }
  .permalink-replies .tweet .avatar { position: relative; width: 16px !important; height: 16px !important; top: 2px !important; left: 0px !important; }
  .my-tweet { background-color: rgb(250, 250, 250); }
  .tweet { padding: 4px 12px !important; }
  
  .tweet:hover { background: white url(data:image/png,%89PNG%0D%0A%1A%0A%00%00%00%0DIHDR%00%00%00%05%00%00%00%0A%08%06%00%00%00%7C9%940%00%00%00%2FIDATx%DAm%C9%B1%0D%00%20%10%C3%C0l%0E%23dcP*%BF%08.%CF%B2%BD%F4%14%3C%19%8D%0C%B0%06%C0%F8%E1VA*H%C0%08%A0%0B%C6%A1K%88%C6%97%5C%FE%00%00%00%00IEND%AEB%60%82) no-repeat center left !important; }
  
  .modal-tweet iframe, .modal-tweet img:not(.avatar), .modal-tweet video { display: none; }
  
  form.focus, input.focus { width: 130px !important; }
  .typeahead { width: 291px !important; }
  
  .permalink-in-reply-tos .tweet, .permalink-replies .tweet { padding-left: 45px !important; }
  
  .modal-large { width: 510px !important; }
  .modal-table { width: 145px !important; }
  .shortcut-label, .social-proof, .social-proof a { font-size: 10px !important; }
  
  .front-signup, .front-welcome-text, .footer { display: none; }
  .front-card { left: 0; }
  
  .tweet { overflow: auto !important; }
  .dropdown-menu { xposition: relative !important; }

  .page-canvas { display: none !important; }
}

}
