css:
@-moz-document url("https://forums.butterflylabs.com/vbshout.php?do=detach&instanceid=1") {
a#collapse_dbtech_shoutbox1, .above_body, .breadcrumb, .dbtech_vbshout_padding, #footer, #footer_copyright, #footer_morecopyright {display:none !important;}


.blockbody, .formcontrols, .settings_form_border {
    bottom: 17px !important;
    top: 24px !important;
    left: 0px !important;
    right: 0px !important;
    position: absolute !important;
  }

  
.body_wrapper, .collapse, .dbtech_vbshout_activeusers, .dbtech_vbshout_wrapper{
    padding: 0px 0px 0px 0px !important;
  }

UL.dbtech_fullshouts, UL.dbtech_shouts {
  top:0px !important;
  bottom:0px !important;
  left:0px !important;
  right:120px !important;
  position:absolute !important;
  float:none !important;
}

LI.alt {
  left:0px;
  right:119px;
  top:16px;
  bottom:0px;
  position:absolute;
}

DIV.dbtech_vbshout_activeusers {
  top:0px;
  bottom:4px;
  right:0px;
  position:absolute;
  overflow-y:hidden;
}

DIV.dbtech_vbshout_frame {
  top:0px !important;
  bottom:0px !important;
  right:0px !important;
  position:absolute !important;
  height: auto !important;
  padding-bottom:5px !important;
}

DIV[name="dbtech_vbshout_menucode"] {
  FIXME: Seem-to-have-broken-the-Shout-To-X-menus;
}

DIV[name="dbtech_vbshout_frame_notice"] DIV.highlight {
  left:3px;
  right:3px;
  bottom:44px;
  position:fixed;
  z-index:999;
}

DIV[name="dbtech_vbshout_frame_sticky"] {
  top:40px;
  left:3px;
  right:140px;
  position:absolute;
  background:#FFFF85;
}

DIV DIV.dbtech_vbshout_wrapper > DIV{
  bottom:0px !important;
    top:0px !important;
    right:0px !important;
    left:0px !important;
    position:absolute !important;
  height:100% !important;
  }

DIV.dbtech_vbshout_wrapper DIV.table DIV.tr {display:block !important;}
DIV.dbtech_vbshout_wrapper DIV.table DIV.tr DIV.td {display:block !important;}

.dbtech_vbshout_tabs {
    padding: 0px 2px 0px 2px !important;
    margin-right: 3px;
  }

DIV.dbtech_vbshout_window, DIV.dbtech_vbshout_window DIV.dbtech_vbshout_frame {
  position:absolute !important;
  bottom:45px;
  top:0px;
  right:0px;
  left:0px;
}

#bsb, form, .body_wrapper, .collapse{
    position:absolute !important;
    left:0px !important;
    top:0px !important;
    right:0px !important;
    bottom:0px !important;
}
  
INPUT.primary {
  right:0px;
  position:absolute;
  bottom:25px;
}

INPUT[name="dbtech_vbshout_roomname"] {
  bottom:3px;
  right:3px;
}

.footer_time{
    position:absolute;
    bottom:2px;
  left:2px;
  }

LI > DIV.texteditor {
  left:0px;
  right:0px;
  bottom:-2px;
  position:absolute;
}

DIV[style="float:left; display:inline; padding-right:6px;white-space:nowrap;"] {
  bottom:3px;
  right:0px;
  position:absolute;
}
}
