input, select, textarea {
-moz-appearance: none !important;
}

body {
font-family: sans-serif;
font-size:0.812em;
background:#FFFAF7  repeat-x;
line-height:100%;
margin:0;
color:#111;
}

#wrap {
background:#fff;
position:relative;
max-width:92.308em;
min-width:70.8em;
width:100%;
margin:0 auto;
}

a:link, a:visited { color:#2042DF !important; }
a:hover, a:focus, a:active { color:#FF3300 !important }

a img { border:none !important }

#header {
margin-left:130px;
padding:0.7em 1em 0.6em 1em;
background:#FE7D27  0% 100% repeat-x;
font-size:0.93em;
overflow:hidden;
margin-bottom:92px;
}
#logo { display:none; }
#header a img {
border:none !important;
vertical-align:middle;
}
#header img[src*="/avatars/"] {
border:1px solid #fff;
}
#header form a img {
margin-left:0.5em;
}
#header form {
margin-top:-0.2em;
}
#header input {
border:1px solid #C95102;
padding:0.2em 0.25em;
vertical-align:middle;
font-size:1em;
color:#666;
background:#f2f2f2  repeat-x;
font-family: sans-serif;
width:15em;
}
#header li {
/*display:inline;*/
float:right;
list-style:none;
border-left:1px solid #C75001;
padding:0 1em;
}
#header a {
color:#FAEEE6 !important;
}
#header a:hover, #header a:active, #header a:focus {
color:#fff !important;
}
#header .noborder {
border:none;
}

#eli {
height:100px;
width:100px;
display:block;
overflow:hidden;
text-indent:-9000px;
position:absolute;
top:0;
left:0;
background: no-repeat;
}

#naviwrap li {
display:inline;
}
#naviwrap {
position:absolute;
z-index:1000;
right:20px;
top:10.4em;
}
#naviwrap a {
background:#E37400;
font-size:0.93em;
font-weight:bold;
color:#fff !important;
padding:0.4em;
text-transform:capitalize;
text-decoration:none !important;
-moz-border-radius:3px;
-webkit-border-radius:3px;
-khtml-border-radius:3px;
border-radius:3px;
background:#FE7D27  -20px 50% repeat-x;
}
#naviwrap a:hover, #naviwrap a:focus, #naviwrap a:active {
text-decoration:underline;
}

.banner-top {
width:728px;
height:90px;
top:2.7em;
right:0;
left: 100px;
margin:0 auto;
position:absolute;
}
.banner-right {
width:300px;
height:250px;
margin-bottom:20px;
}

body * {
margin: 0;
padding: 0;
font-size:1em;
line-height:inherit;
}

#sidebar {
clear:both;
width:300px;
float:right;
display:block;
margin:0 20px 3em 0;
overflow:hidden;
}

#newswrap {
margin:20px 360px 0 20px;
}

p {
line-height:150%;
margin:1em 0;
}

.news-submitted {
font-size:0.85em;
color:#999;
margin-top:0.3em;
}
.news-submitted a {
color:#999 !important;
font-weight:bold;
}
.news-submitted a:focus, .news-submitted a:active, .news-submitted a:hover {
color:#666;
}
.news-submitted p {
margin:0;
}
.news-submitted a img {
float:left;
padding:1px;
border:1px solid #ddd !important;
margin-right:0.3em;
width:16px !important;
height:16px !important;
vertical-align:middle;
}
.news-submitted br {
display:none;
}
.news-submitted strong {
float:left;
width:10em;
font-weight:normal;
color:#777;
margin-right:0.5em;
overflow:hidden;
text-overflow: ellipsis;
white-space: nowrap;
}
.news-submitted {
line-height:20px !important;
overflow:hidden;
}

.news-tags {
float:left;
margin-right:0.5em;
padding-right:0.5em;
border-right:1px solid #d0d0d0;
}

.news-details form {
display:inline;
}
.tool select {
border:none;
color:#555;
text-transform:none;
font-family: sans-serif;
}
.news-details option {
padding:0 0.5em;
}

.news-tags, .news-details {
font-size:0.85em;
color:#999;
}

.news-tags a, .news-details a { color:#5678DC !important; /* color:#4B6FDA; */ }

.news-details .tool a, .news-tags .tool a { text-transform:capitalize !important; }

.news-details a:hover, .news-details a:focus, .news-details a:active { color:#FF3300 !important }

.tool {
border-left:1px solid #D0D0D0;
padding:0 0.5em;
}

.comments-counter {
padding-right:0.5em;
}

.news-body div {
line-height:140%;
}

.news-body .comments-counter a {
padding-left:20px;
background: 0% 80% no-repeat;
}

.news-details img {
vertical-align:middle;
}

.news-details {
margin-top:0.3em;
line-height:150%;
}

#post .news-details {
text-transform:capitalize;
}

.mnm-published, .mnm-queued, .mnm-discarded {
border:1px solid #D95700;
background:#D95700  100% 100% repeat-x;
color:#FBDBBD;
font-size:0.85em;
padding:0 0 0.35em 0;
position:relative;
}

.mnm-published a, .mnm-queued a, .mnm-discarded a {
font-size:2em;
padding:0.3em 0 0 0;
line-height:100%;
text-decoration:none !important;
color:#fff !important;
}

.mnm-queued {
border:1px solid #F2CCB0;
background:#F9E8DB  100% 100% repeat-x;
color:#EBA474;
}
.mnm-queued a { color:#C44F00 !important; }

.mnm-discarded {
border:1px solid #aaa;
background:#999  100% 100% repeat-x;
color:#eee;
}
.mnm-discarded a { color:#fff !important; }

.menealo {
margin-top:3px;
}

.menealo a {
font-weight:bold;
text-decoration:none !important;
}

.menealo {
font-size:0.85em;
background:#EFEFEF  repeat-x;
}

.menealo span, .menealo a {
display:block;
padding:0.5em 0;
line-height:100%;
}

.menealo a { border:1px solid #C9D4F4 !important; }
.menealo span { bordeR:1px solid #ddd; color:#999; }

.menealo a:hover {
color:#2C56D3 !important;
background:#fff;
border:1px solid #6280DE;
}

.news-shakeit {
width:5em;
display:block;
text-align:center;
position:absolute;
top:4px;
left:0;
}

.news-summary {
position:relative;
margin-bottom:1.5em;
min-height:5.6em;
}

.news-shakeit a {
display:block;
}

.news-body p {
margin:0.1em 0 0.3em 0;
}

.news-body {
padding-left: 5.9em;
}

.news-body h1 {
font-size:1.4em;
font-weight:normal;
}

.news-body h1 img {
vertical-align:middle;
}

.news-body h1 a {
line-height:125%;
border-bottom:1px solid #BFCCF2;
text-decoration:none !important;
}

.news-body h1 a:visited { color:#809AE5 !important; }

.news-body h1 a:hover, .news-body h1 a:active, .news-body h1 a:focus {
color:#FF3300 !important;
border-color:#FFD6CC !important;
}

.warn {
padding:0.4em 0.6em;
line-height:125%;
font-size:0.93em;
margin:0 0 0.3em 0;
border:1px solid #FFE580;
background:#FFFFCC;
}

#singlewrap {
clear:both;
margin:0 20px !important;
padding-top:0 !important;
}

#singlewrap .tabsub {
clear:both;
}

.subheader, .tabsub-shakeit, .tabsub {
margin:10px 20px 0 20px;
padding:0 10px;
overflow:hidden;
background:#fff  repeat-x bottom;
}

.tabsub {
margin:0 0 1.5em 0;
}

.subheader li, .tabsub-shakeit li, .tabsub li {
display:inline;
font-size:0.85em;
}

.subheader a, .tabsub-shakeit a, .tabsub a {
border:1px solid #CCC;
background:#F2F2F2  repeat-x;
border-bottom:none;
text-decoration:none !important;
padding:0 0.8em;
margin-right:0.6em;
text-transform:capitalize;
color:#333 !important;
line-height:25px;
border-bottom:1px solid #d95700;
}

.subheader .selected a.toggler, .tabsub-shakeit .tabsub-this a.toggler {
border:none;
background:none !important;
padding:0.6em 0.3em 0 0.3em;
}
.subheader .selected a.toggler, .tabsub-shakeit .tabsub-this a.toggler img {
vertical-align:middle;
}

.subheader a:hover, .subheader a:focus, .subheader a:active, .tabsub-shakeit a:hover, .tabsub-shakeit a:focus, .tabsub-shakeit a:active, .tabsub a:hover, .tabsub a:focus, .tabsub a:active {
background:#fff;
border-color:#666;
border-bottom:1px solid #d95700;
color:#111;
}

.subheader .selected a, .tabsub-shakeit .tabsub-this a, .tabsub .tabsub-this a {
border:1px solid #D95700;
border-bottom:1px solid #fff;
}

ul.subheader a.icon, ul.tabsub-shakeit a.teaser {
background-image:none !important;
}
ul.subheader a.icon:hover, ul.tabsub-shakeit a.teaser:hover {
border:none;
}

.subheader li a, .tabsub-shakeit li a, .tabsub li a { float:left; }

a.icon, a.teaser { border:none; background:none; margin:0; padding:0.6em 0.6em 0 0.6em; }
a.icon img, a.teaser img { border:none; }

.catsub-block {
clear:both;
border:1px solid #D95700;
border-top:none;
padding:0.6em 0.6em 0.5em 0.6em;
font-size:0.85em;
color:#999;
margin:0 20px 0 20px;
}

.catsub-block ul {
line-height:160%;
}

.catsub-block ul li {
display:inline;
text-transform:capitalize;
}

.catsub-block ul li a {
padding:0 0.25em;
}

.tabmain {
clear:both;
font-size:0.938em;
padding:0 40px;
overflow:hidden;
background:#fff  repeat-x bottom;
position:relative;
width:93%;
padding:0 3.5%;
}

.tabmain img {
vertical-align:middle;
}

#post .tabmain {
margin-bottom:20px;
}

.tabmain li {
display:inline;
}

.tabmain a {
border:1px solid #CCC;
color:#333 !important;
background:#F2F2F2  repeat-x;
border-bottom:none;
text-decoration:none !important;
padding:0 0.9em;
margin-right:0.6em;
text-transform:capitalize;
line-height:29px;
float:left;
border-bottom:1px solid #666;
}

.tabmain a:hover, .tabmain a:focus, .tabmain a:active {
background:#fff;
border-color:#999;
border-bottom:1px solid #333;
color:#111;
}

.tabmain .tabmain-this a {
border-color:#666;
border-bottom:1px solid #fff;
}

.subheader .selected a, .tabmain .tabmain-this a, .tabsub-shakeit .tabsub-this a, .tabsub .tabsub-this a {
color:#111 !important;
font-weight:bold;
background: repeat-x !important;
border-bottom:1px solid #fff !important;
}

.tabsub a img {
border:none !important;
vertical-align:middle;
}

#sidebar a {
text-decoration:none !important;
}

#sidebar {
color:#333;
margin-top:20px;
}

#sidebar a:hover {
text-decoration:underline;
}

#sidebar h4 a {
text-decoration:none !important;
color:#333 !important;
border:none;
line-height:100%;
}

#sidebar h4 {
line-height:120%;
font-size:1.31em;
text-transform:capitalize;
border-bottom:1px solid #ccc;
margin-top:1.5em;
margin-bottom:0.3em;
}

#sidebar ul {
list-style:none;
}

#sidebar ul li {
padding:0.3em 0;
}

.mini-pages {
clear:both;
margin-top:1em;
font-size:0.93em;
}

.pages-margin a, .pages a, .pages span.current, .pages span.nextprev, .pages-margin span.current, .pages-margin span.nextprev, .mini-pages a {
border:1px solid #6280DE;
padding:0.4em 0.63em;
margin-right:0.3em;
text-decoration:none !important;
font-weight:bold;
background:#EFEFEF  repeat-x;
text-transform:capitalize;
}

.pages-margin a:hover, .pages-margin a:active, .pages-margin a:focus, .pages a:hover, .pages a:focus, .pages a:active, .mini-pages a:hover, .mini-pages a:focus, .mini-pages a:active {
border-color:#FF5C33;
}

.pages-margin span, .pages span, .mini-pages span {
padding:0.4em 0.6em;
color:#555;
}

.pages span.nextprev, .pages-margin span.nextprev {
border:1px solid#C9D4F4;
color:#ccc;
}

.pages-margin span.current, .pages span.current, .mini-pages .current a {
color:#fff !important;
background:#476BD9;
border:1px solid #2C56D3;
}
.mini-pages span.current {
padding:0.4em 0;
}

.pages-margin, .pages {
padding-top:2em;
font-size:0.93em;
}
.pages {
margin-bottom:2em;
}

.comments-list {
list-style:none;
}

.comment-body, .comment-body-hidden {
border:1px solid #F7C299;
border-bottom:1px dotted #F7C299;
background:#FEFBF9;
padding:0.6em 0.9em;
overflow:hidden;
line-height:160%;
}
.comment-body-hidden {
color:#aaa;
border-color:#eee;
background:#fff;
}

a img.avatar {
float:left;
margin-right:0.9em;
border:1px solid #ccc !important;
background:#fff;
padding:1px;
}

.comment-body img { border:none; vertical-align:middle; }

.comment-body br {
line-height:100%;
}

.high {
background:#FFFFCE;
border:1px solid #DDAC88;
color:#000;
}

.comment-info {
text-align:right;
float:right;
}

.comment-info img {
margin-left:0.5em;
}

.comment-meta, .comment-meta-hidden {
margin-bottom:1.6em;
overflow:hidden;
background:#FEF4ED  100% 100% repeat-x;
border:1px solid #F7C299;
border-top:none;
padding:0.3em 0.4em 0.1em 0.4em;
}
.comment-meta-hidden {
border-color:#eee;
background:#f9f9f9;
}

.comment-votes-info {
float:left;
}

.comment-votes-info img {
margin-right:0.4em;
}

.comment-info a { font-weight:bold; }

.comment-info, .comment-votes-info {
font-size:0.85em;
color:#555;
line-height:180%;
}

.comment-info img, .comment-votes-info img {
border:none;
vertical-align:middle;
}

.commentform {
clear:none;
margin-top:3em;
}

fieldset {
border:1px solid #ddd;
padding:1em;
margin:1em 0;
}

.rich-edit-key {
float:right;
margin-left:0.5em;
}

.commentform label {
font-size:0.93em;
color:#333;
line-height:140%;
margin-bottom:1em;
display:block;
}

legend {
font-weight:bold;
font-size:1.31em;
color:#B74900;
display:block;
}

dl {
overflow:hidden;
margin-left:1em;
}
dt {
display:block;
float:left;
clear:both;
width:12em;
text-transform:capitalize;
font-weight:bold;
padding:0 0 0.8em 0;
}
dd {
display:block;
padding:0 0 0.8em 0;
float:left;
}
dl.categorylist {
width:24.5%;
float:left;
margin:0;
}
.categorylist input {
vertical-align:middle;
}
.categorylist dt {
clear:none;
width:100%;
}
.categorylist dd {
float:none;
padding-left:1.5em;
}


textarea, input {
border:1px solid #7D7D7D;
border-right:1px solid #ccc;
border-bottom:1px solid #ccc;
font-family: sans-serif;
padding:0.2em;
}

.note {
font-weight:normal;
line-height:150%;
color:#666;
font-size:0.85em;
}

.commentform textarea {
padding:0;
margin-bottom:1em;
}

#container textarea {
width:98%;
padding:1%;
}

.commentform .button {
font-size:1.08em;
padding:0.4em 0.3em;
}

.button, button {
font-weight:bold;
background:#efefef;
font-family: sans-serif;
color:#222;
padding:0.2em 0.15em;
border:1px solid #666;
border-left:1px solid #aaa;
border-top:1px solid #aaa;
}

.thumbnail { float:right; padding:3px; background:#fff; border:1px solid #ccc; }

.storyrsslist a, .rss a {
padding-left:20px;
background:url(http://meneame.net/img/common/feed-icon-12x12.png) 0% 80% no-repeat;
}
.storyrsslist em, .rss em {
font-style:normal;
font-weight:bold;
}

.mnu-seguiment-ext {
padding:1em 0;
border-top:1px solid #ccc;
text-transform:capitalize;
}


#footthingy {
text-align:center;
border-top:1px solid #ddd;
margin-top:2em;
padding:1.5em 0 3em 0;
clear:both;
}
#footthingy li {
display:inline;
padding:0 0.5em;
border-left:1px solid #ccc;
font-size:0.85em;
color:#666;
}
#footthingy li:first-child {
border-left:none;
}
#footthingy p { display:none; }
#footthingy ul+ul { display:none; }
#footwrap {
overflow:hidden;
width:100%;
clear:both;
margin:4em 0 0 0;
padding-top:1.5em;
border-top:1px solid #ddd;
background: repeat-x;
}
#footcol1, #footcol2, #footcol3, #footcol4, #footcol5 {
float:left;
width:15%;
padding:0 2.4%;
border-left:1px solid #E8E8E8;
}
#footcol1 {
border-left:none;
}

#footwrap h5 {
font-size:1.08em;
color:#666;
font-weight:bold;
text-transform:capitalize;
margin-bottom:0.2em;
}
#footwrap ul {
font-size:0.85em;
list-style:none;
margin-bottom:1.2em;
}
#footwrap ul li {
text-transform:capitalize;
line-height:160%;
}



.mnm-pop, .votes {
border:1px solid #D95700;
background:#D95700  50% 50% repeat-x;
color:#FBDBBD;
font-weight:bold;
color:#fff;
text-align:center;
padding:0.6em 0;
float: left;
width: 2.7em;
margin:0.1em 0 0.5em 0;
}
.mnm-pop.queued, .votes.queued {
border:1px solid #F2CCB0;
background:#F9E8DB  50% 50% repeat-x;
color:#C44F00;
}
.sidebox h5 {
font-weight:normal;
margin-left:3.4em;
}
.sidebox h5 a {
line-height:125%;
}
.mini-pop, .cell {
clear:both;
border-bottom:1px solid #DDD;
/*margin:0.5em 0;*/
padding:0.5em 0;
}


.nube, .tagcloud {
line-height:200%;
text-align:center;
}


.topcommentsli li, .sidebox .comments li {
border-bottom:1px solid #DDD;
margin:0.3em 0;
line-height:128%;
}
.topcommentsli strong, .sidebox .comments strong {
color:#666;
}
.topcommentsli a strong, .sidebox .comments a strong {
color:inherit;
}



#addpost div {
clear:none;
}


.genericformtxt {
line-height:150%;
color: #303030;
margin-bottom:1em;
}
.genericform {
clear:both;
margin:0 20px !important;
}
#singlewrap .genericform  {
clear:none;
}
.genericform label {
font-weight:bold;
}
.genericform fieldset p {
margin:0 0 1em 0;
}
fieldset fieldset {
padding:0.5em 1em;
margin:1em 0;
}
fieldset fieldset legend { font-size:1em; }


#thisform input {
vertical-align:middle;
}

.instruction-list {
margin:1em 0;
}
.form-full {
width: 100%;
}

.faq {
width:100%;
clear:both;
margin:0 !important;
}
.faq li, .instruction-list li {
margin-left:2em;
padding-bottom:0.5em;
line-height:150%;
}
.faq-title, .topheading h2, #singlewrap h2 {
color:#B74900;
font-weight:normal;
font-size:1.77em;
margin-bottom:1em;
border-bottom:1px solid #ddd;
line-height:100%;
}
.topheading {
margin-top:1.5em;
}
.faq h4, .faq h3 {
color:#D95700;
font-size:1.31em;
}
.faq p {
margin:0.4em 0 1em 0;
}
pre {
font-size:1.15em;
line-height:150%;
margin-top:0.3em;
}
.faq h3 a {
color:#2C56D3 !important;
padding:0.7em 0.8em !important;
background:#f9f9f9 !important;
border:1px solid #ccc !important;
}

















.comments h3, #singlewrap  h4 {
font-size:1.31em;
margin-bottom:0.3em;
}
.comments h3 a, #singlewrap h4 a {
line-height:125%;
border-bottom:1px solid #BFCCF2;
text-decoration:none;
}
.comments h3 a:visited, #singlewrap h4 a:visited {
color:#809AE5;
}
.comments h3 a:hover, .comments h3 a:active, .comments h3 a:focus, #singlewrap h4 a:hover, #singlewrap h4 a:active, #singlewrap h4 a:focus {
color:#FF3300;
border-color:#FFD6CC;
}
h4 {
font-size:1.18em;
}



table {
border-collapse: collapse;
width: 100%;
margin-bottom: 1.5em;
clear: both;
}
table img {
border: 1px solid #ddd;
background:#fff;
margin-right:0.3em;
padding:1px;
vertical-align:middle;
}
th, td {
