css:
@media print {
        
  h2, h3, h4, h5 { page-break-after: avoid; }
  p, h2, h3, h4, h5 { orphans: 3; widows: 3; }
  pre, blockquote { page-break-inside: avoid; }
  thead { display: table-header-group; } 
  tr, img, figure { page-break-inside: avoid; }
   

  .post_title {
    font-size: 100%;
  }

  .post .hubs {
    font-size: 110%;
  }
    
  .post .content {
    font-size: 250% !important;
  }
  
  #comments .message {
    font-size: 210% !important;
  }  
  
  .comments_list .comment_item .info a.username {
     font-size: 170% !important;
  }

  .html_format pre code { 
    font-size: 70% !important;
  }
  
  #header,
  .menu,
  .sidebar_right,
  .voting,
  .link_to_comment,
  .to_parent,
  .show_tree,
  .infopanel_wrapper,
  .for_users_only_msg,
  .rotated_posts,
  iframe,
  #header_mouse_activity,
  #company_id,
  .published,
  #header_parsed,
  .footer_logos,
  #footer,
  .tags,
  .block_after_post,
  .bottom_promo_blocks,
  .comments_list .comment_item .info time {
    border: solid 1px red !important;
    display: none !important;
  }
        
  body {
    width: 100% !important;
    padding: 0 !important;
    min-width: 0 !important;
  }
      
  #layout {
  	margin: 0 !important;
  	max-width: 100% !important;
  	min-width: 100% !important;
  }
         
  #layout .inner {
  	padding-bottom: 0;
    margin: 0;
  }
  
  #layout .wrapper {
    padding: 0 !important;
    border-radius: 0 !important;
    background: none !important;
  }

  #layout .wrapper .inner {
    padding: 0 !important;
    border-radius: 0 !important;
  }

  #layout, .content_left {
    width: 100% !important;
    padding: 0 !important;
  }
        
  .content_left { 
    margin: 0 !important; 
    float: none !important;
  }
        
  a { 
    text-decoration: none !important;
    border-bottom: solid 1px #000 !important;
    padding-bottom: 1px !important;
  	color: #333 !important;
  }
        
  .name a { 
    border: none !important;
  }
        
  .post .content {
  	line-height: 140% !important;
  }

  .post.translation h1.title .post_title {
	background-image: none !important;
    padding-left: 0 !important;
  }

  .spoiler_text { 
    display: block !important
  }  
        
  .html_format pre code { 
    background-color: #fff !important; 
    color: #000 !important; 
  }

  .post h1.title .flag_translation {
	font-size: 100% !important;
    background-image: none !important;
  }
  
  .comments_list h2.title {
	color: #000 !important;
  }
    
  .html_format ol {
	margin-left: 45px !important;
  }

  code .tag, code .tag .title, code 
  .pi code .id, code .attr_selector, code .pseudo, code .literal, code 
  .keyword, code .hexcolor, code .css .function, code .ini .title, code 
  .css .class, code .list .title, code .tex .command,
  code .attribute, code .rules .keyword, code .number, code .date, code .regexp, code .tex .special,
  code .string, code .value, code .filter .argument, code .css .function .params, code .apache .tag {
    color: #000 !important;
  }
}
