css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.flickr.com") {

#global-nav {
position: relative !important;
}
.subnav-refresh {
background: transparent !important;
}
.subnav-refresh .cover-photo-holder:before {
display: none !important;
}
.subnav-refresh .subnav-middle {
height: 75px !important;
}
#cover-photo {
display: none !important;
}
.sn-avatar-mask {
display: none !important;
}
.subnav-refresh .sn-avatar {
bottom: auto !important;
top: 10px !important;
width: 48px !important;
height: 48px !important;
}
.subnav-refresh .sn-avatar.sn-avatar-size-0 img.sn-avatar-ico, .subnav-refresh .sn-

avatar.sn-avatar-size-48 img.sn-avatar-ico, .subnav-refresh .sn-avatar.sn-avatar-

size-60 img.sn-avatar-ico, .subnav-refresh .sn-avatar.sn-avatar-size-100 img.sn-

avatar-ico {
border: 0 !important;
}
.subnav-refresh .sn-avatar a.mm {
display: none !important;
}
.subnav-refresh .person {
left: 70px !important;
bottom: auto !important;
top: 10px !important;
}
.subnav-refresh h1 {
display: inline !important;
font-size: 1.5em !important;
font-weight: bold !important;
color: #000 !important;
text-shadow: 0 0 0 transparent !important;
}
.subnav-refresh h2 {
display: inline !important;
color: #666 !important;
text-shadow: 0 0 0 transparent !important;
}
.subnav-refresh .stats {
bottom: auto !important;
top: 14px !important;
}
.subnav-refresh.subnav-state-ready .subnav-middle .cover-cta {
display: none !important;
}
.subnav-refresh .subnav-holder {
border: 0 !important;
background: transparent !important;
bottom: auto !important;
top: 42px !important;
box-shadow: 0 0 0 transparent !important;
}
.subnav-refresh .subnav-holder ul.nav-links:first-child {
/* Prevent Photostream | Sets | Favorites being deactivated by other subnav overlapping it */
position: relative;
z-index: 10;
width: 230px;
}
.subnav-refresh ul.nav-links {
height: auto !important;
margin-left: 61px !important;
}
.subnav-refresh, .subnav-refresh li {
font-size: 13px !important;
}
.subnav-refresh ul.nav-links li.sn-navitem{
border: 0 !important;

}
.subnav-refresh ul.nav-links li.sn-navitem.sn-active, 
.subnav-refresh ul.nav-links li.sn-navitem:hover {
background: transparent !important;
color: #000 !important;
border: 0 !important;
}
.subnav-refresh ul.nav-links li.sn-navitem a {
background: transparent !important;
color: #3464e6 !important;
border-right: 1px solid #f2f2f2 !important;
}
.subnav-refresh ul.nav-links li.sn-navitem.sn-active a {
color: #000 !important;
}
.subnav-refresh ul.nav-links li.sn-navitem a {
padding: 2px 10px !important;
line-height: 1.2 !important;
text-shadow: 0 0 0 transparent !important;
}

.subnav-refresh ul.nav-links li.sn-navitem a.sn-ico.sn-ico-more {
display: none !important;
}
.subnav-refresh ul.nav-links.nav-links-tools {
right: 0 !important;
left: auto !important;
width: 100% !important;
}
.subnav-refresh ul.nav-links.nav-links-tools li.sn-navitem {
background: transparent !important; !important;
width: 100% !important;
}
.subnav-refresh .sn-dropdowntarget {
top: 0px !important;
}
.subnav-refresh .sn-dropdowntarget ul.sn-dropdown:before {
display: none !important;
}
.subnav-refresh .sn-dropdowntarget ul.sn-dropdown {
display: block !important;
position: static !important;
width: auto !important;
padding-left: 284px !important;
right: 0 !important;
left: auto !important;
background: transparent !important;
box-shadow: 0 0 0 transparent !important;
border: 0 !important;
padding-top: 0 !important;
padding-bottom: 0 !important;
}
.subnav-refresh .sn-dropdowntarget ul.sn-dropdown li.sn-dropdownitem {
padding: 0 !important;
}
.subnav-refresh ul.nav-links.nav-links-tools li.sn-navitem {
border: 0 !important;
}
.subnav-refresh .sn-dropdowntarget ul.sn-dropdown li.sn-dropdownitem a {
float: left !important;
padding: 2px 10px !important;
border: 0 !important;
border-right: 1px solid #f2f2f2 !important;
font-size: inherit !important;
}
.subnav-refresh .sn-dropdowntarget ul.sn-dropdown li.sn-dropdownitem.sn-divider {
display: none !important;
}
.subnav-refresh .sn-dropdowntarget ul.sn-dropdown li.sn-dropdownitem a[data-

track="YouSubnav-popular"],
.subnav-refresh .sn-dropdowntarget ul.sn-dropdown li.sn-dropdownitem a[data-

track="YouSubnav-photos_of"] {
display: none !important;
}

#global-nav {
background: #f8f8f8 !important;
border-bottom: 1px solid #e4e4e4 !important;
}
#global-nav .logo .refresh-logo-2013, #global-nav .logo .refresh-logo-2013-2x {
background-image: url('http://iufoundation.iu.edu/images/iu-extras/flickr-logo.jpg') !important;
}
.gn-link, .gn-link:link, .gn-link:visited {
color: #454545 !important;
font-weight: bold !important;
}
#global-nav .tools form .gn-search {
border-width: 1px !important;
border-radius: 3px !important;
}
}
