@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.420chan.org") {
    #content {
        background: transparent !important;
    }
    
    .reply {
        background: none repeat scroll 0% 0% rgba(64, 64, 64, .8) !important;
    }
    .replyheader {
        background: none repeat scroll 0% 0% rgba(58, 58, 60,.8) !important;
    }
    .opcomment {
        margin-right: 200px !important;
    }

    body {
        background: url("http://i.imgur.com/DqAz6KX.png") fixed no-repeat rgb(42, 42, 42) !important;
        background-position: right -50px bottom !important;
        background-size: 300px !important;
    }
}



