css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.zerohedge.com"), domain("zerohedge.com") {

  @font-face {
    font-family: 'Podkova';
    font-style: normal;
    font-weight: 700;
    src: local('Podkova Bold'), local('Podkova-Bold'), url(http://fonts.gstatic.com/s/podkova/v8/2jQxAnybA2Euoel-lC6TCQzyDMXhdD8sAj6OAJTFsBI.woff2) format('woff2'), url(http://fonts.gstatic.com/s/podkova/v8/2jQxAnybA2Euoel-lC6TCb3hpw3pgy2gAi-Ip7WPMi0.woff) format('woff');
  }


  body {
  color: #636363 !important;
  font-size: 1.6rem !important;
  line-height: 1.5 !important;
  }

  .node__content p {
      line-height: 1.5 !important;
  }
  main {
  padding-left: 100px !important;
      max-width: 940px !important;

  }

  body.sidebars #squeeze {
    margin: 0 auto !important;
  }

  .teaser-header-text h2 a {
   font-size: 1.8rem;
  }
  .view-content h2 {
      font-size: 1.8rem;
  }
  .teaser-title {
  color: #636363;
  font-family: 'Podkova', serif !important;
  font-weight: 700 !important;
  }

  #breadcrumb .breadcrumb a {
  color: #cfcbcb;
  font-size: 150%
  }
  section.field, #footer, .sidebar-left-wrapper, .sidebar-right-wrapper {
  display: none !important;
  }

  .teaser-title a, a {
      color: #3281CB !important;
      }

  .teaser-title a:hover, a:hover {
    color: red !important;
    border: 2px dotted red !important;
    text-decoration: none !important;
    margin: -2px !important;
  }
.menu .menu-item a:hover, a.header-logo {
	color: inherit  !important;
    border: none  !important;
    margin: 0 10px  !important;
}
}
