css:
@-moz-document domain("www.pixiv.net") {
/*** 広告 ***/
#header-banner { display: none !important; }
.ads_anchor { display: none !important; }

/*** ヘッダ ***/
.header .layout-wrapper
{
	width: 970px;
	height: 47px;
}

.header .link-list a[href="http://www.pixiv.net/premium.php?ref=header_ja"] { display: none !important; }

.header .link-list a[href="/cate_r18.php"]
{ 
    position: relative;
    left: 30px;
}

.header .link-list a[href="http://www.pixiv.net/help.php"], a[href="http://www.pixiv.net/setting_user.php"], a[href="/logout.php"]
{ 
    position: relative;
    left: 20px;
}

.header .title { display:none !important; }

.header .navigation-list
{
	position: relative;
  	width: 544px;
	left: -20px;
	bottom: 5px;
}

.notifications
{
	position: relative;
	width: 90px;
	bottom: 30px;
	left: 395px;
}

.header .link-list
{
	position: relative;
  	width: 240px;
	top: 12px;
	left: 745px;
}

.header .category-list {display:none !important;}

.header .ui-search
{
	position: relative;
	left: 170px;
	bottom: 90px;
}

#pp-config-btn
{ 
    position: relative;
    left: 90px;
  	bottom: 30px;
}

.ad {display:none !important;}
}

@-moz-document url("http://www.pixiv.net/mypage.php") {
/*** マイページトップ ***/
.rounded {display:none !important;}
}

@-moz-document url("http://www.pixiv.net/bookmark_new_illust.php") {
/*** お気に入り新着 ***/
.column-title 
{
  	margin: -5px 0px 0px 10px;
	font-size: 14px;
  	font-weight: nomal;
}

.column-title+.count-badge {margin-bottom:6px;}

.column-notice {display:none !important;}

.column-menu
{
	height:20px;
}
  
.column-menu .menu-items
{
  position: absolute;
  top:-9px;
  padding: 0;
}
}

@-moz-document url("http://www.pixiv.net/bookmark.php") {
/*** ブックマークページ ***/
.column-title
{
	margin: -5px 0px 0px 10px;
	font-size: 14px;
  	font-weight: nomal;
}
  
.count-badge
{
    position: relative;
  	bottom: 3px;
}
  
.column-menu { height: 20px; }

.column-menu .menu-items
{
	position: relative;
    bottom: 7px;
}

.column-menu .menu-items { height: 10px; }

.column-order-menu {
height: 25px;
}

.column-action-menu.pp-float{display:none !important;}

.column-action-menu{height: 40px;}

.column-action-menu .buttons
{
	position: relative;
    left: 150px;
    bottom: 30px;
}
}
