css:
@-moz-document domain('kanbanflow.com') {

.columnHeader {
    -moz-user-select: none;
    background-color: #EEEEEE;
    border-bottom: 1px solid silver;
    border-top: 1px solid silver;
    height: 30px;
    padding: 8px 0;
    position: relative;
    text-align: center;
}
.columnHeader > h2 {
    -moz-user-select: none;
    font-size: 16px !important;
    left: 34px;
    margin: 0;
    overflow: hidden;
    position: absolute;
    right: 34px;
    text-overflow: ellipsis;
    top: 10px !important;
    white-space: nowrap;
}
h2 {
    color: #666666;
    font-size: 20px;
    margin-bottom: 15px;
}
.collapsed-title {
    -moz-box-sizing: padding-box;
    display: block;
    font-size: 15px !important;
    margin: auto;
    overflow: hidden;
    padding: 10px 4px;
    text-align: center;
}
#taskDialog {
    background-color: #F4F4F4 !important;
    border: 2px solid #F4F4F4 !important;
    border-radius: 10px 10px 10px 10px;
    box-shadow: none !important;
    <!--8px 8px 20px #666666-->
    height: 430px;
    left: -1000px;
    padding: 32px 7px 12px 12px;
    position: absolute;
    width: 440px;
    z-index: 2001;
}
.taskDialog-header {
    background: linear-gradient(#666666, #333333) repeat scroll 0 0 transparent !important;
    border-bottom: 1px solid #CCCCCC !important;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    cursor: move;
    font-family: Arial;
    font-size: 14px;
    font-weight: bold;
    height: 24px;
    left: -2px;
    padding: 4px 9px 0 16px;
    position: absolute;
    right: -2px;
    top: -2px;
}
.taskDialog-legend {
    background-color: #FFFFFF !important;
    border: 1px solid #BBBBBB !important;
    font-size: 11px !important;
    font-weight: bold;
    left: 7px;
    padding: 0 3px;
    position: absolute;
    top: -10px;
}
#taskDialog-overlay {
    background-color: black;
    bottom: 0;
    left: 0;
    position: absolute;
    right: 0;
    top: 0;
    z-index: 2000;
    opacity: .5 !important;
}
.task {
    background-color: #FFF9D7;
    border: 1px solid #FED22F;
    border-radius: 5px 5px 5px 5px;
    cursor: pointer;
    font-size: 14px !important;
    margin: 10px;
    padding: 10px !important;
    position: relative;
    word-wrap: break-word;
}
.subTasks {
    border-top: 1px dashed #FED22F;
    font-size: 11px !important;
    list-style-type: none;
    margin-top: 7px;
    padding-top: 5px;
}





}
