 /*-------------------------------------------------------------------------------------*/
/*Facebook White By GiNTTeR                                                             */ 

/*-------------------------------------------------------------------------------------*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com") {


/* change background image */

body{
background: url("http://www.mebleprojekt.zafriko.pl/pics/K%20101%20SE%20Bia%C5%82y.jpg") bottom center fixed !important;
}

/* social fixer button */

#bfb_options_button_icon{
     display:none !important;
}

#bfb_options_button{
     opacity: 1 !important;
     margin-top:3px !important;
     margin-bottom:-4px !important;
     background-color:transparent !important;
}

#bfb_options_button:hover{
     color:#000 !important;
}


#bfb_options_button:before{
     content: "Social Fixer" !important;
     font-weight: bold;
     color: #D8DFEA;
}

#bfb_option_list:hover{
     background-color:#6D84B4 !important;
}

/* hide advertisments */

.fbTimelineSideAds, .UIStandardFrame_SidebarAds, #rightCol #pagelet_ego_pane .ego_column:nth-child(1),
#pagelet_side_ads, .ego_column, #sidebar_ads, .fbPhotoSnowboxAds, .fbPhotoSnowboxAdsSide, #pagelet_adbox,
.fbEmu.fbEmuBlock.fbEmuEgo[id*="-id_"], #ego, a[href*="/campaign/111111"], .fbEmuMarketplace,
#egoRefreshAds, #pagelet_ego_pane_m, #pagelet_ego_pane_w, .profile_sidebar_ads, .sidebar_ads, .adcolumn_header, 
.admarket_ad, .ad_story, .social_ad_advert, .ads_feedback, .next_ad_button, .more_ads{
     background: none !important;
     display: none !important
} 


#pagelet_rhc_footer{
     display: none !important;
}

.fbFeedTicker{
     display:none !important;
}



/* hide left hand column elements */

 

#adsNav{
     display:none !important;
}

 

/*[[pagelinks]]*/

 

 



/* font size and face */

*{font-family:"Calibri" !important;}
*{font-size: inherit !important;}

body 
     {font-size: 0.7em !important;
}

.navHeader
     {font-size: 0.9em !important
} 

.sortLink_p{
     font-size: 0.6em !important;
     background-color:transparent !important;
} 



/* fixes for the photo page when not viewing in lightbox mode */

#fbPhotoPageContainer{ 
     background-color: #fff !important;
     border:20px solid rgb(255,255,255); !important;
     box-shadow: 40px 0px 0px rgb(255,255,255);
}



/* left column changes */

.home #leftCol{
     position:fixed! important;
}

#leftCol{ 
     background-color: rgba(229,229,229,0.8); 
     border:none;
     box-shadow: -10px 0px 0px rgba(229,229,229,0.8)
}

     
.uiSideNav .selectedItem .item, .uiSideNav .selectedItem .item:hover, 
.uiSideNav ul .selectedItem .subitem, .uiSideNav ul .selectedItem .subitem:hover{
     background:#b3b3b3 !important;
     border-radius:3px 0 0 3px;
     text-shadow:none;
}

.uiSideNav .item:hover, .uiSideNav .item:active, .uiSideNav .item:focus, 
.uiSideNav .subitem:hover, .uiSideNav .subitem:active, .uiSideNav .subitem:focus{
     background:#fff! important;
     border-radius:3px 0 0 3px;
}

/* navigation bar changes */

#pageNav a:hover{ 
     color: #fff !important; 
     background: none !important; 
}

#navAccount ul a:hover, #navAccount ul a:focus, #navAccount ul a:active, 
#navAccount .logoutButton:hover input, #navAccount .logoutButton input:active, 
#navAccount .logoutButton input:focus{
     background: none !important;
     color:#fff!important;
}


ul #navAccountInfo a:hover, ul #navAccountInfo a:focus, ul #navAccountInfo a:active{
     background: none! important;
     color:#3b59a4 !important;
}

#blueBar, body {
     background-image:url(http://i40.tinypic.com/2rf4ow2.png);
}

#blueBar{
     position: fixed !important;
     top: 0px;
     width: 100%;
     visibility: visible;
     box-shadow: 0px 5px 2px 8px rgb(0,0,0);
     z-index: 14 !important;
     border: 2px solid #3C507A !important;
}

.pas{
     background:#e9e9e9;
     border:1px solid #b3b3b3;
     border-right:0;
     border-radius:3px 0 0 3px;
     text-shadow:none;
}

.uiHeaderTopBorder{
     border-color:#e9e9e9 !important;
}


.uiScrollableAreaGripper{
     display:none;
}


/* hover over story color */

ul.profile-friends div.UIImageBlockTemplate:hover, li.uiStreamStory:hover{
     background-color: #f2f2f2 !important;
}


/* timeline specific changes */

.pagesTimelineButtonPagelet.fixed_elem{
     background: transparent !important;
     margin: transparent !important;
     border: transparent !important;
}

#pagelet_timeline_main_column{
     margin: transparent !important;
     border: transparent !important;
     box-shadow: -10px 0px 0px rgba(255,255,255,0.9);
}

#admin_panel{
     box-shadow: -10px 0px 0px rgb(255,255,255);
     margin: transparent !important;
     border: transparent !important;
}

.fbTimelineScrubber{
     background: transparent !important;
     margin: transparent !important;
     border: transparent !important;
}



/* background transparency and colours */

#contentCol,#left_column,#right_column{ 
     background-color: rgba(255,255,255,0.9) !important; 
}

.inputtext,.add_comment_text,.uiMorePager, .box_header{ 
     background-color: rgba(255,255,255,0.5) !important; 
}

.ufi_section, .photo_table, .photo_container{
     background: none !important; 
     border: none !important;
}

#pageFooter, #rightCol, #contentArea, .UIStory, #mainContainer, .profile_top_wash{
     background: none !important; 
     border: none !important;
}

.uiSideNav a{ 
     border: none !important; 
}

.uiSideNav li.selected a{
     background-color: rgba(255,255,255,0.4) !important;
}

.uiSideNav a:hover{
     background-color: rgba(255,255,255,0.4) !important;
}

}
