*/

/* === Layout === */

@-moz-document url-prefix(http://forum.americasarmy.com/) {


* {
  /* Reset browsers default margin, padding and font sizes */
  margin: 0;
  padding: 0;
}

html {
  font-size: 100%;
}

body {
  /* Text-Sizing with ems: http://www.clagnut.com/blog/348/ */
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, Arial, sans-serif;
  color: #000000;
  background-color: #e3e3dd;
  font-size: 62.5%; /* This sets the default font size to be equivalent to 10px */
  margin: 0px 0px 0px 0px;
}

.topGraphic {
  background-color:#333333;
  background-image:url(http://www.americasarmy.com/images/background/top_border.jpg);
  height:28px;
  margin:0px;
  text-align:center;
  width:100%;
  padding: 0px;
}
.topGraphic IMG {
 margin-top: 8px;
}

.mainLinks {
 background-image:url(http://www.americasarmy.com/images/background/topNavBg.png);
 background-repeat:repeat-x;
 height:55px;
 margin-bottom:2px;
 margin-top:10px;
 padding:2px;
 text-align:center;
 width:740px;
}
.mainLinksBut {
 font-family:Arial;
 font-size:16px;
 font-weight:bold;
 margin:12px 0 0 7px;
}
  .mainLinksBut A:link, .mainNavSub A:link {
  color:#FFF;
    text-decoration:none;
  }
  .mainLinksBut A:visited, .mainNavSub A:visited {
    color:#FFF;
    text-decoration:none;
  }
  .mainLinksBut A:hover, .mainNavSub A:hover {
    text-decoration:underline;
    color:#FBC064;
  }

.lnkon {
 border-top:2px solid #FBC064;
}

#footer {
 height: 140px;
 font-family:Arial,Helvetica,sans-serif;
 font-size:10px;
 width: 750px;
}

#footer .fleft {
  float: left;
}

#footer .ln {
  margin-bottom: 10px;
  clear: left;
}

.footer {
  position:relative;
  float:left;
  margin-left:15px;
  margin-right:15px;
  margin-top: 20px;
  text-align:center;
  width:400px;
}
.footer A:link{
  font-size:10px;
  color:#FFF;
  text-decoration:none;
}
.footer A:visited {
  font-size:10px;
  color:#FFF;
  text-decoration:none;
}
.footer A:hover {
  color:#F90;
  text-decoration:underline;
}





#wrapcentre {
  margin: 15px 15px 0px 15px;
}

#wrapfooter {
  text-align: center;
  clear: both;
}

#wrapper{
  /* clear: both; */
  margin: 0 auto;
  width: 960px;
  /* width: 90%; */

}

#top_logo{
  border-width: 0px 0px 0px 0px;
  margin: 0px 0px 0px 0px;
  padding: 15px 0px 0px 0px;
/**  background: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/top_header.png') repeat; **/
   background-image: url(http://www.americasarmy.com/images/background/background_strip.jpg);
   background-repeat: repeat-y;

  height: 140px;
}

#bottom_logo_ext{
  border-top: 1px solid #444444;
  border-bottom: 1px solid #555555;
}

#bottom_logo{
  border: 0px solid #181818;
/**  background: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/bg_footer.gif') repeat; **/
   background-image: url(http://www.americasarmy.com/images/background/background_strip.jpg);
   background-repeat: repeat-y;
}

.leftshadow{
  background: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/shadow_left.gif') repeat-y;
  padding: 0px;
  /*margin-left: 10px;*/
  width: 9px;
  vertical-align: top;
}

.rightshadow{
  background: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/shadow_right.gif') repeat-y;
  padding: 0px;
  /*margin-right: 10px;*/
  width: 9px;
  vertical-align: top;
}

.forum-buttons {
  color: #FFCC33;
  background: #e3e3dd url('http://forum.americasarmy.com/styles/black_pearl/theme/images/bg_buttons.gif') top left repeat-x;
  border-top: solid 1px #999;
  border-bottom: solid 1px #999;
  height: 24px;
  font-family: Verdana, Arial, Helvetica, sans-serif;
  font-size: 1.1em;
  font-weight: bold;
  text-align: center;
  vertical-align: middle;
  white-space: nowrap;
  padding: 0px;
  padding-top: 6px;
}

#wrapnav {
  width: 100%;
  margin: 0;
  background-color: #e3e3dd;
  border-width: 1px;
  border-style: solid;
  border-color: #181818;
}

#datebar {
  margin: 10px 25px 0 25px;
}

#findbar {
  width: 100%;
  margin: 0;
  padding: 0;
  border: 0;
}

.forumrules {
  color: #222222;
  background-color: #FFFFAA;
  border-width: 1px;
  border-style: solid;
  border-color: #0088FF;
  padding: 4px;
  font-weight: normal;
  font-size: 1.1em;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Arial, Helvetica, sans-serif;
}

.forumrules h3 {
  color: #DD2222;
}

#pageheader { }
#pagecontent { }
#pagefooter { }

#poll { }
#postrow { }
#postdata { }


/* === Text === */

h1 {
  color: #FFCC33;
  font-family: "Trebuchet MS", "Lucida Grande", Verdana, sans-serif;
  font-size: 1.8em;
  font-weight: bold;
  text-decoration: none;
}

h2 {
  font-family: Verdana, Arial, Helvetica, sans-serif;
  font-size: 1.5em;
  font-weight: bold;
  line-height: 120%;
  text-decoration: none;
}

h3 {
  font-family: Verdana, Arial, Helvetica, sans-serif;
  font-size: 1.3em;
  font-weight: bold;
  line-height: 120%;
}

h4 {
  font-size: 0.9em;
  font-weight: bold;
  margin: 0;
  padding-top: 3px;
}

p {
  font-size: 1.1em;
}

p.moderators {
  margin: 0;
  float: left;
  color: #FFCC33;
  font-weight: bold;
}

.rtl p.moderators {
  float: right;
}

p.linkmcp {
  margin: 0;
  float: right;
  white-space: nowrap;
}

.rtl p.linkmcp {
  float: left;
}

p.breadcrumbs {
  margin: 0;
  float: left;
  color: #FFCC33;
  font-weight: bold;
  white-space: normal;
  font-size: 1em;
}

.rtl p.breadcrumbs {
  float: right;
}

p.datetime {
  margin: 0;
  float: right;
  white-space: nowrap;
  font-size: 1em;
}

.rtl p.datetime {
  float: left;
}

p.searchbar {
  padding: 2px 0;
  white-space: nowrap;
}

p.searchbarreg {
  margin: 0;
  float: right;
  white-space: nowrap;
}

.rtl p.searchbarreg {
  float: left;
}

p.forumdesc {
  padding-bottom: 4px;
}

p.topicauthor {
  margin: 1px 0;
}

p.topicdetails {
  margin: 1px 0;
  color: #FFCC33
}

.postreported, .postreported a:visited, .postreported a:hover, .postreported a:link, .postreported a:active {
  margin: 1px 0;
  color: #CC3333;
  font-weight:bold;
}

.postapprove, .postapprove a:visited, .postapprove a:hover, .postapprove a:link, .postapprove a:active {
  color: #007700;
  font-weight:bold;
}

.postapprove img, .postreported img {
  vertical-align: bottom;
}

.postauthor {
  color: #FFCC33;
}

.postdetails {
  color: #000000;
}

.postbody {
  font-size: 1.3em;
  line-height: 1.4em;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, Arial, sans-serif;
}

.postbody li, ol, ul {
  margin: 0 0 0 1.5em;
}

.rtl .postbody li, .rtl ol, .rtl ul {
  margin: 0 1.5em 0 0;
}

.posthilit {
  background-color: #00FF00;
}

.nav {
  margin: 0;
  color: #FFCC33;
  font-weight: bold;
}

.pagination {
  padding: 4px;
  color: #FFCC33;
  font-size: 1em;
  font-weight: bold;
}

.cattitle {

}

.gen {
  margin: 1px 1px;
  font-size: 1.2em;
  color: #FFCC33;
}

.genmed {
  margin: 1px 1px;
  font-size: 1.1em;
}

.gensmall {
  margin: 1px 1px;
  font-size: 1em;
  color: #FFFFFF
}

.copyright {
  padding-left: 2px;
  padding-right: 2px;
  font-size: 1.0em;
  color: #FFCC33;
  vertical-align: middle;
}

.titles {
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, Arial, sans-serif;
  font-weight: bold;
  font-size: 1.3em;
  text-decoration: none;
}

.error {
  color: #CC3333;
}


/* === Tables === */

.tbl-title {
  color: #FFCC33;
  margin-top: 6px;
  text-align: left;
}

.tbl-h-c {
  background: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/tbl_h_c.gif') bottom left repeat-x;
  height: 30px;
  font-weight: bold;
  font-size: 1.3em;
  text-align: center;
  vertical-align: middle;
  margin: 0;
  white-space: nowrap;
  overflow: hidden;
}

.tbl-h-l {
  background: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/tbl_h_l.gif') bottom left no-repeat;
  padding-left: 27px;
}

.tbl-h-r {
  background: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/tbl_h_r.gif') bottom right no-repeat;
  padding-right: 27px;
}

.tbl-f-c {
  background: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/tbl_f_c.gif') bottom left repeat-x;
  height: 12px;
  font-size: 0.1em;
  margin: 0;
  white-space: nowrap;
  overflow: hidden;
}

.tbl-f-l {
  background: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/tbl_f_l.gif') bottom left no-repeat;
  padding-left: 10px;
}

.tbl-f-r {
  background: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/tbl_f_r.gif') bottom right no-repeat;
  padding-right: 10px;
}

th {
  color: #FFCC33;
  background-color: #e3e3dd;
  background-image: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/bg_th.gif');
  font-size: 1.1em;
  font-weight: bold;
  white-space: nowrap;
  padding: 7px 5px;
}

th.f_title {
  text-align: left;
  padding-left: 20px;
}

td {
  padding: 2px;
}

td.profile {
  padding: 4px;
}

td.np {
  padding: 0px;
}

td.np-body {
  background-color: #000000;
  padding: 0px;
}

.tablebg {
 
  background-color: #737373;
  border: solid 1px #555555;
}

.tablebg th{
  color: #FFCC33;
  background: #737373 url('http://forum.americasarmy.com/styles/black_pearl/theme/images/bg_th.gif') top left repeat-x;
  font-size: 1.1em;
  font-weight: bold;
  height: 26px;
  padding: 0 5px;
  border: solid 1px #181818;
  border-right-color: #444444;
  border-bottom-color: #444444;
  white-space: nowrap;
}

.cat {
  color: #0088FF;
  background-color: #000000;
  background-image: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/bg_th.gif');
  border: solid 1px #181818;
  border-right-color: #444444;
  border-bottom-color: #444444;
  height: 26px;
  margin: 0;
  padding: 0;
  text-indent: 4px;
}

.cat h4 a, .cat h4 a:visited {
  color: #FFCC33;
  font-size: 1.2em;
  font-weight: bold;
  text-decoration: none;
}

.cat h4 a:hover, .cat h4 a:active {
  color: #DD2222;
  font-size: 1.2em;
  font-weight: bold;
  text-decoration: underline;
}

.cat-bottom {
  background: #000000 url('http://forum.americasarmy.com/styles/black_pearl/theme/images/bg_catbottom.gif') top left repeat-x;
  border: solid 1px #181818;
  border-right-color: #222222;
  border-bottom-color: #222222;
  height: 28px;
  min-height: 28px;
  text-align: center;
  padding: 0px;
  color: #FFCC33
}

.catdiv {
  background-color: #000000;
  background-image: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/bg_th.gif');
  border: solid 1px #181818;
  border-right-color: #444444;
  border-bottom-color: #444444;
  height: 26px;
  margin: 0;
  padding: 0;
}

.rtl .catdiv {
  background-color: #e3e3dd;
  background-image: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/bg_th.gif');
}

.row1, .row2, .row3, .row4 {
  border: solid 1px #181818;
  border-right-color: #222222;
  border-bottom-color: #222222;
  padding: 4px;
}

.row1 {
  background-color: #000000;
  color: #FFCC33
}

.row2 {
  background-color: #000000;
}

.row3 {
  background-color: #232323;
}

.row4 {
  background-color: #323232;
}

.row-post-top {
  background-color: #737373;
  border: solid 1px #181818;
  border-right-color: #222222;
  border-bottom-color: #222222;
  vertical-align: top;
  color: #FFCC33;
}

.row-post-body {
  background: #a9aaa7 top left repeat-x;
  border: solid 1px #181818;
  border-right-color: #222222;
  border-bottom-color: #222222;
  vertical-align: top;
}

.row-post-bottom {
  color: #a9aaa7;
  background: #FFFFFF;
  border: solid 1px #181818;
  border-right-color: #222222;
  border-bottom-color: #222222;
  vertical-align: middle;
  font-size: 1.0em;
  line-height: 1.2em;
  white-space: nowrap;
  padding: 1px;
}

.spacer {
  background-color: #FF8800;
  background-image: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/bg_spacerow.gif');
  border: solid 1px #181818;
  border-right-color: #222222;
  border-bottom-color: #222222;
  height: 4px;
  padding: 0;
}

hr {
  color: #232323;
  background-color: #232323;
  border-width: 0;
  height: 1px;
}

.legend {
  text-align: center;
  margin: 0 auto;
  border: solid 1px #181818;
  background-color: #e3e3dd;
}


/* === Links === */

a:link {
  color: #FFCC33;
  text-decoration: none;
}

a:active,
a:visited {
  color: #FFCC33;
  text-decoration: none;
}

a:hover {
  color: #DD2222;
  text-decoration: underline;
}

a.forumlink, a.forumlink:visited {
  color: #FFCC33;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, Arial, sans-serif;
  font-size: 1.2em;
  font-weight: bold;
  text-decoration: none;
}

a.forumlink:hover, a.forumlink:active {
  color: #DD2222;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, Arial, sans-serif;
  font-size: 1.2em;
  font-weight: bold;
  text-decoration: underline;
}

a.topictitle {
  margin: 1px 0;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, Arial, sans-serif;
  font-weight: bold;
  font-size: 1.2em;
}

a.topictitle:visited {
  color: #FFCC33;
  text-decoration: none;
}

th a,
th a:visited {
  color: #0088FF !important;
  text-decoration: none;
}

th a:hover {
  text-decoration: underline;
}

a.subforum.read {
  background-image: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/subforum_read.gif');
  background-repeat: no-repeat;
  background-position: 3px;
  padding-left: 12px;
}

a.subforum.unread {
  background-image: url('http://forum.americasarmy.com/styles/black_pearl/theme/images/subforum_unread.gif');
  background-repeat: no-repeat;
  background-position: 3px;
  padding-left: 12px;
}

.forum-buttons a, .forum-buttons a:visited {
  color: #FFCC33;
  font-weight: bold;
  text-decoration: none;
  vertical-align: top;
}

.forum-buttons a:hover, .forum-buttons a:active {
  color: #DD2222;
  font-weight: bold;
  text-decoration: none;
  vertical-align: top;
}


/* === Form Elements === */

form {
  margin: 0;
  padding: 0;
  border: 0;
}

input {
  color: #000000;
  background-color: #e3e3dd;
  border: 1px solid #CCCCCC;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, sans-serif;
  font-size: 1.1em;
  font-weight: normal;
  padding: 1px;
}

textarea {
  color: #000000;
  background-color: #e3e3dd;
  border: 1px solid #CCCCCC;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, Arial, sans-serif;
  font-size: 1.3em;
  line-height: 1.4em;
  font-weight: normal;
  padding: 2px;
}

select {
  color: #FFCC33;
  background-color: #000000;
  border: 1px solid #CCCCCC;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, sans-serif;
  font-size: 1.1em;
  font-weight: normal;
  padding: 1px;
}

input:hover, textarea:hover, select:hover {
  color: #DD2222;
  background-color: #232323;
  border: solid 1px #FFCC33;
}

input:focus, textarea:focus, select:focus {
  color: #000000;
  background-color: #e3e3dd;
  border: solid 1px #DD2222;
}

option {
  padding: 0 1em 0 0;
}

option.disabled-option {
  color: #888888;
}

.rtl option {
  padding: 0 0 0 1em;
}

input.radio {
  background-color: transparent;
  border: none;
}

.post {
  background-color: #e3e3dd;
  border-style: solid;
  border-width: 1px;
}

.btnbbcode {
  color: #000000;
  background-color: #e3e3dd;
  border: 1px solid #CCCCCC;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, sans-serif;
  font-size: 1.1em;
  font-weight: normal;
  padding: 1px;
}

.btnmain {
  color: #FFCC33;
  background-color: #000000;
  border: 1px solid #CCCCCC;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, sans-serif;
  font-size: 1.1em;
  font-weight: bold;
  padding: 1px;
  cursor: pointer;
}

.btnlite {
  color: #FFCC33;
  background-color: #000000;
  border: 1px solid #CCCCCC;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, sans-serif;
  font-size: 1.1em;
  font-weight: normal;
  padding: 1px;
  cursor: pointer;
}

.btnfile {
  color: #FFCC33;
  background-color: #e3e3dd;
  border: 1px solid #CCCCCC;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, sans-serif;
  font-size: 1.1em;
  font-weight: normal;
  padding: 1px;
}

btnmain:hover, btnlite:hover, btnfile:hover, btnbbcode:hover {
  color: #DD2222;
  background-color: #232323;
  border: solid 1px #FFCC33;
}

btnmain:focus, btnlite:focus, btnfile:focus, btnbbcode:hover {
  color: #FFCC33;
  background-color: #000000;
  border: solid 1px #DD2222;
}


.helpline {
  color: #000000;
  background-color: #e3e3dd;
  border: 1px solid #555555;
}


/* === PM === */

.popuppm-new a {
  color: #FFCC33;
  background: #a9aaa7 url('http://forum.americasarmy.com/styles/black_pearl/theme/images/bg_post.gif') top left repeat-x;
  border: solid 1px #444444;
  font-size: 1.2em;
  font-weight: bold;
  text-decoration: none;
  margin: 5px 0;
  padding: 40px 60px;
}

.popuppm-new a:hover {
  color: #DD2222;
  border: solid 1px #DD2222;
  text-decoration: underline;
}

a.pm-new-link, a.pm-new-link:visited {
  color: #0088FF;
  text-decoration: underline;
}

a.pm-new-link:hover, a.pm-new-link:active {
  color: #DD2222;
  text-decoration: underline;
}

.pm-bg-color {
  background-color: #666666;
}


/* === BBCode === */

.quotetitle {
  color: #FFDD99;
  background: #FF9933 url('http://forum.americasarmy.com/styles/black_pearl/theme/images/bbc_quote.gif') no-repeat right;
  border: 1px solid #555555;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, Arial, sans-serif;
  font-size: 0.9em;
  font-weight: bold;
  text-align: left;
  padding: 5px;
  width: 90%;
  margin-top: 10px;
}

.attachtitle {
  color: #446688;
  background: #AACCFF url('http://forum.americasarmy.com/styles/black_pearl/theme/images/bbc_zip.gif') no-repeat right;
  border: 1px solid #555555;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, Arial, sans-serif;
  font-size: 0.9em;
  font-weight: bold;
  text-align: left;
  padding: 5px;
  width: 90%;
  margin-top: 10px;
}

.quotetitle .quotetitle {
  font-size: 1.0em;
}

.quotecontent, .attachcontent {
  background: #9d9d9d;
  border: 1px solid #555555;
  border-top: 0;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, Arial, sans-serif;
  font-size: 1em;
  text-align: left;
  padding: 5px;
  width: 90%;
  overflow: auto;
  direction: ltr;
}

.attachcontent {
  font-size: 0.85em;
}

.codetitle {
  color: #FFDD99;
  background: #119933 url('http://forum.americasarmy.com/styles/black_pearl/theme/images/bbc_code.gif') no-repeat right;
  border: 1px solid #555555;
  font-family: Verdana, "Trebuchet MS", "Lucida Grande", Helvetica, Arial, sans-serif;
  font-size: 0.9em;
  font-weight: bold;
  text-align: left;
  margin-top: 10px;
  padding: 5px;
  width: 90%;
}

.codecontent {
  color: #000000;
  background: #9d9d9d;
  border: 1px solid #555555;
  border-top: 0;
  font-family: 'Courier New', Monaco, monospace;
  font-size: 0.85em;
  font-weight: normal;
  text-align: left;
  padding: 5px;
  width: 90%;
  height: 100px;
  overflow: auto;
  direction: ltr;
}

.syntaxbg {
  color: #FFCC33;
}

.syntaxcomment {
  color: #FF8800;
}

.syntaxdefault {
  color: #FFCC33;
}

.syntaxhtml {
  color: #FFCC33;
}

.syntaxkeyword {
  color: #007700;
}

.syntaxstring {
  color: #DD2222;
}


/* === Navigation === */

.bc-tbl {
  border: solid 1px #555555;
  background-color: #e3e3dd;
  margin: 5px 0;
}

.bc-header {
  color: #FFCC33;
  background: #000000 url('http://forum.americasarmy.com/styles/black_pearl/theme/images/bg_nav1.gif');
  font-size: 1.3em;
  font-weight: bold;
  text-align: left;
  padding: 4px 5px;
}

.bc-links {
  color: #FFCC22;
  background: #000000 top left repeat-x;
  font-size: 1.0em;
  line-height: 1.4em;
  text-align: right;
  padding: 3px 5px;
}

.bc-links-left {
  position: relative;
  float: left;
  text-align: left;
}

.bc-header, .bc-links {
  border: solid 1px #181818;
  border-right-color: #222222;
  border-bottom-color: #222222;
}

.bc-div a, .bc-div a:visited {
  color: #FFCC33;
  text-decoration: none;
}

.bc-div a:hover, .bc-div a:active {
  color: #DD2222;
  text-decoration: none;
}

.bc-links a, .bc-links a:visited, .bc-links a:hover{ text-decoration: underline; }

.bc-div a.nav-current, .bc-div a.nav-current:visited {
  color: #0088FF;
}

.bc-div a.nav-current:hover, .bc-div a.nav-current:active {
  color: #DD2222;
  text-decoration: none;
}


/* === Private messages === */

.pm_marked_colour {
  background-color: #333333;
}

.pm_replied_colour {
  background-color: #DDEEFF;
}

.pm_friend_colour {
  background-color: #007700;
}

.pm_foe_colour {
  background-color: #DD2222;
}


/* === Misc === */

img {
  border: none;
}

.sep {
  color: #FFCC33;
  background-color: #0088FF;
}

table.colortable td {
  padding: 0;
}

pre {
  font-size: 1.1em;
  font-family: 'Courier New', Monaco, monospace;
}

.nowrap {
  white-space: nowrap;
}

.username-coloured {
  font-weight: bold;
}
}
