/*

     Toned-down Promoted Posts, for Gaia Online
     by Maerua

     Last Update: 8/14/14

*/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("gaiaonline.com"){

    .forum-list .sparkle {
        border-top: none;
        border-bottom: none;
        background: #DBEAF5 !important;
    }
    .forum-list .row_sparkle, .row_sparkle_pg {
        border-top: none;
        border-bottom: none;
        background: none !important;
    }
    .forum-list .gold {
        border-top: none;
        border-bottom: none;
        background: #FAEBA7 !important;
    }
    .forum-list .row_gold, .row_gold_pg {
        border-top: none;
        border-bottom: none;
        background: none !important;
    }
    .forum-list .alert {
        border-top: none;
        border-bottom: none;
        background: #FFD8D6 !important;
    }
    .forum-list .row_alert, .row_alert_pg {
        border-top: none;
        border-bottom: none;
        background: none !important;
    }
    .topic-icon.icon_alert {
        margin-left: 10px !important;
        margin-right: 10px !important;
    }
}
