css:
/**
 * Dark theme for Twirpx.com (beta).
 * @version 0.2.2
 */
@namespace url(http://www.w3.org/1999/xhtml);
/* main style */
@-moz-document domain("twirpx.com") {
    body {
        background-color: #202020 !important;
        color: #999 !important;
    }
    a:link, a:visited {
        color: #0069BC !important;
    }
    a:active, a:hover, a:focus {
        color: #FF8038 !important;
        outline: none !important;
    }
    ::-moz-selection {
        background-color: #0069BC;
        color: #fff;
    }
    /* selection for chrome/safari
    ::-webkit-selection {
        background-color: #0069BC;
        color: #fff;
    }*/
    /* лого */
    .header {
        background-image: url("data:image/png;base64,") !important;
    }
    /* логин */
    .loginbox .flogin,
    .ulogin {
        background-color: #111 !important;
    }
    /* users */
    .usertag a.rating_guest,
    .usertag a.rating_0,
    .usertag a.rating_1,
    .usertag a.rating_2,
    .usertag a.rating_3,
    .usertag a.rating_4,
    .usertag a.rating_5,
    .usertag a.rating_6,
    .usertag a.rating_trusted,
    .usertag a.rating_moderator,
    .usertag a.rating_admin { 
        background-image: url("data:image/png;base64,") !important;
    }
    
    /* заголовки разделов */
    h1 {
        color: #ddd !important;
        font-family: "Open Sans Light", "DejaVu Sans Light", Tahoma, Arial, sans-serif;
    }
    /* кнопки +/- */
    h1 a.t_minus, h1 a.t_plus {
        background-image: url("data:image/png;base64,") !important;
    }
    .content .path h1 em {
        display: inline-block;
        padding: 3px 6px;
        background-color: green;
        color: #eee !important;
    }
    /* rss */
    img[src="/images/rss.gif"] {
        width: 0;
        height: 0;
        padding-left: 20px;
        padding-top: 20px;
    }
    .content .path h1 .rss img {
        display: none;
    }
    .content .path h1 a.rss {
        display: inline-block;
        width: 20px;
        height: 20px;
    }
    .content .path h1 a.rss,
    .content .path h1 a img[src="/images/rss.gif"] {
        margin: 0 0 0 10px !important;
        position: relative;
        top: 4px;
        content: "";
        background-image: url("data:image/png;base64,") !important;
    }
    /* share */
    .content .share .google a,
    .content .share .yandex a {
        background-color: #ddd !important;
        padding: 0 4px 0 4px;
        border-radius: 3px;
        border: 1px solid #ccc;
    }
    /* формы */
    .header .search .button,
    .header .search-category .button,
    .content .psearch .button {
        background-image: url("data:image/png;base64,") !important;
        
    }
    .header .search .query,
    .content .psearch .query {
        margin-top: 6px !important;
    }
    .content .comments .rform .form,
    div.form div.bg {
        background-color: #111 !important;
    }
    div.form div.bgnw {
        background-color: #111 !important;
    }
    /* Редактор
       TODO: иконки редактора! 
       */
    div.form table.form {
        width: auto !important;
    }
    .form .sublist {
        background-color: #222 !important;
        border: 1px solid #333 !important;
    }
    .form .sublist .tree {
        border-bottom: 1px solid #333 !important;
    }
    .form .sublist .tree:last-of-type {
        border-bottom: none !important;
    }
    .droplist {
        background-color: #222 !important;
        border: 1px solid #333 !important;
    }
    div.form .value0 .textarea {
        border: none !important;
        resize: vertical;
        font-family: Consolas, "Lucida Console", monospace !important;
        background-color: #333 !important;
        padding: 0 0 !important;
        line-height: 16px !important;
        min-height: 64px;
    }
    .markItUpContainer {
        padding-top: 6px !important;
        border: none !important;
        background-color: #ccc;
    }
    .markItUpHeader ul .markItUpSeparator {
        background-color: #aaa !important;
    }
    textarea.markItUpEditor {
    }
    /* комментарии */
    .content .comments .trusted .text {
        background-color: #333 !important;
        color: inherit !important;
    }
    
    .content .comments .admin .text {
        background-color: #633 !important;
        color: inherit !important;
    }
    .content .comments .start .text {
        background-color: #363 !important;
        color: inherit !important;
    }
    .content .comments .reply .text {
        background-color: #336 !important;
        color: inherit !important;
    }
    .content .comments .cline {
        background: none !important;
        position: relative;
    }
    .content .comments .cline:before {
        position: absolute;
        display: block;
        width: 0;
        height: 0;
        background-color: transparent;
        left: 20px;
        top: 0;
        content: "";
        
        border-left: 8px solid transparent;
        border-right: 8px solid transparent;
        border-bottom: none;
    }
    .content .comments .trusted .cline:before {
        border-top: 8px solid #333;
    }
    .content .comments .admin .cline:before {
        border-top: 8px solid #633;
    }
    .content .comments .start .cline:before {
        border-top: 8px solid #363;
    }
    .content .comments .reply .cline:before {
        border-top: 8px solid #336;
    }
    /* см. также */
    .content .page .see-also {
        color: inherit !important;
    }
    .content .page .follow {
        background: #111 !important;
    }
    /* лычки */
    .content .page .rewards  {
        padding: 15px 0px 30px 30px !important;
    }
    .content .page .rewards .reward {
        position: relative;
        display: block;
        text-align: center;
        line-height: 60px;
        height: 60px !important;
        padding-top: 18px !important;
        margin-right: 4px !important;
        background-image: none !important;
        background-repeat: no-repeat;
    }
    .content .page .rewards .reward ins,
    .content .page .rewards .reward span {
        font-size: inherit !important;
        padding: 0 0 !important;
    }
    .content .page .rewards .reward:before {
        position: absolute;
        display: block;
        left: 0;
        right: 0;
        top: 0;
        padding: 2px 0;
        color: #fff;
        font-size: 12px;
        line-height: 18px;
        background-color: rgba(0,0,0, .5);
        border-bottom: 1px solid #eee;
    }
    .content .page .rewards .reward:after {
        position: absolute;
        display: block;
        left: 0;
        right: 0;
        top: 100%;
        height: 3px;
        content: "";
        
        border-left: 45px solid transparent;
        border-right: 45px solid transparent;
        border-bottom: none;
    }
    
    .content .page .rewards .reward.rating {
        background-color: #004277 !important;
    }
    .content .page .rewards .reward.rating:before {
        content: "РЕЙТИНГ";
    }
    .content .page .rewards .reward.rating:after {
        border-top: 22px solid #004277;
    }
    .content .page .rewards .reward.fixes {
        background-color: #5e5e5e !important;
    }
    .content .page .rewards .reward.fixes:before {
        content: "ПРАВКИ";
    }
    .content .page .rewards .reward.fixes:after {
        border-top: 22px solid #5e5e5e;
    }
    .content .page .rewards .reward.intop {
        background-color: #015F00 !important;
    }
    .content .page .rewards .reward.intop:before {
        content: "В ТОП-1000";
    }
    .content .page .rewards .reward.intop:after {
        border-top: 22px solid #015F00;
    }
    .content .page .rewards .reward.trusted {
        background-color: #E56300 !important;
        background-image: url("data:image/png;base64,") !important;
        background-position: 0 36px !important;
    }
    .content .page .rewards .reward.trusted:before {
        content: "ДОВЕРЕННЫЙ";
    }
    .content .page .rewards .reward.trusted:after {
        border-top: 22px solid #E56300;
    }
    /* .rating_1..6 */
    .content .page .rewards .reward.rating_0,
    .content .page .rewards .reward.rating_1,
    .content .page .rewards .reward.rating_2,
    .content .page .rewards .reward.rating_3,
    .content .page .rewards .reward.rating_4,
    .content .page .rewards .reward.rating_5,
    .content .page .rewards .reward.rating_6 {
        background-image: url("data:image/png;base64,") !important;
        background-repeat: no-repeat;
    }
    .content .page .rewards .reward.rating_0 {
        background-color: #007b2a !important;
        background-position: 0 36px !important;
    }
    .content .page .rewards .reward.rating_0:before {
        content: "НОВИЧОК";
    }
    .content .page .rewards .reward.rating_0:after {
        border-top: 22px solid #007b2a;
    }
    .content .page .rewards .reward.rating_1 {
        background-color: #00678a !important;
        background-position: -90px 36px !important;
    }
    .content .page .rewards .reward.rating_1:before {
        content: "БЫВАЛЫЙ";
    }
    .content .page .rewards .reward.rating_1:after {
        border-top: 22px solid #00678a;
    }
    .content .page .rewards .reward.rating_2 {
        background-color: #972e4b !important;
        background-position: -180px 36px !important;
    }
    .content .page .rewards .reward.rating_2:before {
        content: "ЗАВСЕГДАТАЙ";
    }
    .content .page .rewards .reward.rating_2:after {
        border-top: 22px solid #972e4b;
    }
    .content .page .rewards .reward.rating_3 {
        background-color: #8b6100 !important;
        background-position: -270px 36px !important;
    }
    .content .page .rewards .reward.rating_3:before {
        content: "ЗНАТОК";
    }
    .content .page .rewards .reward.rating_3:after {
        border-top: 22px solid #8b6100;
    }
    .content .page .rewards .reward.rating_4 {
        background-color: #b9a300 !important;
        background-position: -360px 36px !important;
    }
    .content .page .rewards .reward.rating_4:before {
        content: "ВЕТЕРАН";
    }
    .content .page .rewards .reward.rating_4:after {
        border-top: 22px solid #b9a300;
    }
    .content .page .rewards .reward.rating_5 {
        background-color: #89009a !important;
        background-position: -450px 36px !important;
    }
    .content .page .rewards .reward.rating_5:before {
        content: "СТАРЕЙШИНА";
    }
    .content .page .rewards .reward.rating_5:after {
        border-top: 22px solid #89009a;
    }
    .content .page .rewards .reward.rating_6 {
        background-color: #003d5f !important;
        background-position: -540px 36px !important;
    }
    .content .page .rewards .reward.rating_6:before {
        content: "МАСТЕР";
    }
    .content .page .rewards .reward.rating_6:after {
        border-top: 22px solid #003d5f;
    }
    /* топы */
    .content .page .topusers table th {
        padding: 10px 10px !important;
        background-color: #333 !important;
    }
    .content .page .topusers table .i1 {
        background-color: #333 !important;
    }
    /* заголовки на странице профиля */
    .content .prstat .head {
        height: 30px !important;
        background-color: #00589E;
    }
    .content .prstat .head .bg {
        background: none !important;
        position: relative;
        height: 30px !important;
    }
    .content .prstat .head .bg:after {
        position: absolute;
        display: block;
        width: 0;
        height: 0;
        background-color: transparent;
        left: 100%;
        top: 0;
        content: "";
        
        border-top: 15px solid transparent;
        border-bottom: 15px solid transparent;
        border-left: 15px solid #00589E;
        border-right: none;
    }
    .content .prstat .head .bg h2 {
        padding: 5px 5px 0px 10px !important;
    }
    /* ссылка на загрузку */
    .download .price .link2 .bg {
        height: 24px !important;
    }
    .download .price .link2 .bg_b .arr,
    .download .price .link2 .bg_r .arr {
        background-image: none !important;
        position: relative;
        height: 24px !important;
    }
    .download .price .link2 .bg_b .arr:after, 
    .download .price .link2 .bg_r .arr:after {
        position: absolute;
        display: block;
        width: 0;
        height: 0;
        background-color: transparent;
        left: 100%;
        top: 0;
        content: "";
        
        border-top: 12px solid transparent;
        border-bottom: 12px solid transparent;
        border-right: none;
    }
    .download .price .link2 .bg_b .arr:after {
        border-left: 14px solid #00589E;
    }
    .download .price .link2 .bg_r .arr:after {
        border-left: 14px solid #9E0006;
    }
    .download .price .link2 .bg .arr a {
        color: #fff !important;
    }
    /* действия */
    .content .actions {
        background-color: #111 !important;
    }
    /* список файлов: категория */
    .content .list table.categories td div.ctype_none a {
        background-image: url("data:image/png;base64,") !important;
    }
    /* список файлов: группа */
    .content .list .group {
        border-bottom: 1px solid #444 !important;
    }
    /* список файлов: недавние */
    .content .recent .h_date {
        border-bottom: 1px solid #444 !important;
    }
    /* список файлов: заголовок */
    .content .list .file h2 a {
        color: #0064B4 !important;
    }
    /* список файлов: удалить */
    a.delete {
        /*color: #FF8038 !important;*/
    }
    /* список файлов, поиск: файл */
    .content .list .file, 
    .content .search-results .item  {
        border-left: 2px solid transparent;
        transition: background-color 0.5s;
    }
    .content .list .file:hover, 
    .content .search-results .item:hover {
        border-left: 2px solid #444;
        background: #111 !important;
        transition: background-color 0.5s;
    }
    .content .list .featured_True {
        background: none !important;
        border-left: 2px solid #933;
    }
    .content .list .featured_True:hover {
        border-left: 2px solid #b33;
        background-color: #422 !important;
    }
    .content .list .marked_True {
        border-left: 2px solid #393;
        background-color: #232 !important;
    }
    .content .list .marked_True:hover {
        background-color: #363 !important;
    }
    .content .list .file h2 em {
        background-color: green !important;
        color: #eee !important;
        padding: 2px 4px;
        display: inline-block;
    }
    /* утверждённый файл: информация */
    .content .list .fixstatus_Approved .info {
        color: #363 !important;
        border-left: 3px solid #363;
    }
    /* утверждённый файл: комментарий */
    .content .list .fixstatus_Approved .comment {
        background-color: #363 !important;
        color: #eee !important;
        border: none !important;
    }
    /* утверждённый файл: изменения */
    .content .list .fix .diff {
        color: inherit !important;
    }
    .content .list .fix .diff ins {
        background-color: #363 !important;
        color: #eee !important;
    }
    .content .list .fix .diff del {
        background-color: #633 !important;
        color: #eee !important;
    }
    /* обсуждения */
    .content .list .topic {
        transition: background-color 0.5s;
    }
    .content .list .topic:hover {
        background-image: none !important;
        background-color: #111 !important;
        transition: background-color 0.5s;
    }
    .content .list .topic h2 a {
        color: #0064B4 !important;
    }
    .bb_code blockquote {
        background-color: #333 !important;
        border: 1px solid #444 !important;
    }
    /* пагинация */
    .content .pager a {
        background-color: #333 !important;
        border: 1px solid #444 !important;
        color: #999 !important;
    }
    .content .pager a.disabled {
        color: #444 !important;
    }
    .content .pager a:hover {
        background-color: #111 !important;
    }
}

/* разделы */
@-moz-document url-prefix("http://www.twirpx.com/files/") {
    .header .menu li a[href="/files/"] {
        color: #fff !important;
    }
}
@-moz-document url-prefix("http://www.twirpx.com/library/"),
    url("http://www.twirpx.com/recent/files/?category_id=4") {
    .header .menu li a[href="/library/"] {
        color: #fff !important;
    }
}
/** 
 * страница просмотра файла:
 * - исправлено перетекание длинных текстов под обложку
 */
@-moz-document url-prefix("http://www.twirpx.com/file/") {
    .download .text.bb_code {
        padding-left: 260px !important;
        overflow: visible;
    }
    .download .text .pvi {
        display: block;
        float: left;
        width: 240px;
        margin: 0 0 15px -260px !important;
    }
    .download .text .pvi img {
        display: block;
        max-width: 240px !important;
        height: auto;
        margin: 0 auto;
        box-shadow: 0 0 12px #000;
    }
}
@-moz-document url-prefix("http://www.twirpx.com/file/"),
    url("http://www.twirpx.com/recent/files/?category_id=3") {
    .header .menu li a[href="/files/"] {
        color: #fff !important;
    }
}
@-moz-document url-prefix("http://www.twirpx.com/looks/") {
    .header .menu li a[href="/looks/"] {
        color: #fff !important;
    }
}
@-moz-document url-prefix("http://www.twirpx.com/discussions/"),
    url-prefix("http://www.twirpx.com/topic/"),
    url-prefix("http://www.twirpx.com/recent/topics/"){
    .header .menu li a[href="/discussions/"] {
        color: #fff !important;
    }
}
@-moz-document regexp("^http://www.twirpx\\.com/about/$") {
    .header .menu li.category_8 a {
        color: #fff !important;
    }
}
@-moz-document url-prefix("http://www.twirpx.com/about/faq/") {
    .header .menu li a[href="/about/"] {
        color: #0069BC !important;
        text-decoration: underline !important;
    }
    .header .top a[href="/about/faq/"] {
        color: #fff !important;
    }
}
@-moz-document url-prefix("http://www.twirpx.com/top/") {
    .header .top a[href="/top/"] {
        color: #fff !important;
    }
}
