css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hkgalden.com") {

/*flat*/
*{
	border-radius: 0 !important;
	text-shadow: none !important;
	box-shadow: none !important;
}

/*remove*/
#info, .tp-banner, #pagelm br, .label u:before, .label u:after, #mtplist td.tpg .pg:hover span .pgt:before, #mtplist td.tpg .pg:hover span .pgt, #mtplist td.tpg .pg .pnum:before,.naseotn:before,.uxbk:hover small,.gob.il:after,.gob.ir:after {
    display: none !important;
}

/*bg*/
body, #main, #pagelm, #ratrap, .fryxb > span:only-child, .curpgind, #alertor, td.tnm[colspan="4"], td.taf[colspan="3"] {
    background: none repeat scroll 0 0 #000000 !important;
}
#mtplist tr.lt td, #mtplist tr.dt td, table.gob, table.hkga-pagin, .cke_bottom, .cke_source, .cke_reset, table.hkga-stdpf td, table.hkga-stdpf th, #mtplist td.tpg .pg:hover span,.prevcon,.ihodr, .hkga-pagin, .gob.topic, .gob {
    background: none repeat scroll 0 0 #222222 !important;
}
#mtplist th, .laime, table.sectab tr td b.hig, .naseobtn-wrap {
    background: none repeat scroll 0 0 #333333 !important;
}
table.sectab tr td button, .label u.lv1, .hkga-pagin td.opt select, .cke_inner, .cke_top, table.hkga-stdpf, #mtplist td.tpg .pg:hover span a:hover {
    background: none repeat scroll 0 0 #444444 !important;
}
#mtplist td.tic:before {
    background: none repeat scroll 0 0 #555 !important;
}
#EmotionTray, #mtplist td.tpg .pg:hover span a,#EmotionTray a img {
    background: none repeat scroll 0 0 transparent !important;
}
#mtplist td.tic.ht:before {
    background: none repeat scroll 0 0 #AAAAAA !important;
}
body.cke_ltr {
    background: none repeat scroll 0 0 #FFF !important;
}

/*color*/
a, body, .cke_source, #galdenbar, #pagelm, .label u.lv1, #mtplist th, .prevcon a ,.prevcon{
    color: #DDD !important;
}
#galdenbar a, a.newt-btn, input.lighearox.smt, .naseotn, .uxbk {
    color: #FFFFFF !important;
}
table.gob.topic, table.sectab tr td button, table.sectab tr td b.hig, blockquote, #mtplist td.tic, #mtplist td span.lrt-t, .hkga-pagin td.opt select, #mtplist td.qrw, #mtplist td.tpg .pg, #mtplist td.tpg .pg > a  {
    color: #BBB !important;
}
table.sectab td span.tiny, .curpgind, #alertor,.blkusr  {
    color: #999 !important;
}
b.bro, b.bro a {
    color: #0093EE !important;
}
b.sis, b.sis a {
    color: #FF4476 !important;
}
.cke_panel_list a, .cke_panel_block a{
    color: #000000 !important;
}
#mtplist td.tnm a:visited, td.rbk a:visited, #mtplist td.tpg a:visited {
    color: #C675EC !important;
}

/*border-color*/
#ratrap {
    border-color: #666 !important;
}
.naseobtn-wrap, .cke_bottom, .cke_top, .cke_chrome, .laime,#mtplist td.tpg .pg:hover span,#EmotionTray.fxb,table.gob, .curpgind, #alertor  {
    border-color: #333 !important;
}
#mtplist td, table.sectab tr td button, table.sectab tr td b.hig, table.hkga-pagin, .hkga-pagin td.opt select {
    border-color: #000000 !important;
}


/*font-size*/
#mtplist td.tlt, th.tlt, #mtplist td.qrw {
    font-size: 0.8em !important;
}


/*no border*/
#main,.cke_bottom, .cke_top, table.hkga-stdpf,#mtplist td.tpg .pg:hover span a,#EmotionTray.fxa,#EmotionTray a img  {
    border: medium none !important;
}

/*misc*/
#main {
    margin: 0 !important;
    width: 100% !important;
	padding: 42px 0 !important;
}
td.lbk {
    border-right-color: #333333 !important;
}
.mnuhodr {
    width: auto !important;
}
.laime {
    text-shadow: 1px 1px 1px #000000 !important;
}
#mtplist td.tpg .pg:hover span {
    height: auto !important;
    margin: -5px 0 0 -9px !important;
    width: 256px !important;
}
#mtplist td.tpg .pg:hover span a {
    display: block !important;
    height: 32px !important;
    line-height: 32px !important;
    margin: 0 !important;
    padding: 0 !important;
    text-align: center !important;
    width: 12.5% !important;
}
#mtplist td.tpg .pg:before {
    border-bottom-color: #555555 !important;
    border-right-color: #555555 !important;
}
#mtplist td.tpg .pg .pnum {
    margin: 0 !important;
}
#mtplist td.tpg {
    width: 5% !important;
}
#mtplist td.tic:before {
    border-radius: 99px;
    box-shadow: 0 0 2px #222222 inset;
    content: "";
    display: inline-block;
    height: 5px;
    width: 5px;
	vertical-align:middle;
}
.naseotn {
    cursor: pointer;
}
#galdenbar {
	position: fixed !important;
	line-height: 36px !important;
	height: 36px !important;
	z-index: 3 !important;
	padding: 0 !important;
	overflow: hidden !important;
	display: block !important;
}
#galdenbar a{
	line-height: 36px !important;
	height: 36px !important;
	display: inline-block !important;
	padding: 0 5px !important;
	margin: 0 !important;
}
#galdenbar a:hover{
	background: rgba(255,255,255,0.2) !important;
}
#mtplist td.tpg .pg:hover span {
	z-index: 2 !important;
}
.uxbk{
	padding: 2% !important;
	margin: 0 !important;
	border: #000 solid 5px !important;
	border-width: 0 0.5% 1% !important;
	width: 19% !important;
	max-width: 200px !important;
	transition: opacity 0.2s linear;
}
.uxbk:hover{
	opacity: 1 !important;
}
#uxli:hover .uxbk{
	opacity: 0.7;
}
.uxbk small{
	right: 2% !important;
	bottom: 2% !important;
}
.uxbk:hover:before{
    position: absolute;
	content: "";
	display: block;
	border: 12px solid transparent;
	width:0;
	height:0;
	border-left-color: rgba(255,255,255,0.5);
	right: 2%;
	top: 50%;
	margin-top: -12px;
	overflow: hidden;
}
::selection
{
	color:#FFF;
	background:#1C83F2;
}
::-moz-selection
{
	color:#FFF;
	background:#1C83F2;
}

}
