css:
@-moz-document domain("github.com") {
.only-with-full-nav {
display: block !important;
position: absolute !important;
top: 170px !important;
left: 15% !important;
width:700px !important;

}
.repository-with-sidebar .repository-sidebar .clone-url.open{
width:100% !important;

}

.repository-with-sidebar .repository-sidebar h3 {
float:left !important;
}

.repository-meta{
position:absolute;
top:100px;
}




input.clone{
width:65% !important;
float:left !important;
}


.file-navigation
{
margin-top:120px !important;
}
.overall-summary,
.kill-the-chrome .repository-lang-stats-graph {
display:none !important;
}
.repository-with-sidebar .repository-sidebar .clone-options{
display:inline !important;
}
.repository-with-sidebar .repository-sidebar .sidebar-button{
width: 150px !important;
}



.clone-options a.js-clone-selector{
text-decoration: none  !important;
background-color:   rgb(245, 245, 245)  !important;

line-height:20px !important;
font-size:12px !important;
border-radius: 0px !important;
border-width: 2px  !important;
border-style: outset  !important;
border-color: #DDD  !important;
border-bottom-color: #C5C5C5;
color: #333 !important;
display: inline-block;
font-weight: bold;
}

.clone-options a.js-clone-selector:hover{
text-decoration:none !important;
background-color:   rgb(218, 218, 218) !important;
}


.edit-link a
{
color: #333 !important;
text-decoration: none  !important;
border-width: 1px  !important;
border-color: #DDD  !important;
background-color:   rgb(245, 245, 245)  !important;
}
.edit-link a:hover{
text-decoration:none !important;
background-color:   rgb(218, 218, 218) !important;
}
}

@-moz-document regexp(".*github\\.com/.+/.+/(issues|pulls|network|pulse|graphs|settings|wiki|search|commit)?.*") {
.only-with-full-nav{
display:none !important;
}
}
