@namespace url(http://www.w3.org/1999/xhtml);
/* This style is an enhancement from "about:blank - Arctic Firefox" posted by iconolith */

/*****************************/
/*  about:blank - ArcticFox  */
/*****************************/
/*     Enhanced by loxaxs    */
/*****************************/

@-moz-document url(about:blank)
{
    *:empty:not([style]):not([text]):not([class]):not([onresize]) {
        margin:0px !important;
        background-color: white;
        background-image: none,
        url('http://s23.postimg.org/xlvkrhufv/Arctik_Fox.png'),
        url('http://img4.hostingpics.net/pics/146081ArctikFox.png');
    	background-repeat:no-repeat;
    	background-attachment:fixed;
    	background-position:center;
        background-size: inherit;
    }
    
    @media all and (max-width: 339px), (max-height: 329px) {
        * {
            background-size: contain;
        }
    }
}
