css:
@namespace url(http://www.w3.org/1999/xhtml);
/* ad remover */
@-moz-document url("http://www.pixiv.net/member_illust.php") {
.ui-layout-west {
    display: none !important;
}
}

@-moz-document domain("pixiv.net") {

.ad,
.ad-footer,
.ads_area_no_margin,
.ads_area,
#header-banner,
#touch_introduction,
.finish_upload > center,
div.worksImageresponse,
div#premiumballoon,
div.user-ad-container,
iframe,
.badges,
.require-premium,


/* fuck */
.rounded,
.area_new,

.project_pixiv,
.official-contest,


.recruit-ad,
.comic-container,
.news
 {
    display: none !important;
}


/* right nav */
#page-mypage {
    position: relative;
}
}

@-moz-document
url("http://www.pixiv.net"),
url("http://www.pixiv.net/mypage.php") {
.ui-layout-west:before {
    display: block !important;
    position: relative !important;
    
    content: "　News, Ranking, etc...";
    width: 236px !important;
    padding: 2px !important;
    
    background-color: #EEF0F3;
    border: 1px solid #D6DEE5;
    border-radius: 5px 5px 5px 5px;
    
    margin-bottom: 10px;
}
}

@-moz-document domain("pixiv.net") {
.ui-layout-west {
    position: relative !important;
}

#column-misc:before {
    display: block !important;
    position: relative !important;
    
    content: "　News, Ranking, etc...";
    width: 236px !important;
    padding: 2px !important;
    
    background-color: #EEF0F3;
    border: 1px solid #D6DEE5;
    border-radius: 5px 5px 5px 5px;
    
    margin-bottom: 10px;
}

#column-misc {
    position: absolute !important;
    top: 0px;
    left: 0px;
    width: 242px !important;
    height: 24px !important;
    overflow: hidden !important;
    opacity: 0;
}

#column-misc * {
    overflow: hidden !important;
}

#column-misc:hover {
    width: 242px !important;
    height: auto !important;
    overflow: auto !important;
    padding: 8px !important;
    top: -8px;
    left: -8px;
    transition: All 1s ease;
    opacity: 1 !important;
    background-color: #e3e7ee !important;
    border: 1px solid #D6DEE5;
    border-radius: 5px 5px 5px 5px;
    z-index: 1;
}



/* adjust */
.header,
.layout-wrapper {
    height: 84px !important;
}

.ui-search {
    position: relative !important;
    left: -160px !important;
    top: -66px !important;
}

.category-list {
    position: relative !important;
    left: 450px !important;
    top: -37px !important;
}

.navigation-list {
    position: relative !important;
    left: 214px !important;
    top: 55px !important;
    width: 720px;
}

.notifications {
    position: relative !important;
    left: 688px !important;
    top: -7px !important;
    width: 100px;
}

.ui-layout-west,
._unit,
.group-list {
    width: 240px !important;
}

.my-profile-unit{
    width: 242px !important;
}

.stacc_left_menu_inner {
    width: 252px !important;
}

.menu-unit {
    margin-top: -10px !important;
    border-top: 0px !important;
    border-top-right-radius: 0px !important;
    border-top-left-radius: 0px !important;
}

.ui-layout-east,
.contents-east,
.contents-main,
.work-detail-unit {
    width: 720px !important;
}

.contents-main {
    margin-left: 12px !important;
}

.profile-unit {
    width: 186px !important;
}
.work-detail-unit,
.manage-unit,
.action-unit,
#illust-recommend  {
    margin-left: 14px !important;
    width: 768px !important;
}

}

@-moz-document url-prefix("http://www.pixiv.net/member.php") {
.profile-unit {
    width: 240px !important;
}

.layout-column-2 {
    margin-left: 14px !important;
}

.layout-column-2,
.layout-column-2 > div {
    width: 716px !important;
}

.worksOption {
    width: 676px !important;
}
}


@-moz-document
url("http://www.pixiv.net/content_upload.php"),
url("http://www.pixiv.net/manga_upload.php"),
url("http://www.pixiv.net/novel/upload.php"),
url("http://www.pixiv.net/bookmark.php"),
url-prefix("http://www.pixiv.net/tags.php"),
url-prefix("http://www.pixiv.net/search.php"),
url("http://www.pixiv.net/member_illust.php"),
url("http://www.pixiv.net/bookmark_add.php") {
._unit,
#illust-recommend,
.display_works {
    width: 968px !important;
    margin-left: 0px !important;
}

.layout-column-2 {
    float: none !important;
}
}

@-moz-document url-prefix("http://www.pixiv.net/member_illust.php?mode=big") {
body {
    overflow: visible !important;
}
}

@-moz-document url("http://www.pixiv.net/event.php") {
.two_column {
    width: 970px !important;
}
}

@-moz-document url-prefix("http://www.pixiv.net/group/") {
.ui-layout-west,
._unit,
.group-list {
    width: 182px !important;
}

.ui-layout-west {
    margin-right: 14px !important;
}
}

@-moz-document url-prefix("http://www.pixiv.net/stacc/") {
.stacc_east_area {
    width: 714px !important;
    margin-left: 14px !important;
}

.stacc_center_area {
    width: 674px !important;
}

.profile-unit {
    width: 240px !important;
}
}
