css:
/*
=====================================
Author: Brian Gillespie
Applies to: Discourse
  http://meta.discourse.org
  http://discourse.mcneel.com
Version: 0.0.1
=====================================
*/

/*
=====================================
Whole-site settings
=====================================
*/

body { background-color: #fff; }
article.boxed div.row {
  background-color: #fff;
}

/* Overall Width */
.container { 
    width: 550px !important;
    margin: 0px !important; 
    margin-left: 40px !important;   
  }
.topic-body {
  border-radius:0px;
  width: 565px !important;
  margin-right:0px !important;
}

.topic-meta-data .contents { 
  width: 500px !important; 
}
/* Heading */
.d-header { box-shadow: none; }

/* Heading, When scrolled to top */
.d-header .panel { position: absolute; left: 425px; }
.d-header .current-username { display:none; }

/* Heading, When scrolled down */
.d-header img.logo-small {     content:url(http://wiki.mcneel.com/_media/rhino_discourse.png);
  width:122px !important;
}
.d-header .container {
  padding-left: 0px !important;
  margin-left: 15px !important;
}
.d-header .extra-info-wrapper { 
  margin-left: 0px !important; 
  margin-bottom: 0px !important; 
  float:left; clear:left;
}

/* Admin button */
#show-topic-admin {
  left: 600px !important;
  top: 10px !important;
  right: auto !important;
}

/*
==========================================
Topic view
==========================================
*/

/* Bookmark */
.read-icon { left: 530px !important; right: auto !important}  

/* Title */
#topic-title .container { margin-left: 16px !important }

/* Borders */
div.bottom-round {
  border: 0px !important;
  border-radius: 0px;
}

.topic-post article.boxed .topic-body .contents.bottom-round {
  border: 0px !important;
  /*border-left: 1px solid #ddd !important;*/
  box-shadow: none !important;
  }

/* Arrow pointing to avatar */
.topic-body:before, .topic-body:after {
  border-style:none;
  background-color:#fff;
}

article.boxed div.row {   
  border-top: 2px solid #ddd !important;
}

.topic-meta-data { margin-top: 4px; }

/* Stack metadata, post, and gutter */
.topic-meta-data { float:left; clear: left !important; margin-left: 6px !important;}
.topic-body {float:left; clear: left !important;}
.gutter {float:left; clear: left !important; }

/* Inline the metadata */
.topic-meta-data .contents { 
  text-align:left !important; 
  vertical-align: bottom !important;
}
.topic-meta-data h3, .topic-meta-data .post-info { 
  display: inline !important; height: 20px; 
}
.topic-meta-data .avatar { 
  height: 20px; width: 20px; 
}

/* You liked this... */
.post-actions { padding-left: 0px !important; margin-left 0px !important; }

/* Topic footer buttons */
#topic-footer-buttons {
  	margin-left: 0px !important;
  }

/* Suggested Topics */
#suggested-topics {
  margin-left: 0px !important;
}

/*
==========================================
Quote Block
==========================================
*/

.quote {
  border: 0px !important;
  border-left: 3px solid #08c !important; 
  border-radius: 0px 8px 8px 0px !important;
  /*background-color:#f5f5f5;*/
  margin-left: 10px !important;
  padding-left: 0px !important;
}

.quote .title {
  background-color: transparent !important;
  padding:4px 10px !important;
  border-bottom:1px solid #eee;
}
      
.quote blockquote { 
  background-color: transparent !important;
  font-style: italic; 
  margin-left: 0px !important;
  padding-top: 0px !important;
}
  


/* Summary of topic */
/*topic-summary { margin: 5px -40px 0px -15px !important; } */

/*
==========================================
Topic List 
==========================================
*/
table#topic-list {
    width: 700px !important;
}

table#topic-list .avatar { width: 20px; height: 20px; }

table#topic-list .main-link .title { font-size: 80%; }

/* Hide 'Participants Column' */
/*
#topic-list th:nth-child(0n+6) {display: none}
td.posters { display:none;}
*/

/* Hide 'Likes' Column */
/*
#topic-list th:nth-child(0n+8) {display: none;}
#topic-list td:nth-child(0n+6) {display: none}
*/

/* Hide 'Views' Column */
#topic-list th:nth-child(0n+9) {display: none;}
#topic-list td:nth-child(0n+7) {display: none}

/* Hide 'Activity' Column */
#topic-list th:nth-child(0n+10) {display: none;}
#topic-list td:nth-child(0n+8) {display: none}
#topic-list td:nth-child(0n+9) {display: none}
