css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com') {

{color:rgba(51, 51, 51, 1.0) !important;}

body {background: url('http://oi39.tinypic.com/bfqhoh.jpg') center left #fff no-repeat fixed !important;}

#new_post .arrow {
  opacity: 1.0 !important;
  margin-left: 0px !important; 
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background-color: rgba(0, 202, 202, 0) !important;
}

#posts LI[class*=" post"] { 
  background-image: url(http://28.media.tumblr.com/tumblr_ls0w5txuft1r3vazto1_250.png) !important;
}

ol#posts li blockquote,

body.mceContentBody blockquote {
margin-left: 0 !important;
margin-right: 0 !important;
padding-left: 10px !important;
border-width: 4px !important;
border-color: #fffffff !important;
}

ol#posts li blockquote blockquote,
body.mceContentBody blockquote blockquote {
border-color: #e3e3e3 !important;
}
ol#posts li blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote {
border-color: #dedede !important;
}
ol#posts li blockquote blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote blockquote {
border-color: #d4d4d4 !important;
}
ol#posts li blockquote blockquote blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote blockquote blockquote {
border-color: #c7c7c7 !important;
}

.selection_nipple.white,
.selection_nipple {
  border: 0px !important;
background-image: !important;
  width:  36px !important;
  height: 48px !important;
  margin-top: -12px !important;
  background-position: 0px 0px !important;
  background-repeat: no-repeat !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(44,71,98, 1.0)  !important;
}

li.selected, li.recessed, a.active, div.post_filter_tabs a {
}

.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;
  border-radius: 12px !important;
}

.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}

.mceContentBody {
  color: rgba(0, 0, 0, 1.0) !important;
  background: rgba(255,255,255, 1.0) !important;
}

#auto_pagination_loader 
	{background: transparent !important;} 

}
