css:
/*ヘッダを消す */
#AreaHeaderLeft, #AreaHeaderCenter, #AreaHeaderRight{ display:none; }

/*タブバーを消す*/
ul#tabhead{ display:none; }

/* フォーム横のボタンを消す */
#fm1 a, #fm1 nobr{ display:none; }

/* サイドバー */
#AreaUpperRight{
display:none;
}

#resultsArea table:first-child,
div.partition,
span.smallredfont
{display:none;}

/*フッタ*/
#AreaFooter,
#SS_mainColumn,
#AreaLowerLeft{
display:none;
}

#resultsList li {
margin-bottom: 20px;
list-style:none;
margin-left: -10px;
}
#resultsList li div{
margin-left: 10px;
margin-top: 5px;
}

#resultsList{
clear:both;
border: 2px solid gray;
border-radius: 15px;
padding: 25px;
}

#AreaUpperLeftContainer{
margin-left: auto !important;
margin-right: auto !important;
margin-bottom: 50px;
}

#body-tab0{
background: white;
}

#body-tab0 div.inner, #body-tab0 div.inner2{
background: white;
margin: 0 0 0 0;
padding: 0 0 0 0;
padding-top: 15px;
padding-bottom: 15px;
}
#body-tab0 div.inner2{
background-color: #064595;
border-radius: 10px;

}
#fm1 input:nth-child(3){
display:none;
}
#q{
padding: 5px 10px;
border-radius: 5px;
margin-left: 20px;
}

#gnav, .bg_i, .cl_l{
display:none;
}

#resultsList > div{
display:none;
}

#paging{
float:right;
}


/* 広告 */
ins,
div[align="center"],
#AreaBody>.clearfix,
#AreaSponsorSite{
	display:none !important;
}
