css:
@-moz-document domain("community.spotify.com"), domain("accounts.spotify.com"), domain("embed.spotify.com") {
/* Dark Design for the Spotify Community Forum */


/*  Optional:
    Hides Badges and Rock Star Roles on Profile
    Shows Poll on HOME 
*/

/* */
.lia-quilt-view-profile-page  .lia-component-badges-widget-user-badges-list, .lia-quilt-view-profile-page .lia-component-author-rock-star-roles {
    display: none !important;
} 
.CommunityPage .lia-panel.lia-panel-standard.CustomContent.Chrome.lia-component-common-widget-custom-content { 
    display: none !important;
}


    /* GENERAL */

html { /* Load page & Editor */
    background: /*[[custombox]]*/ !important; 
}
body { /* For example: Home side bars */
    background: /*[[customback]]*/ !important;
}
.lia-content p { /* Normal font for posts */
    color: #ccc !important;
}
::-moz-selection { /* Firefox Text Selection */
    background: #1ED760 !important;
    color: #000 !important;
}
::selection { /* Chrome Text Selection */
    background: #1ED760 !important;
    color: #000 !important;
}
span.local-date { /* Date in some posts */
    color: #bbb !important;
}
font[color="#000000"] { 
    color: #bfbfbf !important;
}
span[style="font-size: medium;"] {
    color: #1ED760 !important;
}
span[style="font-size: 40px; color: #0f1113; weight: bold;"] { /* Mostly ideas exchange */
    color: #1ED760 !important;
}
table { /* For example: Boxes right side of home */
    background-color: /*[[customback]]*/ !important;
}
/* All kind of text fields , e.g Search bar */
#lia-body .lia-content .lia-form input[type="text"], #lia-body .lia-content .lia-form input[type="password"], #lia-body .lia-content .lia-form input[type="file"], #lia-body .lia-content .lia-form select, #lia-body .lia-content .lia-form textarea, #lia-body .lia-content .lia-form .lia-form-terms-of-service {
     border: 1px solid #555 !important;
 }
.default-menu-option { /* All kind of drop down menus' title */
    color: #ccc !important;
}
.default-menu-option:hover {
    color: #fff !important;
}
#lia-body .lia-content .dropdownHover .lia-menu-navigation .lia-menu-dropdown-items { /* Drop down box */
    border: 1px solid #555 !important;
}
.lia-menu-dropdown-items a { /* Items inside drop down list */
    color: #ccc !important;
}
.lia-menu-dropdown-items a:hover { /* All kind of drop down highlights */
    background-image: linear-gradient(to bottom, #1ED760, #008A00)!important;
    color: #fff !important;
}
.lia-menu-dropdown-items a:focus { /* Active but not hovered items in drop down */
    background: #222 !important;
    color: #fff !important;
}
#lia-body .lia-content table.lia-list-wide .message-subject .lia-message-read a{ /* Read  messages in a list (profile, recent posts) */
    color: #bfbfbf !important;
}
#lia-body .lia-content table.lia-list-wide .message-subject .lia-message-read a:hover{
    color: #fff !important;
}
a.lia-message-unread { /* Unread messages in Private messages & Home */
    color: #eee !important;
    font-weight: bold;
}
.lia-paging-page-link { /* "previous" and "next" above and below posts */
    color: #ccc !important;
}
.lia-paging-page-link:hover {
    color: #fff !important;
}
#lia-body .lia-content table.lia-list-wide td.lia-data-cell-integer { /* All kind of numbers, e.g. number of views */
    color: #aaa !important;
}

/* Alert warning, e.g "Message no longer available" when going back */
#lia-body .lia-content .lia-panel-feedback-banner-alert, #lia-body .lia-content .lia-panel-feedback-inline-alert {
    background: /*[[custombox]]*/ !important;
    color: #ccc !important;
    border: 1px solid #333 !important;
}
/* Solution banner */
#lia-body .lia-content .lia-panel-feedback-inline-safe, #lia-body .lia-content .lia-panel-feedback-banner-safe {
    background: /*[[custombox]]*/ !important;
    color: #ccc !important;
    border: 1px solid #333 !important;
    margin-top: 23px !important;
}
#lia-body .lia-content .lia-panel-feedback-banner-alert a, .lia-component-common-widget-page-feedback a { 
    color: #1ED760 !important;
}

/* All kind of text input fields or drop downs */
#lia-body .lia-content .lia-form input[type="text"], #lia-body .lia-content .lia-form input[type="password"], #lia-body .lia-content .lia-form input[type="file"], #lia-body .lia-content .lia-form select, #lia-body .lia-content .lia-form textarea {
    background: /*[[custombox]]*/ !important;
    color: #ddd !important;
}
/* All kind of banners above topics: e.g "Showing ideas with statu.." */
#lia-body .lia-content .lia-panel-feedback-banner-note {
    background-color: /*[[custombox]]*/ !important;
    border: 1px solid #333 !important;
}

    /* TABS */

li.lia-tabs-active { /* Tabs that are selected - also sub-sections that are selected */
    font-weight: bold !important;
    font-size: 1.1em !important;
    background-image: linear-gradient(to bottom,#222 0,#000 100%) !important;
}
.lia-tabs-active * { /* No green background on active tabs */
    background: transparent !important;
}
.lia-tabs-standard li.lia-tabs-inactive  { /* Tabs that are not selected */
    background-image: linear-gradient(to bottom,#333 0,#191919 100%) !important;
}
.lia-tabs-standard li.lia-tabs-inactive:hover {
    background-image: linear-gradient(to bottom,#00FF5B 0,#196E32 100%) !important;
}
#lia-body .lia-content .lia-tabs-standard-wrapper ul.lia-tabs-standard li.lia-tabs-inactive:hover a {
    color: #fff !important;
}

/* All sub "tabs" */
#lia-body .lia-content ul.lia-tabs-secondary li.lia-tabs-active, #lia-body .lia-content ul.lia-tabs-secondary li.lia-tabs-active a  {
    background: transparent !important;
    font-size: 1.1em !important;
    color: #eee !important;
    font-weight: normal !important;
}
.lia-tabs-inactive:hover, .lia-tabs-inactive:hover a:hover { /* Hovering inactive sub tabs - e.g. Settings - Login etc */
    background: transparent !important;
    color: #fff !important;
}
#lia-body .lia-content ul.lia-tabs-sort a.lia-view-filter-descending, #lia-body .lia-content ul.lia-tabs-sort a.lia-view-filter-descending { /* sub sub tab . e.g. received kudos by user name */
    background: #0a0a0a !important;
    border: 1px solid #333 !important;
    border-radius: 5px !important;
}


    /* FORUM BODY */

/* HEADER */
    
.navbar.navbar-default {
    background: /*[[customhead]]*/ !important;
    border-bottom: 1px solid #000 !important;
}
    

/* AREA UNDER HEADER - TOPIC */

.lia-quilt-row.lia-quilt-row-header { /* No community banner */
    padding: 0px !important;
    margin: 0px !important;
    border-radius: 0px !important;
    height: 0px !important;
}
.wrap { /* Left and right bezels */
    background: /*[[customback]]*/ !important;
}
.lia-quilt-column-24 { /* Area under header till topic lists */
    background: /*[[customback]]*/ !important;
}
.lia-content .PageTitle span, #lia-body H1.PageTitle.lia-component-common-widget-page-title, #lia-body H1.PageTitle.lia-component-common-widget-page-title a, #lia-body H2.PageTitle.lia-component-common-widget-page-title, #lia-body H2.PageTitle.lia-component-common-widget-page-title a { /* All kind of headlines at the top of pages */
    color: #1ED760 !important;
}  
    

/* CRUMBS */

.lia-breadcrumb-community.lia-breadcrumb-forum:hover { /*1. Crumb: Home */
    color: #fff !important;
}
#viewUserProfile{ /* Username */
    color: #ccc !important;
}
#viewUserProfile:hover{
    color: #fff !important;
}
#logoutPage { /* "Log Out" */
    color: #ccc !important;
}
#logoutPage:hover{
    color: #fff !important;
}
#myProfileSettings { /* "My Settings" */
    color: #ccc !important;
}
#myProfileSettings:hover { 
    color: #fff !important;
}
#faqPage { /* "Community Help" */
    color: #ccc !important;
}
#faqPage:hover {
    color: #fff !important;
}
.registration-link.lia-component-users-action-registration { /* "Sign up for Spotify" */
    color: #ccc !important;
}
.registration-link.lia-component-users-action-registration:hover {
    color: #fff !important;
}
.login-link.lia-component-users-action-login { /* "Log in" */
    color: #ccc !important;
}
.login-link.lia-component-users-action-login:hover{
    color: #fff !important;
}
a#privateNotes { /* Private messages "x Unread" */
    color: #1ED760 !important;
    font-size: 12px !important;
}
a#privateNotes:hover {
    color: #66FF94 !important;
}    
#indicator a {  /* "Notifactions" */
    color: #ccc !important;
}
#indicator a:hover {
    color: #fff !important;
}
.lia-header-nav-component-unread-count { /* Notifications with 0 messages background of number */
    background: /*[[customback]]*/ !important;
    color: #bbb !important;
    box-shadow: inset 0px 0px 1px #aaa !important;
}  
#lia-body .lia-content .lia-header-nav-component-widget.unread .lia-header-nav-component-unread-count { /* When there are notifications */
    border-color: #000 !important;
    background-color: #00C245 !important;
    color: #000 !important;
}
#lia-body .lia-content .lia-header-nav-component-widget.unread .lia-header-nav-component-unread-count:hover {
    border-color: #000 !important;
    background-color: #00FA59 !important;
    color: #000 !important;
} 
.child-thread.lia-link-disabled { /* Crumbs - Home - Welcome - actual topic at the end */
    color: #bbb !important;
}

/* Topic and Overview navigation at top */
.lia-paging-full-pages a:hover { /*Hovering over page selection numbers */
    background: #000 !important;
}
.lia-paging-full-pages .lia-link-disabled { /* Selected page & "..." */
    background: #000 !important;
    color: #eee !important;
}
.lia-paging-page-ellipsis .lia-link-disabled  { /* "..." at topic overview & in actual topic */
    background: /*[[customback]]*/ !important;
}       


    /* SEARCH BAR */

#lia-body .lia-content .lia-component-quilt-header .lia-component-common-widget-search-form input.search-input:focus { /* Shadow appears */
    transition: 0.7s !important;
    box-shadow: 0 0 2px #9ecaed !important;
}

.lia-quilt-column-search-bar { /* Less space above and below Search bar */
    padding: 0px 10px 0px 0px !important;
}
/* Search input field & Drop down field */
#lia-body .lia-content .lia-component-quilt-header .lia-component-common-widget-search-form input.search-input, select#searchGranularity.lia-search-form-granularity.search-granularity { 
    color: #ccc !important;
    background: /*[[custombox]]*/ !important;
}
.lia-quilt-column-search-bar {  /* Background of search bar */ 
    border: 0px !important;
}
    
/* Autocomplete Results */
.lia-autocomplete-header { /* Middle of popup*/
    background: /*[[customback]]*/ !important;
    color: #bfbfbf !important;
    border-bottom: 1px solid #575757 !important;
}
#lia-body .lia-content .lia-autocomplete-container { 
    background: /*[[customback]]*/ !important;
    border-radius: 0px 0px 3px 3px !important;
}
.lia-autocomplete-footer { /* Bottom of pop up */
    background: /*[[custombox]]*/ !important;
}
.lia-component-search-action-disable-auto-complete { /* "turn off suggestions" */
    color: #bbb !important;
}
.lia-component-search-action-disable-auto-complete:hover {
    color: #eee !important;
}
.lia-autocomplete-list-even-row { /* Even row background */
    background: /*[[custombox]]*/ !important;
    border-bottom: 1px solid #333 !important;
}
.lia-autocomplete-list-even-row:hover {
    background: #333 !important;
}
.lia-autocomplete-list-odd-row {
    background: /*[[customback]]*/ !important;
    border-bottom: 1px solid #333 !important;
}
.lia-autocomplete-list-odd-row:hover {
    background: #333 !important;

}
.lia-component-forums-auto-complete-message-list-item { /* Autocomplete topic links */
    color: #ccc !important;
}
.lia-component-forums-auto-complete-message-list-item:hover {
    color: #fff !important;
}
.lia-search-match-lithium { /* Letters/words that matched the search */
    background: none !important;
    color: #fff !important;
    font-weight: bold !important;
}


    /* SEARCH RESULTS */

#lia-body .lia-content .search-result-message-subject .lia-message-subject { /* Search results subject */
    font-size: 17px !important;
}
body#lia-body.SearchPage.lia-body div#tabgroup.lia-tabs-standard-wrapper { /* No outer border */
    border: 0px !important;
}
#lia-body .lia-content .lia-quilt-search-page .lia-tabs-standard-wrapper > .lia-quilt-row > .lia-quilt-column-right > .lia-quilt-column-alley { /* Right side a bit higher */
    margin-top: -13px !important;
    margin-bottom: -20px !important;
}
.spell-check-do-you-mean { /* Under search bar */
    padding-bottom: 7px !important;
    color: #bbb !important;
}
.spell-check-do-you-mean a {
    color: #fff !important;
}    
#searchautocompletetoggle_0 { /* Search bar doesn't cut with left elements */
    padding-left: 7px !important;
}    

/* Left sidebar*/
#lia-body .lia-content .lia-quilt-search-page .lia-component-search-widget-search-filter-container fieldset legend { /* Left side filter headlines bit more space */
    padding-bottom: 5px !important;
}
.lia-form-fieldset-wrapper { /* Outer background of left sorting menu */
    background: /*[[customback]]*/ !important;
}
.lia-component-forums-widget-community-node-tree-item { /* Sorting menu - by location - Forum tree */
    color: #ccc !important;
    background: /*[[customback]]*/ !important;
}
.lia-component-forums-widget-community-node-tree-item:hover{
    color: #fff !important;
}
#lia-body .lia-content .lia-quilt-search-page .lia-component-search-widget-search-filter-container label { /* All options left side */
    color: #ddd !important;
}

.lia-panel-feedback-inline-note { /* When a sorting option is applied - background of that bar  */
    background: /*[[customback]]*/ !important;
    color: #bbb !important;
}
.bread-crumb-filters-applied { /* "Discussions" when a sorting option is applied */
    color: #bbb !important;
}

.search-header-selected { /* Selected at drop down menu - top right */
    background-color: #0a0a0a !important;
    font-weight: bold !important;
    color: #fff !important;
    font-size: 1.15em !important;
}
.search-result-syndicate-rss a { /* "Subcribe to rss feed ..." top right */
    color: #ccc !important;
}
.search-result-syndicate-rss a:hover {
    color: #fff !important;
}
.search-result-syndicate-permalink a { /* Permalink */
    color: #ccc !important;
}
.search-result-syndicate-permalink a:hover {
    color: #fff !important;
}

/* Advanced Search Panel */
.advancedSearchPanel { /* Outer background of advanced search panel */
    background: /*[[customback]]*/ !important;
}
.lia-panel-heading-bar { /* Advanced search panel info message at top & Other headlines */
    background: /*[[customback]]*/ !important;
}

/* When search engine refuses to search for word e.g. "asdf" */
.spell-check-showing-result, .spell-check-search-instead {
    color: #D60B30 !important;
}
.spell-check-search-instead a {
    color: #ddd !important;
}
    
/* Direct Result lists */    
a.lia-message-read { /* Title of read topics */
    color: #ccc !important;
}
a.lia-message-read:hover {
    color: #eee !important;
}
.search-result-message-subject .lia-message-author a { /* After topic - author and forum section */
    color: #ccc !important;
}
.search-result-message-blurb-root { /* Under topic - short extract of post */
    color: #ccc !important;
}
.lia-js-search-result-count-link { /* "Show results in replies" */
    color: #aaa !important;
}
.lia-js-search-result-count-link:hover {
    color: #eee !important;
}
.lia-summary-view-statistic-replies { /* Replies */
    color: #999 !important;
}
.lia-summary-view-statistic-views { /* Views */
    color: #999 !important;
}
.blockUI.blockOverlay { /* When clicking between search pages */
    opacity: 0 !important;
}

/* Clicking on 'User' tab - top left */
.search-menu-bar { /* Result bar under search window for users - after clicking on User tab */
    background: /*[[custombox]]*/ !important;
    color: #ccc !important;
}
.user-search-result-container { /* Background */
    background: /*[[custombox]]*/ !important;
    border: 1px solid #333 !important;
    border-radius: 5px !important;
}
.lia-summary-view-statistics-wrapper, .lia-summary-view-statistics-wrapper a { /* Statistics */
    color: #ccc !important;
}
.lia-user-title { /* Real name in statistics */
    color: #ccc !important;
}
.user-post-count-link:hover { /* hover over "x posts" */
    color: #fff !important;
}
  
    
/* AREA INBETWEEN ELEMENTS FURTHER DOWN */

.lia-quilt-row-main { /* Background inbetween boxes - also under top border of a box */
    background: /*[[customback]]*/ !important;
}


    /* BUTTONS */    
    
/* All kind of buttons*/
#lia-body .lia-content a.lia-button, #lia-body .lia-content a.lia-button.lia-button-secondary, #lia-body .lia-content input.lia-button.lia-button-primary, #lia-body .lia-content input.lia-button.lia-button-secondary{ 
    background-image: linear-gradient(to bottom,#00FF5B 0,#196E32 100%) !important;
}
#lia-body .lia-content a.lia-button:hover, #lia-body .lia-content a.lia-button.lia-button-secondary:hover, #lia-body .lia-content input.lia-button.lia-button-primary:hover, #lia-body .lia-content input.lia-button.lia-button-secondary:hover {
    background-image: linear-gradient(to bottom,#3DF273 0,#289E4B 100%) !important;
}
 .lia-body .lia-content a.lia-button.lia-button-secondary { /* font of some buttons "Browse" in Profile - Badges */
    color: #fff !important;
}

/* Kudo Buttons normal Posts */
div.lia-quilt-column-alley .lia-quilt-column-alley-right .lia-link-navigation.kudos-link, div.lia-quilt-column-alley .lia-quilt-column-alley-right .lia-button-image-kudos-give a { /* Kudos button in posts - 0 kudos available */
    background-color: #04B342 !important;
}
#lia-body .lia-content .lia-component-kudos-widget-button-version-2 .lia-button-image-kudos-horizontal.lia-button-image-kudos-disabled .lia-button-image-kudos-give span:hover { /* Disabled kudos button doesn't highlight */
    background-color: #04B342 !important;
}
#lia-body .lia-content .lia-component-kudos-widget-button-version-2 .lia-button-image-kudos-horizontal.lia-button-image-kudos-has-kudos .lia-button-image-kudos-give span { /* Kudos button in posts - has my kudos */
    background: url("http://spotify.i.lithium.com/html/assets/btn_kudos_given_2.png"), linear-gradient(to bottom,#00FF5B 0,#196E32 100%) !important;
}
    
/* Kudo buttons Ideas Exchange */
#lia-body .lia-content .lia-message-idea .lia-component-kudos-widget-button-version-2 .lia-button-image-kudos-kudoed .lia-button-image-kudos-give { /* Kudoed button */
    background-image: url('http://spotify.i.lithium.com/html/assets/btn_kudos_given_2.png'), linear-gradient(to bottom,#00FF5B 0,#196E32 100%) !important;
}
#lia-body .lia-content .lia-message-idea .lia-component-kudos-widget-button-version-2 .lia-button-image-kudos-give .lia-link-navigation.kudos-link, #lia-body .lia-content .lia-message-idea .lia-button-image-kudos-give { /* Not yet kudoed*/
    background-image: url("http://spotify.i.lithium.com/html/assets/btn_kudos_give_2.png"), linear-gradient(to bottom,#00FF5B 0,#196E32 100%) !important;
}
#lia-body .lia-content .lia-message-idea .lia-component-kudos-widget-button-version-2 .lia-button-image-kudos-give .lia-link-navigation.kudos-link:hover, #lia-body .lia-content .lia-message-idea .lia-button-image-kudos-give:hover {
    background-image: url("http://spotify.i.lithium.com/html/assets/btn_kudos_give_2.png"), linear-gradient(to bottom,#3DF273 0,#289E4B 100%) !important;
}
#lia-body .lia-content .lia-message-idea .lia-component-kudos-widget-button-version-2 .lia-button-image-kudos-frozen .lia-button-image-kudos-give, #lia-body .lia-content .lia-component-kudos-widget-button-version-2 .lia-button-image-kudos-horizontal.lia-button-image-kudos-frozen .lia-button-image-kudos-give span { /* Frozen kudos button */
    background-image: url('https://spotify.i.lithium.com/html/assets/btn_kudos_frozen_2.png'), linear-gradient(to bottom,#C7EDA6 0,#C7EDA6 100%) !important;
}    


    /* HOME */

/* HOME - MIDDLE */
    
/* Announcement box */
#TopLinkGreenRoom.BoxAnnounce, #TopLinkMusicChat.BoxAnnounce {
    color: #ddd !important;
}
#lia-body .lia-component-common-widget-custom-content .lia-panel-content-wrapper {
    background-color: /*[[custombox]]*/ !important;
}

/* All kind of boxes' borders */
#lia-body .lia-content .lia-browser-category-list .BoardList, #lia-body .lia-component-forums-widget-recent-messages .lia-panel-content-wrapper, #lia-body .lia-component-forums-widget-unread-threads .lia-panel-content-wrapper, #lia-body .lia-component-forums-widget-unanswered-topics-taplet .lia-panel-content-wrapper, #lia-body .lia-quilt-unanswered-topics-page .MessageList.lia-component-forums-widget-message-list.lia-component-unanswered-list, #lia-body .lia-quilt-users-online-page DIV.UserList, div.MessageList.lia-component-forums-widget-message-list.lia-component-message-list, #lia-body .lia-component-tkb-widget-user-contributed-articles .lia-panel-content-wrapper, #lia-body .lia-component-tkb-widget-recent-tkb-articles-taplet .lia-panel-content-wrapper {
    border: 1px solid #555 !important;
}
#lia-body .lia-content table.lia-list-wide th , #lia-body .lia-content table.lia-list-wide td { /* First row bottom border */
    border-bottom: 1px solid #999 !important;
}
#lia-body .lia-content table.lia-list-wide td { /* First row top border */
    border-top: 0px solid #999 !important;
}
#lia-body .lia-content table.lia-list-slim td, #lia-body .lia-content table.lia-list-wide td, #lia-body .lia-content table.lia-list-statistics td, #lia-body .lia-content table.lia-list-statistics-small td { /* Line inside boxes */
    border-bottom: 1px solid #999 !important;
}
#lia-body .lia-content table.lia-list-wide .t-last td {
    border-bottom: 0px !important;
}

/* Polls when not hidden */
.CommunityPage .BoxAnnounce {
    background: /*[[custombox]]*/ !important;
    color: #ccc !important;
    border: 1px solid #555 !important;
}

#lia-body .lia-content table.lia-list-wide th { /* Bar over forum sections. e.g TITLE, etc */
    background-image: linear-gradient(to bottom, #191919 0, #000 100%) !important;
    font-weight: bold !important;
}    
#lia-body .lia-content table.lia-list-wide th, #lia-body .lia-content table.lia-list-wide th a { /* Sub-titles like "Title, "Posts"... */
    color: #bfbfbf !important;
}
.lia-list-row.lia-node-web-player {
    display: none !important;
}
.lia-message-item{ /* Latest topics and latest posts links */
    color: #ccc !important;
}
.lia-message-item:hover {
    color: #fff !important;
}
.lia-custom-event { /* Topic links under Recent Topics/Posts & Unread Discussions & Unanswered topics */
    color: #ddd !important;
}
.lia-custom-event:hover {
    color: #fff !important;
}
