css:
@-moz-document domain("www.dokuwiki.org") {
.dokuwiki textarea.edit {
  color:#000;
}

.dokuwiki a.wikilink1,
a:link, a:visited,
#dw__toc ul li a
{
  color:#000 !important;
}

#global__header a,
.dokuwiki div.breadcrumbs a,
.bchead, .bcsep,
.breadcrumbs div.trace,
.dokuwiki div.breadcrumbs,
#dokuwiki__header *,
.dokuwiki .docInfo,
#dokuwiki__footer div.license,
#dokuwiki__footer div.license a
{
  color:#eee !important;
  border:0 !important;
}

*,
div.page *
{
  font-size: 16px;
  color:#000 !important;
}

#dokuwiki__site,
body,
#dokuwiki__sitetools form.search input.edit
{
  background-color: #111 !important;
}

.dokuwiki div.page,
.dokuwiki .pageId span
{
  border: 1px solid #333 !important;
  box-shadow: 0 0 .9em #333 !important;
  border-radius: 5px !important;
}

.dokuwiki div.page{
border-top-right-radius: 0 !important;
  }

.dokuwiki .pageId span {
border-bottom-left-radius: 0 !important;
border-bottom-right-radius: 0 !important;
border-bottom:0 !important;
}

#dokuwiki__aside{
background-color:#fff;
border: 1px solid #333 !important;
box-shadow: 0 0 .9em #333 !important;
border-radius: 5px !important;
position:absolute !important;
width:16em !important;
margin-left:-17em !important;
}

#dokuwiki__aside:hover{
position:absolute !important;
margin-left:-6em !important;
padding-left: 1.5em !important;
}


.showSidebar #dokuwiki__content > .pad{
margin-left:0 !important;
}
}

@-moz-document url-prefix("http://pluginwiz.dokuwiki.org/") {
#container,
#dokuwiki__site,
body,
#dokuwiki__sitetools form.search input.edit
{
  background-color: #fff !important;
}
}
