/* Facebook Background Generator (Galaxy) - by pfeiffer stylez customizing */
/* http://userstyles.org/styles/89347/facebook-background-generator-galaxy */

@-moz-document regexp("http(s)?://.*\\.facebook\\.com/(?!plugins/|Fplugins|help|business|safety).*"){
#facebook body:not(.plugin):not(.transparent_widget):not([style*="#f2f2f2"]):not(.mceContentBody) {
background: #000 url(/*[[Background]]*/) center center no-repeat fixed !important; background-size: cover !important;
}
#facebook body:not(.UIPage_LoggedOut):not(.privacyPolicyPage) #globalContainer,
#facebook timelineLayoutLoggedOutUserProfile #globalContainer,
#facebook .timelineLayoutLoggedOutProfile #globalContainer {
background-color: transparent !important;
border: none !important;
}
}
