css:
@-moz-document domain("plus.google.com") {
/* Increase the width of the notifications box overlay */
#gbwc, #gbwc iframe, #gbd1 {width: 550px!important;}
.gb_gbsh #gbwc {right: -150px!important;}

/* Allow the contents of the notifications overlay to stretch within the container */
.fRPVBb, .eWXGXb, .nhreF, .My6zPe { width: 535px!important; }

/* Hide most of the 'Caught up on everything' box with the cute but space-wasting moodle */
.nTe87e {height: 4.5em;}

/* I like my notification box to pop out more; not a big fan of the low contrast grey background */
.aFtpL, .epxpje {background-color: #fcfcfc!important; }

/* Remove 'read more' */
.DIcL3e { max-height: inherit!important; overflow: inherit!important;}

/* Improve cards data in notifications overlay */
.YdLeIb, .IWn7pf, .CeWOs { width: 100%!important; white-space: inherit!important;}
.YdLeIb, .IWn7pf {display: inline!important; font-size: 0.9em;}
.IWn7pf {margin-left: 0.5em!important;}
.CeWOs { /*max-height: 30px;*/ text-align: justify!important; margin-top: 0.5em!important;}
.EDqEAf {width: 100%!important; max-width: inherit!important;}
.xAYdH {width: 100%!important; margin: 0.5em;position: absolute;}
.nhreF {margin: inherit!important; padding-bottom: 0.5em!important;padding-left: 0.3em!important;}
.My6zPe {margin: inherit!important; padding: inherit!important;}

/* If you want to keep the profile pictures: */
.ukA1gf {width: 80px;}
.EDqEAf { padding: 0 105px 0 2px!important; }

/* If you want to hide profile pictures in notifications overlay */
/*
.ukA1gf {display: none;}
.EDqEAf { padding: 0 30px 0 0px!important; }
*/
}
