css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cnbeta.com"), domain("www.cnbeta.com") {}

@-moz-document url-prefix("http://www.cnbeta.com/articles"), url-prefix("http://cnbeta.com/articles") {
.head, .comment, .feed, .global_navi .main_navi, .cb_prev, .cb_next, .cb_more, .dot_box li, .dot_box li.current{
background:none!important;
}
.global_navi .main_navi li{
background:none!important;
}
.global_navi .main_navi li a{
color:rgb(76,111,148)!important;
}
.global_navi .sub_navi,.nav_arrow, .nav_arrow_hover,.main_navi,.tiny_bar #sina_follow,#qzone_follow{
display:none!important;
}
.global_navi .sub_navi_bottom{
background:none!important;
}
.global_navi .sub_navi a{
color:rgb(76,111,148)!important;
}
.nav_arrow, .nav_arrow_hover{
background:rgb(76,111,148)!important;
}
.blue_bar, .blue_bar_v2, .white_bottom, .white_bottom_v2, .black_bar, .black_bottom, .yellow_bar, .yellow_bottom_v1, .yellow_bottom_v2, .read_more .more{
background:rgb(76,111,148)!important;
}
.blue_bar li a{
color:rgb(255,255,255)!important;
}
.blue_bar em{
color:rgb(205,205,205)!important;
}
.btn{
background:rgb(76,111,148)!important;
border-radius:0 0 0 0!important;
}
.white_bottom,.white_bottom_v2{
display:none!important;
}
.blue_bar ul li a.current{
color:rgb(76,111,148)!important;
}
.all_news_list .read_more .more{
border-radius:0 0 0 0!important;
color:rgb(255,255,255)!important;

}
.hotcomments header h4{
color:rgb(255,255,255)!important;
}
.re_mark em,#comment_num{
color:rgb(76,111,148)!important;
}
*{
font-family:Tahoma,Verdana,"宋体"!important;

}
.commt_list .comment_avatars{
background:url(data:image/jpeg;base64,)!important;
}
.post_commt textarea{
width:620px!important;
}
.article .navi{
display:none!important;
}

}
