@-moz-document domain("facebook.com") {
body, body.fbIndex
{
  background: url(http://i.imgur.com/HC8rFE6.jpg) !important;
  background-attachment: fixed !important;
  background-size:100% !important;
}

#mainContainer, #fbProfileCover, #content, #contentCol, #profileApprovalContentContainer, ._kj3, .fbTimelineLogColumn .pendingPostsHeader, .fbTimelineLogColumn .pendingProfileRequestsHeader
{
  background: rgba(255, 255, 255, 0.3);
}

#pagelet_navigation
.uiSideNav .item, .uiSideNav .subitem
{
  border-bottom:0px solid #fff
}

#u_1u_0, #pagelet_ego_pane_w, #pagelet_rhc_footer, .ego_column
{
